import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI, reviewAPI, enquiryAPI } from '../../services/api';
import './UserPanel.css';

const InstituteDetail = () => {
  const { id } = useParams();
  const { user, isAuthenticated } = useAuth();
  
  const [institute, setInstitute] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [reviewData, setReviewData] = useState({
    rating: 5,
    reviewText: ''
  });
  const [enquiryData, setEnquiryData] = useState({
    message: ''
  });
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    fetchInstituteDetail();
  }, [id]);

  const fetchInstituteDetail = async () => {
    try {
      // Mock data for now - replace with actual API call
      const mockInstitute = {
        _id: id,
        name: 'Delhi Public School',
        category: 'school',
        affiliation: 'CBSE/12345',
        address: {
          street: 'Sector 45',
          city: 'Delhi',
          state: 'Delhi',
          pincode: '110045'
        },
        contact: {
          phone: '011-12345678',
          email: 'info@dpsdelhi.com',
          website: 'https://dpsdelhi.com'
        },
        description: 'One of the top-ranked schools in Delhi with excellent academic record and infrastructure.',
        facilities: [
          { name: 'Library', description: 'Well-stocked library with 10,000+ books' },
          { name: 'Sports Complex', description: 'Basketball court, football field, swimming pool' },
          { name: 'Science Lab', description: 'Physics, Chemistry, Biology and Computer labs' },
          { name: 'Transport', description: 'School bus service available' }
        ],
        courses: [
          {
            title: 'Science Stream',
            description: 'PCB and PCM options available with advanced laboratory facilities',
            duration: '2 years',
            fees: 120000
          },
          {
            title: 'Commerce Stream',
            description: 'Business studies, accountancy, and economics with practical training',
            duration: '2 years',
            fees: 100000
          }
        ],
        rating: {
          average: 4.5,
          count: 8
        },
        images: {
          banner: null,
          logo: null,
          gallery: []
        }
      };

      const mockReviews = [
        {
          _id: '1',
          user: {
            name: 'Rahul Sharma',
            profileImage: null
          },
          rating: 5,
          reviewText: 'Excellent school with great facilities and dedicated teachers. My child has shown remarkable improvement.',
          createdAt: new Date('2024-01-15')
        },
        {
          _id: '2',
          user: {
            name: 'Priya Singh',
            profileImage: null
          },
          rating: 4,
          reviewText: 'Good infrastructure and curriculum. Sports facilities could be better.',
          createdAt: new Date('2024-01-10')
        }
      ];

      setInstitute(mockInstitute);
      setReviews(mockReviews);
    } catch (error) {
      console.error('Error fetching institute details:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) {
      alert('Please login to submit a review');
      return;
    }

    setSubmitting(true);
    try {
      // Mock API call - replace with actual
      console.log('Submitting review:', reviewData);
      alert('Review submitted successfully!');
      
      setReviewData({ rating: 5, reviewText: '' });
      setShowReviewForm(false);
      fetchInstituteDetail(); // Refresh reviews
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('Error submitting review. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) {
      alert('Please login to send an enquiry');
      return;
    }

    setSubmitting(true);
    try {
      // Mock API call - replace with actual
      console.log('Submitting enquiry:', enquiryData);
      alert('Enquiry sent successfully!');
      
      setEnquiryData({ message: '' });
      setShowEnquiryForm(false);
    } catch (error) {
      console.error('Error sending enquiry:', error);
      alert('Error sending enquiry. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  // Safe facility icon getter
  const getFacilityIcon = (facilityName) => {
    if (!facilityName) return 'âš¡';
    
    const name = facilityName.toLowerCase();
    if (name.includes('library')) return 'ðŸ“š';
    if (name.includes('sports')) return 'âš½';
    if (name.includes('lab')) return 'ðŸ”¬';
    if (name.includes('hostel')) return 'ðŸ ';
    if (name.includes('transport')) return 'ðŸšŒ';
    if (name.includes('cafeteria') || name.includes('food')) return 'ðŸ½ï¸';
    if (name.includes('medical')) return 'ðŸ¥';
    if (name.includes('playground')) return 'ðŸŒ³';
    if (name.includes('swimming')) return 'ðŸŠ';
    return 'âš¡';
  };

  const renderStars = (rating) => {
    return 'â­'.repeat(rating) + 'â˜†'.repeat(5 - rating);
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading institute details...</p>
        </div>
      </div>
    );
  }

  if (!institute) {
    return (
      <div className="error-state">
        <div className="error-icon">âŒ</div>
        <h2>Institute not found</h2>
        <p>The institute you're looking for doesn't exist or has been removed.</p>
        <Link to="/institutes" className="btn btn-primary">
          Browse Institutes
        </Link>
      </div>
    );
  }

  return (
    <div className="institute-detail">
      <div className="container">
        {/* Institute Header */}
        <div className="institute-header">
          <div className="institute-hero">
            {institute.images?.banner ? (
              <img 
                src={`http://localhost:5000/uploads/${institute.images.banner}`} 
                alt={institute.name}
                className="institute-banner"
              />
            ) : (
              <div className="banner-placeholder">
                <span>ðŸ«</span>
              </div>
            )}
            
            <div className="institute-overview">
              <div className="institute-logo">
                {institute.images?.logo ? (
                  <img 
                    src={`http://localhost:5000/uploads/${institute.images.logo}`} 
                    alt={`${institute.name} logo`}
                  />
                ) : (
                  <div className="logo-placeholder">
                    {institute.name?.charAt(0) || 'I'}
                  </div>
                )}
              </div>
              
              <div className="institute-info">
                <h1>{institute.name || 'Institute Name'}</h1>
                <p className="institute-category">{institute.category || 'Educational Institute'}</p>
                <p className="institute-location">
                  ðŸ“ {institute.address?.street && `${institute.address.street}, `}
                  {institute.address?.city || 'City'}, {institute.address?.state || 'State'}
                </p>
                <div className="institute-rating">
                  <span className="rating-stars">
                    {renderStars(Math.round(institute.rating?.average || 0))}
                  </span>
                  <span className="rating-text">
                    {institute.rating?.average?.toFixed(1) || 'N/A'} 
                    ({institute.rating?.count || 0} reviews)
                  </span>
                </div>
              </div>
              
              <div className="action-buttons">
                <button 
                  className="btn btn-primary"
                  onClick={() => setShowEnquiryForm(true)}
                >
                  ðŸ“§ Send Enquiry
                </button>
                <button 
                  className="btn btn-outline"
                  onClick={() => setShowReviewForm(true)}
                >
                  â­ Write Review
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Navigation Tabs */}
        <div className="detail-tabs">
          <button 
            className={`tab ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </button>
          <button 
            className={`tab ${activeTab === 'courses' ? 'active' : ''}`}
            onClick={() => setActiveTab('courses')}
          >
            Courses ({institute.courses?.length || 0})
          </button>
          <button 
            className={`tab ${activeTab === 'facilities' ? 'active' : ''}`}
            onClick={() => setActiveTab('facilities')}
          >
            Facilities ({institute.facilities?.length || 0})
          </button>
          <button 
            className={`tab ${activeTab === 'reviews' ? 'active' : ''}`}
            onClick={() => setActiveTab('reviews')}
          >
            Reviews ({reviews.length})
          </button>
          <button 
            className={`tab ${activeTab === 'contact' ? 'active' : ''}`}
            onClick={() => setActiveTab('contact')}
          >
            Contact
          </button>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {/* Overview Tab */}
          {activeTab === 'overview' && (
            <div className="tab-panel">
              <div className="overview-grid">
                <div className="overview-main">
                  <h2>About {institute.name}</h2>
                  <p className="institute-description">
                    {institute.description || 'No description available.'}
                  </p>
                  
                  <div className="key-features">
                    <h3>Key Information</h3>
                    <div className="features-grid">
                      <div className="feature-item">
                        <span className="feature-icon">ðŸŽ“</span>
                        <div className="feature-info">
                          <strong>Category</strong>
                          <span>{institute.category || 'Not specified'}</span>
                        </div>
                      </div>
                      <div className="feature-item">
                        <span className="feature-icon">ðŸ“œ</span>
                        <div className="feature-info">
                          <strong>Affiliation</strong>
                          <span>{institute.affiliation || 'Not specified'}</span>
                        </div>
                      </div>
                      <div className="feature-item">
                        <span className="feature-icon">â­</span>
                        <div className="feature-info">
                          <strong>Rating</strong>
                          <span>{institute.rating?.average?.toFixed(1) || 'N/A'}/5</span>
                        </div>
                      </div>
                      <div className="feature-item">
                        <span className="feature-icon">ðŸ“š</span>
                        <div className="feature-info">
                          <strong>Courses</strong>
                          <span>{institute.courses?.length || 0} Available</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="overview-sidebar">
                  <div className="quick-info-card">
                    <h3>Quick Info</h3>
                    <div className="info-item">
                      <strong>ðŸ“ Location</strong>
                      <p>
                        {institute.address?.city || 'City'}, {institute.address?.state || 'State'}
                        {institute.address?.pincode && ` - ${institute.address.pincode}`}
                      </p>
                    </div>
                    <div className="info-item">
                      <strong>ðŸ“ž Phone</strong>
                      <p>{institute.contact?.phone || 'Not provided'}</p>
                    </div>
                    <div className="info-item">
                      <strong>ðŸ“§ Email</strong>
                      <p>{institute.contact?.email || 'Not provided'}</p>
                    </div>
                    <div className="info-item">
                      <strong>ðŸŒ Website</strong>
                      <p>
                        {institute.contact?.website ? (
                          <a href={institute.contact.website} target="_blank" rel="noopener noreferrer">
                            Visit Website
                          </a>
                        ) : 'Not provided'}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Courses Tab */}
          {activeTab === 'courses' && (
            <div className="tab-panel">
              <h2>Available Courses</h2>
              {institute.courses && institute.courses.length > 0 ? (
                <div className="courses-grid">
                  {institute.courses.map((course, index) => (
                    <div key={index} className="course-card">
                      <div className="course-image">
                        {course.images && course.images.length > 0 ? (
                          <img 
                            src={`http://localhost:5000/uploads/${course.images[0]}`} 
                            alt={course.title}
                          />
                        ) : (
                          <div className="image-placeholder">ðŸ“š</div>
                        )}
                      </div>
                      <div className="course-content">
                        <h3>{course.title || 'Untitled Course'}</h3>
                        <p className="course-description">
                          {course.description || 'No description available.'}
                        </p>
                        <div className="course-details">
                          <span className="duration">â±ï¸ {course.duration || 'Duration not specified'}</span>
                          <span className="fees">ðŸ’° â‚¹{(course.fees || 0).toLocaleString()}</span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <div className="empty-icon">ðŸ“š</div>
                  <h3>No Courses Available</h3>
                  <p>This institute hasn't added any courses yet.</p>
                </div>
              )}
            </div>
          )}

          {/* Facilities Tab */}
          {activeTab === 'facilities' && (
            <div className="tab-panel">
              <h2>Campus Facilities</h2>
              {institute.facilities && institute.facilities.length > 0 ? (
                <div className="facilities-grid">
                  {institute.facilities.map((facility, index) => (
                    <div key={index} className="facility-card">
                      <div className="facility-icon">
                        {getFacilityIcon(facility?.name)}
                      </div>
                      <div className="facility-content">
                        <h3>{facility?.name || 'Facility'}</h3>
                        <p>{facility?.description || 'No description available.'}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <div className="empty-icon">âš¡</div>
                  <h3>No Facilities Listed</h3>
                  <p>This institute hasn't added any facility information yet.</p>
                </div>
              )}
            </div>
          )}

          {/* Reviews Tab */}
          {activeTab === 'reviews' && (
            <div className="tab-panel">
              <div className="reviews-header">
                <h2>Student Reviews</h2>
                <button 
                  className="btn btn-primary"
                  onClick={() => setShowReviewForm(true)}
                >
                  Write Review
                </button>
              </div>

              {reviews.length > 0 ? (
                <div className="reviews-list">
                  {reviews.map(review => (
                    <div key={review._id} className="review-item">
                      <div className="reviewer-info">
                        <div className="reviewer-avatar">
                          {review.user?.profileImage ? (
                            <img 
                              src={`http://localhost:5000/uploads/${review.user.profileImage}`} 
                              alt={review.user.name}
                            />
                          ) : (
                            <div className="avatar-placeholder">
                              {review.user?.name?.charAt(0)?.toUpperCase() || 'U'}
                            </div>
                          )}
                        </div>
                        <div className="reviewer-details">
                          <h4>{review.user?.name || 'Anonymous User'}</h4>
                          <div className="review-rating">
                            {renderStars(review.rating || 0)}
                          </div>
                          <span className="review-date">
                            {review.createdAt ? new Date(review.createdAt).toLocaleDateString() : 'Unknown date'}
                          </span>
                        </div>
                      </div>
                      <div className="review-content">
                        <p>{review.reviewText || 'No review text.'}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <div className="empty-icon">â­</div>
                  <h3>No Reviews Yet</h3>
                  <p>Be the first to review this institute!</p>
                  <button 
                    className="btn btn-primary"
                    onClick={() => setShowReviewForm(true)}
                  >
                    Write First Review
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Contact Tab */}
          {activeTab === 'contact' && (
            <div className="tab-panel">
              <h2>Contact Information</h2>
              <div className="contact-grid">
                <div className="contact-info">
                  <div className="contact-item">
                    <strong>ðŸ“ž Phone</strong>
                    <p>{institute.contact?.phone || 'Not provided'}</p>
                  </div>
                  <div className="contact-item">
                    <strong>ðŸ“§ Email</strong>
                    <p>{institute.contact?.email || 'Not provided'}</p>
                  </div>
                  <div className="contact-item">
                    <strong>ðŸŒ Website</strong>
                    <p>
                      {institute.contact?.website ? (
                        <a href={institute.contact.website} target="_blank" rel="noopener noreferrer">
                          {institute.contact.website}
                        </a>
                      ) : 'Not provided'}
                    </p>
                  </div>
                  <div className="contact-item">
                    <strong>ðŸ“ Address</strong>
                    <p>
                      {institute.address?.street && <>{institute.address.street},<br/></>}
                      {institute.address?.city || 'City'}, {institute.address?.state || 'State'}<br/>
                      {institute.address?.pincode && institute.address.pincode}
                    </p>
                  </div>
                </div>
                
                <div className="enquiry-section">
                  <h3>Send Enquiry</h3>
                  <p>Have questions? Send a message to the institute.</p>
                  <button 
                    className="btn btn-primary"
                    onClick={() => setShowEnquiryForm(true)}
                  >
                    Send Message
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Review Form Modal */}
      {showReviewForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Write a Review</h3>
              <button 
                className="close-btn"
                onClick={() => setShowReviewForm(false)}
              >
                Ã—
              </button>
            </div>
            <form onSubmit={handleReviewSubmit} className="modal-form">
              <div className="form-group">
                <label>Rating</label>
                <div className="rating-input">
                  {[1, 2, 3, 4, 5].map(star => (
                    <button
                      key={star}
                      type="button"
                      className={`star-btn ${star <= reviewData.rating ? 'active' : ''}`}
                      onClick={() => setReviewData(prev => ({ ...prev, rating: star }))}
                    >
                      â­
                    </button>
                  ))}
                </div>
              </div>
              <div className="form-group">
                <label>Your Review</label>
                <textarea
                  value={reviewData.reviewText}
                  onChange={(e) => setReviewData(prev => ({ ...prev, reviewText: e.target.value }))}
                  rows="5"
                  placeholder="Share your experience with this institute..."
                  required
                />
              </div>
              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowReviewForm(false)}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={submitting}
                >
                  {submitting ? 'Submitting...' : 'Submit Review'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Enquiry Form Modal */}
      {showEnquiryForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Send Enquiry</h3>
              <button 
                className="close-btn"
                onClick={() => setShowEnquiryForm(false)}
              >
                Ã—
              </button>
            </div>
            <form onSubmit={handleEnquirySubmit} className="modal-form">
              <div className="form-group">
                <label>Your Message</label>
                <textarea
                  value={enquiryData.message}
                  onChange={(e) => setEnquiryData({ message: e.target.value })}
                  rows="5"
                  placeholder="Enter your enquiry message..."
                  required
                />
              </div>
              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowEnquiryForm(false)}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={submitting}
                >
                  {submitting ? 'Sending...' : 'Send Enquiry'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default InstituteDetail;
