import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';

const EnquiryForm = ({ institute, onSubmit, onClose }) => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    message: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.message.trim()) {
      alert('Please enter your enquiry message');
      return;
    }
    onSubmit(formData);
  };

  if (!user) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <div className="modal-header">
            <h3>Login Required</h3>
            <button onClick={onClose} className="close-btn">Ã—</button>
          </div>
          <div className="modal-body">
            <p>Please login to send an enquiry.</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Send Enquiry to {institute.name}</h3>
          <button onClick={onClose} className="close-btn">Ã—</button>
        </div>
        
        <form onSubmit={handleSubmit} className="enquiry-form">
          <div className="form-group">
            <label>Your Message *</label>
            <textarea
              value={formData.message}
              onChange={(e) => setFormData({ message: e.target.value })}
              placeholder="Please describe your enquiry in detail..."
              rows="6"
              required
            />
          </div>

          <div className="form-info">
            <p><strong>Your contact information will be shared with the institute:</strong></p>
            <p>ðŸ“§ {user.email}</p>
            <p>ðŸ“ž {user.phone || 'Not provided'}</p>
          </div>

          <div className="form-actions">
            <button type="button" onClick={onClose} className="btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn-primary">
              Send Enquiry
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EnquiryForm;
