import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { instituteAPI } from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import './InstituteDashboard.css';

const ProfileManagement = () => {
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [profile, setProfile] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    website: '',
    affiliation: '',
    address: '',
    city: '',
    state: '',
    contactNumber: '',
    description: '',
    establishedYear: '',
    totalStudents: '',
    facultyCount: ''
  });
  const [images, setImages] = useState({
    logo: null,
    banner: null,
    gallery: []
  });

  useEffect(() => {
    fetchProfile();
  }, [user]);

  const fetchProfile = async () => {
    try {
      const response = await instituteAPI.getById(user.instituteId);
      const institute = response.data;
      setProfile(institute);
      setFormData({
        name: institute.name || '',
        email: institute.email || '',
        website: institute.website || '',
        affiliation: institute.affiliation || '',
        address: institute.address || '',
        city: institute.city || '',
        state: institute.state || '',
        contactNumber: institute.contactNumber || '',
        description: institute.description || '',
        establishedYear: institute.establishedYear || '',
        totalStudents: institute.totalStudents || '',
        facultyCount: institute.facultyCount || ''
      });
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load profile'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleImageUpload = (e, type) => {
    const file = e.target.files[0];
    if (file) {
      if (type === 'logo' || type === 'banner') {
        setImages(prev => ({
          ...prev,
          [type]: file
        }));
      } else if (type === 'gallery') {
        setImages(prev => ({
          ...prev,
          gallery: [...prev.gallery, file]
        }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);

    try {
      await instituteAPI.update(user.instituteId, formData);
      
      // Upload images if any
      if (images.logo) {
        const logoFormData = new FormData();
        logoFormData.append('image', images.logo);
        logoFormData.append('type', 'logo');
        await instituteAPI.uploadImage(user.instituteId, logoFormData);
      }

      if (images.banner) {
        const bannerFormData = new FormData();
        bannerFormData.append('image', images.banner);
        bannerFormData.append('type', 'banner');
        await instituteAPI.uploadImage(user.instituteId, bannerFormData);
      }

      addAlert({
        type: 'success',
        message: 'Profile updated successfully'
      });
      setEditMode(false);
      fetchProfile(); // Refresh data
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to update profile'
      });
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <InstituteSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading profile...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <InstituteSidebar />
      <div className="dashboard-content">
        <div className="profile-header">
          <h1>Institute Profile</h1>
          <div className="header-actions">
            {!editMode ? (
              <button 
                onClick={() => setEditMode(true)} 
                className="btn btn-primary"
              >
                Edit Profile
              </button>
            ) : (
              <div className="action-buttons">
                <button 
                  onClick={() => setEditMode(false)} 
                  className="btn btn-outline"
                >
                  Cancel
                </button>
                <button 
                  onClick={handleSubmit}
                  disabled={saving}
                  className="btn btn-primary"
                >
                  {saving ? 'Saving...' : 'Save Changes'}
                </button>
              </div>
            )}
          </div>
        </div>

        <div className="profile-card">
          {editMode ? (
            <form onSubmit={handleSubmit} className="profile-form">
              <div className="form-section">
                <h3>Basic Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Institute Name</label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Email</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Website</label>
                    <input
                      type="url"
                      name="website"
                      value={formData.website}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Affiliation</label>
                    <input
                      type="text"
                      name="affiliation"
                      value={formData.affiliation}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                </div>
              </div>

              <div className="form-section">
                <h3>Contact Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Address</label>
                    <input
                      type="text"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">City</label>
                    <input
                      type="text"
                      name="city"
                      value={formData.city}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">State</label>
                    <input
                      type="text"
                      name="state"
                      value={formData.state}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Contact Number</label>
                    <input
                      type="tel"
                      name="contactNumber"
                      value={formData.contactNumber}
                      onChange={handleInputChange}
                      className="form-control"
                      required
                    />
                  </div>
                </div>
              </div>

              <div className="form-section">
                <h3>Additional Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Established Year</label>
                    <input
                      type="number"
                      name="establishedYear"
                      value={formData.establishedYear}
                      onChange={handleInputChange}
                      className="form-control"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Total Students</label>
                    <input
                      type="number"
                      name="totalStudents"
                      value={formData.totalStudents}
                      onChange={handleInputChange}
                      className="form-control"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Faculty Count</label>
                    <input
                      type="number"
                      name="facultyCount"
                      value={formData.facultyCount}
                      onChange={handleInputChange}
                      className="form-control"
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label className="form-label">Description</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    className="form-control"
                    rows="4"
                    placeholder="Describe your institute..."
                  />
                </div>
              </div>

              <div className="form-section">
                <h3>Images</h3>
                <div className="image-upload-grid">
                  <div className="image-upload-item">
                    <label className="form-label">Logo</label>
                    <div className="image-upload-box">
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => handleImageUpload(e, 'logo')}
                        className="image-input"
                      />
                      <div className="upload-placeholder">
                        <i className="fas fa-upload"></i>
                        <span>Upload Logo</span>
                      </div>
                    </div>
                  </div>

                  <div className="image-upload-item">
                    <label className="form-label">Banner Image</label>
                    <div className="image-upload-box">
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => handleImageUpload(e, 'banner')}
                        className="image-input"
                      />
                      <div className="upload-placeholder">
                        <i className="fas fa-upload"></i>
                        <span>Upload Banner</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </form>
          ) : (
            <div className="profile-view">
              <div className="profile-banner">
                {profile.bannerImage ? (
                  <img src={profile.bannerImage} alt="Banner" />
                ) : (
                  <div className="banner-placeholder">
                    <i className="fas fa-school"></i>
                    <span>No banner image</span>
                  </div>
                )}
              </div>

              <div className="profile-header-info">
                <div className="profile-logo">
                  {profile.logo ? (
                    <img src={profile.logo} alt="Logo" />
                  ) : (
                    <div className="logo-placeholder">
                      {profile.name?.charAt(0)}
                    </div>
                  )}
                </div>
                <div className="profile-basic-info">
                  <h2>{profile.name}</h2>
                  <p className="affiliation">{profile.affiliation}</p>
                  <div className="profile-stats">
                    <span>Est. {profile.establishedYear}</span>
                    <span>{profile.totalStudents} Students</span>
                    <span>{profile.facultyCount} Faculty</span>
                  </div>
                </div>
              </div>

              <div className="profile-details">
                <div className="detail-section">
                  <h3>Contact Information</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <strong>Email:</strong>
                      <span>{profile.email}</span>
                    </div>
                    <div className="detail-item">
                      <strong>Website:</strong>
                      <a href={profile.website} target="_blank" rel="noopener noreferrer">
                        {profile.website}
                      </a>
                    </div>
                    <div className="detail-item">
                      <strong>Phone:</strong>
                      <span>{profile.contactNumber}</span>
                    </div>
                    <div className="detail-item">
                      <strong>Address:</strong>
                      <span>{profile.address}, {profile.city}, {profile.state}</span>
                    </div>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>About</h3>
                  <p>{profile.description || 'No description provided.'}</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfileManagement;
