import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import toast from 'react-hot-toast';

const CourseManagement = ({ institute }) => {
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    fees: '',
    category: '',
    eligibility: '',
    syllabus: ''
  });

  useEffect(() => {
    fetchCourses();
  }, [institute._id]);

  const fetchCourses = async () => {
    try {
      const response = await api.get(`/courses/institute/${institute._id}`);
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (editingCourse) {
        await api.put(`/courses/${editingCourse._id}`, formData);
        toast.success('Course updated successfully!');
      } else {
        await api.post('/courses', formData);
        toast.success('Course added successfully!');
      }
      
      resetForm();
      fetchCourses();
    } catch (error) {
      toast.error('Failed to save course');
      console.error('Error saving course:', error);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      duration: '',
      fees: '',
      category: '',
      eligibility: '',
      syllabus: ''
    });
    setEditingCourse(null);
    setShowForm(false);
  };

  const handleEdit = (course) => {
    setFormData({
      title: course.title,
      description: course.description,
      duration: course.duration,
      fees: course.fees,
      category: course.category,
      eligibility: course.eligibility || '',
      syllabus: course.syllabus || ''
    });
    setEditingCourse(course);
    setShowForm(true);
  };

  const handleDelete = async (courseId) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;

    try {
      await api.delete(`/courses/${courseId}`);
      toast.success('Course deleted successfully!');
      fetchCourses();
    } catch (error) {
      toast.error('Failed to delete course');
      console.error('Error deleting course:', error);
    }
  };

  return (
    <div className="course-management">
      <div className="section-header">
        <div className="header-content">
          <div>
            <h2>Course Management</h2>
            <p>Manage your institute's courses and programs</p>
          </div>
          <button 
            onClick={() => setShowForm(true)}
            className="btn btn-primary"
          >
            + Add New Course
          </button>
        </div>
      </div>

      {showForm && (
        <div className="form-modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingCourse ? 'Edit Course' : 'Add New Course'}</h3>
              <button onClick={resetForm} className="close-btn">√ó</button>
            </div>
            
            <form onSubmit={handleSubmit}>
              <div className="form-grid">
                <div className="form-group">
                  <label className="form-label">Course Title *</label>
                  <input
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Duration *</label>
                  <input
                    type="text"
                    name="duration"
                    value={formData.duration}
                    onChange={handleChange}
                    className="form-control"
                    placeholder="e.g., 2 years, 6 months"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Fees (‚Çπ) *</label>
                  <input
                    type="number"
                    name="fees"
                    value={formData.fees}
                    onChange={handleChange}
                    className="form-control"
                    min="0"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Category *</label>
                  <select
                    name="category"
                    value={formData.category}
                    onChange={handleChange}
                    className="form-control"
                    required
                  >
                    <option value="">Select Category</option>
                    <option value="primary">Primary Education</option>
                    <option value="secondary">Secondary Education</option>
                    <option value="senior-secondary">Senior Secondary</option>
                    <option value="undergraduate">Undergraduate</option>
                    <option value="postgraduate">Postgraduate</option>
                    <option value="diploma">Diploma</option>
                    <option value="certificate">Certificate</option>
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Description *</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  className="form-control"
                  rows="3"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Eligibility Criteria</label>
                <input
                  type="text"
                  name="eligibility"
                  value={formData.eligibility}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="e.g., 10+2 with 50% marks"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Syllabus (comma separated)</label>
                <input
                  type="text"
                  name="syllabus"
                  value={formData.syllabus}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="e.g., Mathematics, Physics, Chemistry"
                />
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  onClick={resetForm}
                  className="btn btn-outline"
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Saving...' : (editingCourse ? 'Update Course' : 'Add Course')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="courses-grid">
        {courses.length === 0 ? (
          <div className="empty-state">
            <h3>No courses added yet</h3>
            <p>Start by adding your first course to showcase your programs.</p>
          </div>
        ) : (
          courses.map((course) => (
            <div key={course._id} className="course-card">
              <div className="course-header">
                <h4 className="course-title">{course.title}</h4>
                <div className="course-actions">
                  <button 
                    onClick={() => handleEdit(course)}
                    className="btn btn-sm btn-outline"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleDelete(course._id)}
                    className="btn btn-sm btn-danger"
                  >
                    Delete
                  </button>
                </div>
              </div>
              
              <div className="course-details">
                <div className="course-meta">
                  <span className="course-duration">‚è±Ô∏è {course.duration}</span>
                  <span className="course-fees">üí∞ ‚Çπ{course.fees}</span>
                  <span className="course-category">üìÅ {course.category}</span>
                </div>
                
                <p className="course-description">{course.description}</p>
                
                {course.eligibility && (
                  <div className="course-eligibility">
                    <strong>Eligibility:</strong> {course.eligibility}
                  </div>
                )}
                
                {course.syllabus && course.syllabus.length > 0 && (
                  <div className="course-syllabus">
                    <strong>Syllabus:</strong> {Array.isArray(course.syllabus) ? course.syllabus.join(', ') : course.syllabus}
                  </div>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CourseManagement;
