import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import toast from 'react-hot-toast';
import './UserPanel.css';

const InstituteDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [institute, setInstitute] = useState(null);
  const [courses, setCourses] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [enquiryData, setEnquiryData] = useState({ message: '' });
  const [reviewData, setReviewData] = useState({ rating: 5, reviewText: '' });

  useEffect(() => {
    fetchInstituteDetail();
  }, [id]);

  const fetchInstituteDetail = async () => {
    try {
      const response = await api.get(`/institutes/${id}`);
      setInstitute(response.data.institute);
      setCourses(response.data.courses);
      setReviews(response.data.reviews);
    } catch (error) {
      console.error('Error fetching institute details:', error);
      toast.error('Failed to load institute details');
    } finally {
      setLoading(false);
    }
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    
    if (!user) {
      toast.error('Please login to send an enquiry');
      navigate('/login');
      return;
    }

    try {
      await api.post('/enquiries', {
        institute: id,
        message: enquiryData.message
      });
      
      toast.success('Enquiry sent successfully!');
      setEnquiryData({ message: '' });
      setShowEnquiryForm(false);
    } catch (error) {
      toast.error('Failed to send enquiry');
      console.error('Error sending enquiry:', error);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    
    if (!user) {
      toast.error('Please login to submit a review');
      navigate('/login');
      return;
    }

    try {
      await api.post('/reviews', {
        institute: id,
        rating: reviewData.rating,
        reviewText: reviewData.reviewText
      });
      
      toast.success('Review submitted successfully!');
      setReviewData({ rating: 5, reviewText: '' });
      setShowReviewForm(false);
      fetchInstituteDetail(); // Refresh reviews
    } catch (error) {
      toast.error('Failed to submit review');
      console.error('Error submitting review:', error);
    }
  };
  // In the InstituteDetail component, update the rating display:
const getRatingDisplay = (rating) => {
  if (!rating) return 'N/A';
  
  // If rating is an object, extract the average
  if (typeof rating === 'object' && rating !== null) {
    return rating.average || 'N/A';
  }
  
  // If rating is a number, use it directly
  return rating;
};

// Use it in the component:
<div className="stat">
  <div className="stat-value">{getRatingDisplay(institute.rating)}</div>
  <div className="stat-label">Rating</div>
</div>

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading institute details...</p>
        </div>
      </div>
    );
  }

  if (!institute) {
    return (
      <div className="container">
        <div className="error-state">
          <h2>Institute not found</h2>
          <p>The institute you're looking for doesn't exist.</p>
          <button onClick={() => navigate('/institutes')} className="btn btn-primary">
            Back to Institutes
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="institute-detail">
      {/* Hero Section */}
      <div className="institute-hero">
        <div className="container">
          <div className="institute-hero-content">
            <div className="institute-logo">
              {institute.logo ? (
                <img src={institute.logo} alt={institute.name} />
              ) : (
                <div className="logo-placeholder">ğŸ«</div>
              )}
            </div>
            
            <div className="institute-info">
              <h1>{institute.name}</h1>
              <div className="institute-meta">
                <span className="category">{institute.category}</span>
                <span className="affiliation">{institute.affiliation}</span>
                <span className="location">
                  ğŸ“ {institute.address.city}, {institute.address.state}
                </span>
              </div>
              
              <div className="institute-stats">
                <div className="stat">
                  <div className="stat-value">{institute.rating || 'N/A'}</div>
                  <div className="stat-label">Rating</div>
                </div>
                <div className="stat">
                  <div className="stat-value">{institute.reviewCount || 0}</div>
                  <div className="stat-label">Reviews</div>
                </div>
                <div className="stat">
                  <div className="stat-value">{courses.length}</div>
                  <div className="stat-label">Courses</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="institute-content-grid">
          {/* Main Content */}
          <div className="institute-main">
            {/* Description */}
            <section className="content-section">
              <h2 className="section-title">About Institute</h2>
              <p className="institute-description">{institute.description}</p>
            </section>

            {/* Courses */}
            <section className="content-section">
              <h2 className="section-title">Courses Offered</h2>
              {courses.length === 0 ? (
                <p className="no-data">No courses listed yet</p>
              ) : (
                <div className="courses-grid">
                  {courses.map((course) => (
                    <div key={course._id} className="course-card">
                      <div className="course-header">
                        <h4 className="course-title">{course.title}</h4>
                        <div className="course-fees">â‚¹{course.fees}</div>
                      </div>
                      <div className="course-duration">â±ï¸ {course.duration}</div>
                      <p className="course-description">{course.description}</p>
                      {course.eligibility && (
                        <div className="course-eligibility">
                          <strong>Eligibility:</strong> {course.eligibility}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </section>

            {/* Reviews */}
            <section className="content-section">
              <div className="section-header">
                <h2 className="section-title">Reviews & Ratings</h2>
                <button 
                  onClick={() => setShowReviewForm(true)}
                  className="btn btn-primary"
                  disabled={!user}
                >
                  Write a Review
                </button>
              </div>

              {reviews.length === 0 ? (
                <p className="no-data">No reviews yet. Be the first to review!</p>
              ) : (
                <div className="reviews-list">
                  {reviews.map((review) => (
                    <div key={review._id} className="review-card">
                      <div className="review-header">
                        <div className="review-user">{review.user.name}</div>
                        <div className="review-rating">
                          {'â­'.repeat(review.rating)}
                        </div>
                      </div>
                      <p className="review-text">{review.reviewText}</p>
                      <div className="review-date">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </section>
          </div>

          {/* Sidebar */}
          <div className="institute-sidebar">
            {/* Contact Info */}
            <div className="sidebar-card">
              <h3>Contact Information</h3>
              {institute.contact?.phone && (
                <div className="contact-item">
                  <strong>Phone:</strong> {institute.contact.phone}
                </div>
              )}
              {institute.contact?.email && (
                <div className="contact-item">
                  <strong>Email:</strong> {institute.contact.email}
                </div>
              )}
              {institute.contact?.website && (
                <div className="contact-item">
                  <strong>Website:</strong>{' '}
                  <a href={institute.contact.website} target="_blank" rel="noopener noreferrer">
                    Visit Website
                  </a>
                </div>
              )}
              <div className="contact-item">
                <strong>Address:</strong> {institute.address.street && `${institute.address.street}, `}
                {institute.address.city}, {institute.address.state}
                {institute.address.pincode && ` - ${institute.address.pincode}`}
              </div>
            </div>

            {/* Facilities */}
            {institute.facilities && institute.facilities.length > 0 && (
              <div className="sidebar-card">
                <h3>Facilities</h3>
                <div className="facilities-grid">
                  {institute.facilities.map((facility, index) => (
                    <div key={index} className="facility-item">
                      âœ… {facility}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Fee Range */}
            {institute.feeRange && (
              <div className="sidebar-card">
                <h3>Fee Range</h3>
                <div className="fee-range">
                  â‚¹{institute.feeRange.min} - â‚¹{institute.feeRange.max}
                </div>
                <small>Annual fees range</small>
              </div>
            )}

            {/* Action Buttons */}
            <div className="sidebar-card">
              <button 
                onClick={() => setShowEnquiryForm(true)}
                className="btn btn-primary btn-block"
                disabled={!user}
              >
                ğŸ“§ Send Enquiry
              </button>
              {!user && (
                <p className="login-prompt">
                  <a href="/login">Login</a> to send enquiries or write reviews
                </p>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Enquiry Form Modal */}
      {showEnquiryForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Send Enquiry to {institute.name}</h3>
              <button onClick={() => setShowEnquiryForm(false)} className="close-btn">Ã—</button>
            </div>
            <form onSubmit={handleEnquirySubmit}>
              <div className="form-group">
                <label className="form-label">Your Message *</label>
                <textarea
                  value={enquiryData.message}
                  onChange={(e) => setEnquiryData({ message: e.target.value })}
                  className="form-control"
                  rows="5"
                  placeholder="Enter your enquiry message..."
                  required
                />
              </div>
              <div className="form-actions">
                <button 
                  type="button" 
                  onClick={() => setShowEnquiryForm(false)}
                  className="btn btn-outline"
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Send Enquiry
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Review Form Modal */}
      {showReviewForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Write a Review for {institute.name}</h3>
              <button onClick={() => setShowReviewForm(false)} className="close-btn">Ã—</button>
            </div>
            <form onSubmit={handleReviewSubmit}>
              <div className="form-group">
                <label className="form-label">Rating *</label>
                <div className="rating-selector">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <button
                      key={star}
                      type="button"
                      onClick={() => setReviewData({ ...reviewData, rating: star })}
                      className={`star-btn ${star <= reviewData.rating ? 'active' : ''}`}
                    >
                      â­
                    </button>
                  ))}
                </div>
              </div>
              <div className="form-group">
                <label className="form-label">Your Review *</label>
                <textarea
                  value={reviewData.reviewText}
                  onChange={(e) => setReviewData({ ...reviewData, reviewText: e.target.value })}
                  className="form-control"
                  rows="5"
                  placeholder="Share your experience with this institute..."
                  required
                />
              </div>
              <div className="form-actions">
                <button 
                  type="button" 
                  onClick={() => setShowReviewForm(false)}
                  className="btn btn-outline"
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Submit Review
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default InstituteDetail;
