import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import toast from 'react-hot-toast';

const ProfileManagement = ({ institute, onUpdate }) => {
  const [formData, setFormData] = useState({
    name: institute.name || '',
    category: institute.category || '',
    affiliation: institute.affiliation || '',
    description: institute.description || '',
    address: {
      street: institute.address?.street || '',
      city: institute.address?.city || '',
      state: institute.address?.state || '',
      pincode: institute.address?.pincode || ''
    },
    contact: {
      phone: institute.contact?.phone || '',
      email: institute.contact?.email || '',
      website: institute.contact?.website || ''
    },
    feeRange: {
      min: institute.feeRange?.min || '',
      max: institute.feeRange?.max || ''
    }
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Clean the data
      const submitData = { ...formData };
      if (submitData.feeRange.min === '') submitData.feeRange.min = 0;
      if (submitData.feeRange.max === '') submitData.feeRange.max = 0;

      await api.put('/users/institute-profile', submitData);
      toast.success('Profile updated successfully!');
      onUpdate(); // Refresh data
    } catch (error) {
      toast.error('Failed to update profile');
      console.error('Error updating profile:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-management">
      <div className="section-header">
        <h2>Institute Profile Management</h2>
        <p>Update your institute information and contact details</p>
      </div>

      <form onSubmit={handleSubmit} className="form-container">
        <div className="form-section">
          <h3>Basic Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">Institute Name *</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Category *</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                className="form-control"
                required
              >
                <option value="">Select Category</option>
                <option value="school">School</option>
                <option value="college">College</option>
                <option value="coaching">Coaching Center</option>
                <option value="preschool">Preschool</option>
                <option value="university">University</option>
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Affiliation *</label>
              <input
                type="text"
                name="affiliation"
                value={formData.affiliation}
                onChange={handleChange}
                className="form-control"
                placeholder="e.g., CBSE, State Board, UGC"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="form-control"
              rows="4"
              placeholder="Describe your institute, facilities, achievements..."
              required
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Contact Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">Contact Phone</label>
              <input
                type="tel"
                name="contact.phone"
                value={formData.contact.phone}
                onChange={handleChange}
                className="form-control"
              />
            </div>

            <div className="form-group">
              <label className="form-label">Contact Email</label>
              <input
                type="email"
                name="contact.email"
                value={formData.contact.email}
                onChange={handleChange}
                className="form-control"
              />
            </div>

            <div className="form-group">
              <label className="form-label">Website</label>
              <input
                type="url"
                name="contact.website"
                value={formData.contact.website}
                onChange={handleChange}
                className="form-control"
                placeholder="https://example.com"
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Address Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">Street Address</label>
              <input
                type="text"
                name="address.street"
                value={formData.address.street}
                onChange={handleChange}
                className="form-control"
              />
            </div>

            <div className="form-group">
              <label className="form-label">City *</label>
              <input
                type="text"
                name="address.city"
                value={formData.address.city}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">State *</label>
              <input
                type="text"
                name="address.state"
                value={formData.address.state}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Pincode</label>
              <input
                type="text"
                name="address.pincode"
                value={formData.address.pincode}
                onChange={handleChange}
                className="form-control"
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Fee Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label className="form-label">Minimum Fee (₹)</label>
              <input
                type="number"
                name="feeRange.min"
                value={formData.feeRange.min}
                onChange={handleChange}
                className="form-control"
                min="0"
              />
            </div>

            <div className="form-group">
              <label className="form-label">Maximum Fee (₹)</label>
              <input
                type="number"
                name="feeRange.max"
                value={formData.feeRange.max}
                onChange={handleChange}
                className="form-control"
                min="0"
              />
            </div>
        </div>
        </div>

        <div className="form-actions">
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? 'Updating...' : 'Update Profile'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProfileManagement;
