import axios from 'axios';

// Use localhost:5000 for development - remove timeout for debugging
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Create axios instance with shorter timeout for development
const api = axios.create({
  baseURL: API_URL,
  timeout: 5000, // Reduced from 10000 to 5000ms
});

// Add request interceptor for debugging
api.interceptors.request.use(
  (config) => {
    console.log(`ðŸ”„ API Request: ${config.method?.toUpperCase()} ${config.url}`);
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    console.error('âŒ Request Error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for debugging
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… API Response: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('âŒ Response Error:', {
      url: error.config?.url,
      method: error.config?.method,
      status: error.response?.status,
      message: error.message,
      code: error.code
    });
    
    if (error.code === 'ECONNREFUSED') {
      console.error('ðŸ’¥ Backend server is not running!');
      console.error('ðŸ’¥ Please start the backend server on port 5000');
    }
    
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth services
export const authService = {
  login: (email, password) => {
    console.log(`ðŸ” Login attempt: ${email}`);
    return api.post('/auth/login', { email, password });
  },
  register: (userData) => api.post('/auth/register', userData),
  getCurrentUser: () => api.get('/auth/me'),
};

// Institute services
export const instituteService = {
  getAll: (params) => api.get('/institutes', { params }),
  getById: (id) => api.get(`/institutes/${id}`),
  getCurrentInstitute: () => api.get('/institutes/current'),
  updateInstitute: (id, data) => api.put(`/institutes/${id}`, data),
  uploadImage: (id, formData) => api.put(`/institutes/${id}`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  }),
  addCourse: (formData) => api.post('/institutes/courses', formData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  }),
  updateCourse: (courseId, formData) => api.put(`/institutes/courses/${courseId}`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  }),
  deleteCourse: (courseId) => api.delete(`/institutes/courses/${courseId}`),
};

// Review services
export const reviewService = {
  create: (data) => api.post('/reviews', data),
  getByInstitute: (instituteId) => {
    if (!instituteId) {
      return Promise.reject(new Error('Institute ID is required'));
    }
    return api.get(`/reviews/institute/${instituteId}`);
  },
  getByInstituteDashboard: (instituteId) => {
    if (!instituteId) {
      return Promise.reject(new Error('Institute ID is required'));
    }
    return api.get(`/reviews/institute/${instituteId}/dashboard`);
  },
  getByUser: () => api.get('/users/reviews'),
  update: (id, data) => api.put(`/reviews/${id}/approval`, data),
  delete: (id) => api.delete(`/reviews/${id}`),
};

// Enquiry services
export const enquiryService = {
  create: (data) => api.post('/enquiries', data),
  getByInstitute: (instituteId) => {
    if (!instituteId) {
      return Promise.reject(new Error('Institute ID is required'));
    }
    return api.get(`/enquiries/institute/${instituteId}`);
  },
  getByUser: () => api.get('/users/enquiries'),
  updateStatus: (id, data) => api.put(`/enquiries/${id}`, data),
};

// Admin services
export const adminService = {
  getInstitutes: () => api.get('/admin/institutes'),
  getUsers: () => api.get('/admin/users'),
  updateInstituteVerification: (id, verified) => 
    api.put(`/admin/institutes/${id}/verify`, { verified }),
  updateInstituteStatus: (id, isActive) => 
    api.put(`/admin/institutes/${id}/status`, { isActive }),
  updateUserStatus: (id, isActive) => 
    api.put(`/admin/users/${id}/status`, { isActive }),
  getAnalytics: () => api.get('/admin/analytics'),
};

export default api;
