import api from './api';

export const instituteService = {
  getAllInstitutes: async (filters = {}) => {
    try {
      const params = new URLSearchParams();
      Object.keys(filters).forEach(key => {
        if (filters[key]) {
          params.append(key, filters[key]);
        }
      });
      const response = await api.get(`/institutes/public?${params}`);
      return response.data;
    } catch (error) {
      console.error('Error fetching institutes:', error);
      // Return mock data for demo
      return [
        {
          _id: '1',
          name: 'Delhi Public School',
          category: 'school',
          affiliation: 'CBSE',
          address: {
            city: 'New Delhi',
            state: 'Delhi'
          },
          contact: {
            email: 'info@dpsdelhi.com',
            phone: '011-12345678',
            website: 'https://dpsdelhi.com'
          },
          description: 'One of the premier educational institutions in Delhi, known for academic excellence and holistic development.',
          facilities: [
            { name: 'Library', description: 'Well-stocked library' },
            { name: 'Sports Complex', description: 'Olympic-sized swimming pool' }
          ],
          reviews: [],
          status: 'approved'
        },
        {
          _id: '2',
          name: 'St. Xavier\'s College',
          category: 'college',
          affiliation: 'University of Delhi',
          address: {
            city: 'New Delhi',
            state: 'Delhi'
          },
          contact: {
            email: 'admissions@stxaviers.edu',
            phone: '011-27667745'
          },
          description: 'A premier liberal arts college offering undergraduate and postgraduate programs.',
          facilities: [
            { name: 'Auditorium', description: '1000-seat auditorium' },
            { name: 'Computer Labs', description: 'State-of-the-art labs' }
          ],
          reviews: [],
          status: 'approved'
        }
      ];
    }
  },

  getInstituteById: async (id) => {
    try {
      const response = await api.get(`/institutes/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error fetching institute:', error);
      return null;
    }
  },

  getInstituteProfile: async () => {
    try {
      const response = await api.get('/institutes/profile');
      return response.data;
    } catch (error) {
      console.error('Error fetching institute profile:', error);
      // Return mock data for demo
      return {
        name: 'Delhi Public School',
        category: 'school',
        affiliation: 'CBSE',
        address: {
          street: 'RK Puram',
          city: 'New Delhi',
          state: 'Delhi',
          pincode: '110022'
        },
        contact: {
          phone: '011-12345678',
          email: 'info@dpsdelhi.com',
          website: 'https://dpsdelhi.com'
        },
        description: 'One of the premier educational institutions in Delhi...',
        facilities: [
          { _id: '1', name: 'Library', description: 'Well-stocked library with 50,000+ books' },
          { _id: '2', name: 'Sports Complex', description: 'Olympic-sized swimming pool and indoor stadium' }
        ]
      };
    }
  },

  updateInstitute: async (data) => {
    const response = await api.put('/institutes/profile', data);
    return response.data;
  },

  addFacility: async (facility) => {
    const response = await api.post('/institutes/facilities', facility);
    return response.data;
  },

  removeFacility: async (facilityId) => {
    const response = await api.delete(`/institutes/facilities/${facilityId}`);
    return response.data;
  }
};
