import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation, Navigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import AdminSidebar from './AdminSidebar';
import ManageInstitutes from './ManageInstitutes';
import ManageUsers from './ManageUsers';
import AnalyticsDashboard from './AnalyticsDashboard';
import './AdminPanel.css';

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);

  useEffect(() => {
    // Redirect to analytics if at root dashboard path
    if (location.pathname === '/admin/dashboard' || location.pathname === '/admin/dashboard/') {
      navigate('/admin/dashboard/analytics');
    }
  }, [location, navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="admin-dashboard">
      <AdminSidebar 
        isOpen={sidebarOpen} 
        onClose={() => setSidebarOpen(false)}
        onLogout={handleLogout}
      />
      
      <div className="admin-main">
        <div className="admin-header">
          <button 
            className="menu-toggle"
            onClick={() => setSidebarOpen(true)}
          >
            â˜°
          </button>
          <h1>Admin Dashboard</h1>
          <div className="admin-info">
            <span>Welcome, {user?.name}</span>
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </div>
        </div>

        <div className="admin-content">
          <Routes>
            <Route path="analytics" element={<AnalyticsDashboard />} />
            <Route path="institutes" element={<ManageInstitutes />} />
            <Route path="users" element={<ManageUsers />} />
            <Route path="*" element={<Navigate to="analytics" replace />} />
          </Routes>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
