import React, { useState, useEffect } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import api from '../../services/api';
import AdminSidebar from './AdminSidebar';
import ManageInstitutes from './ManageInstitutes';
import ManageUsers from './ManageUsers';
import AnalyticsDashboard from './AnalyticsDashboard';
import './AdminPanel.css';

const AdminDashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await api.get('/admin/dashboard');
      setDashboardData(response.data);
    } catch (error) {
      console.error('Error fetching admin dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading admin dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <AdminSidebar />
      
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
          <p>Manage institutes, users, and platform analytics</p>
        </div>

        {location.pathname === '/admin' && dashboardData && (
          <AdminOverview data={dashboardData} />
        )}

        <Routes>
          <Route path="institutes" element={
            <ManageInstitutes />
          } />
          <Route path="users" element={
            <ManageUsers />
          } />
          <Route path="analytics" element={
            <AnalyticsDashboard />
          } />
        </Routes>
      </div>
    </div>
  );
};

const AdminOverview = ({ data }) => {
  const { stats, recentInstitutes, recentReviews } = data;

  return (
    <div className="dashboard-overview">
      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üè´</div>
          <div className="stat-info">
            <div className="stat-number">{stats.totalInstitutes}</div>
            <div className="stat-label">Total Institutes</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-info">
            <div className="stat-number">{stats.totalUsers}</div>
            <div className="stat-label">Total Users</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <div className="stat-number">{stats.totalReviews}</div>
            <div className="stat-label">Total Reviews</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üìß</div>
          <div className="stat-info">
            <div className="stat-number">{stats.totalEnquiries}</div>
            <div className="stat-label">Total Enquiries</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚è≥</div>
          <div className="stat-info">
            <div className="stat-number">{stats.pendingInstitutes}</div>
            <div className="stat-label">Pending Approvals</div>
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="dashboard-grid">
        <div className="recent-card">
          <h3>Pending Institute Approvals</h3>
          {recentInstitutes.filter(inst => !inst.isVerified).length === 0 ? (
            <p className="no-data">No pending approvals</p>
          ) : (
            <div className="activity-list">
              {recentInstitutes
                .filter(inst => !inst.isVerified)
                .slice(0, 5)
                .map((institute) => (
                <div key={institute._id} className="activity-item">
                  <div className="activity-content">
                    <strong>{institute.name}</strong>
                    <div className="activity-meta">
                      {institute.category} ‚Ä¢ {institute.address.city}
                    </div>
                    <div className="activity-time">
                      Applied on {new Date(institute.createdAt).toLocaleDateString()}
                    </div>
                  </div>
                  <div className="activity-status pending">
                    Pending
                  </div>
                </div>
              ))}
            </div>
          )}
          <button 
            onClick={() => window.location.href = '/admin/institutes'}
            className="btn btn-outline btn-sm"
          >
            Manage Institutes
          </button>
        </div>

        <div className="recent-card">
          <h3>Recent Reviews</h3>
          {recentReviews.length === 0 ? (
            <p className="no-data">No recent reviews</p>
          ) : (
            <div className="activity-list">
              {recentReviews.slice(0, 5).map((review) => (
                <div key={review._id} className="activity-item">
                  <div className="activity-content">
                    <strong>{review.user.name}</strong> reviewed{' '}
                    <strong>{review.institute.name}</strong>
                    <div className="activity-rating">
                      {'‚≠ê'.repeat(review.rating)}
                    </div>
                    <div className="activity-review">
                      {review.reviewText.length > 60 
                        ? `${review.reviewText.substring(0, 60)}...`
                        : review.reviewText
                      }
                    </div>
                    <div className="activity-time">
                      {new Date(review.createdAt).toLocaleDateString()}
                    </div>
                  </div>
                  <div className={`activity-status ${review.isApproved ? 'approved' : 'pending'}`}>
                    {review.isApproved ? 'Approved' : 'Pending'}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
