import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    role: 'user',
    instituteData: {
      category: '',
      affiliation: '',
      address: {
        street: '',
        city: '',
        state: '',
        pincode: ''
      },
      contact: {
        website: ''
      },
      description: ''
    },
    adminKey: ''
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.startsWith('institute.')) {
      const field = name.split('.')[1];
      const subfield = name.split('.')[2];
      
      setFormData(prev => ({
        ...prev,
        instituteData: {
          ...prev.instituteData,
          [field]: subfield ? {
            ...prev.instituteData[field],
            [subfield]: value
          } : value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone is required';
    } else if (!/^\d{10}$/.test(formData.phone)) {
      newErrors.phone = 'Phone must be 10 digits';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    if (formData.role === 'institute') {
      if (!formData.instituteData.category) {
        newErrors['institute.category'] = 'Category is required';
      }
      if (!formData.instituteData.affiliation) {
        newErrors['institute.affiliation'] = 'Affiliation is required';
      }
      if (!formData.instituteData.address.city) {
        newErrors['institute.address.city'] = 'City is required';
      }
      if (!formData.instituteData.description) {
        newErrors['institute.description'] = 'Description is required';
      }
    }

    if (formData.role === 'admin' && !formData.adminKey) {
      newErrors.adminKey = 'Admin key is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    
    try {
      const { confirmPassword, ...submitData } = formData;
      const result = await register(submitData);
      
      if (result.success) {
        navigate(formData.role === 'user' ? '/user' : `/${formData.role}`);
      } else {
        setErrors({ submit: result.message });
      }
    } catch (error) {
      setErrors({ submit: 'Registration failed. Please try again.' });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Create Account</h2>
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Full Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className={errors.name ? 'error' : ''}
            />
            {errors.name && <span className="error-text">{errors.name}</span>}
          </div>

          <div className="form-group">
            <label>Email *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? 'error' : ''}
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label>Phone *</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className={errors.phone ? 'error' : ''}
            />
            {errors.phone && <span className="error-text">{errors.phone}</span>}
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={errors.password ? 'error' : ''}
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>

          <div className="form-group">
            <label>Confirm Password *</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              className={errors.confirmPassword ? 'error' : ''}
            />
            {errors.confirmPassword && <span className="error-text">{errors.confirmPassword}</span>}
          </div>

          <div className="form-group">
            <label>Register As *</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
            >
              <option value="user">Student/Parent</option>
              <option value="institute">Institute</option>
              <option value="admin">Admin</option>
            </select>
          </div>

          {formData.role === 'institute' && (
            <div className="institute-fields">
              <div className="form-group">
                <label>Institute Category *</label>
                <select
                  name="institute.category"
                  value={formData.instituteData.category}
                  onChange={handleChange}
                  className={errors['institute.category'] ? 'error' : ''}
                >
                  <option value="">Select Category</option>
                  <option value="school">School</option>
                  <option value="college">College</option>
                  <option value="coaching">Coaching Center</option>
                  <option value="preschool">Preschool</option>
                  <option value="university">University</option>
                </select>
                {errors['institute.category'] && <span className="error-text">{errors['institute.category']}</span>}
              </div>

              <div className="form-group">
                <label>Affiliation Number *</label>
                <input
                  type="text"
                  name="institute.affiliation"
                  value={formData.instituteData.affiliation}
                  onChange={handleChange}
                  className={errors['institute.affiliation'] ? 'error' : ''}
                />
                {errors['institute.affiliation'] && <span className="error-text">{errors['institute.affiliation']}</span>}
              </div>

              <div className="form-group">
                <label>City *</label>
                <input
                  type="text"
                  name="institute.address.city"
                  value={formData.instituteData.address.city}
                  onChange={handleChange}
                  className={errors['institute.address.city'] ? 'error' : ''}
                />
                {errors['institute.address.city'] && <span className="error-text">{errors['institute.address.city']}</span>}
              </div>

              <div className="form-group">
                <label>Website</label>
                <input
                  type="url"
                  name="institute.contact.website"
                  value={formData.instituteData.contact.website}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="institute.description"
                  value={formData.instituteData.description}
                  onChange={handleChange}
                  rows="4"
                  className={errors['institute.description'] ? 'error' : ''}
                />
                {errors['institute.description'] && <span className="error-text">{errors['institute.description']}</span>}
              </div>
            </div>
          )}

          {formData.role === 'admin' && (
            <div className="form-group">
              <label>Admin Key *</label>
              <input
                type="password"
                name="adminKey"
                value={formData.adminKey}
                onChange={handleChange}
                className={errors.adminKey ? 'error' : ''}
              />
              {errors.adminKey && <span className="error-text">{errors.adminKey}</span>}
            </div>
          )}

          {errors.submit && <div className="error-message">{errors.submit}</div>}

          <button 
            type="submit" 
            className="auth-button"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <p className="auth-link">
          Already have an account? <Link to="/login">Sign In</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
