import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import AdminSidebar from './AdminSidebar';
import ManageInstitutes from './ManageInstitutes';
import ManageUsers from './ManageUsers';
import AnalyticsDashboard from './AnalyticsDashboard';
import { adminAPI } from '../../services/api';
import './AdminPanel.css';

const AdminDashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  
  const { logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Mock data for now - replace with actual API call
      const mockData = {
        stats: {
          totalUsers: 150,
          totalInstitutes: 25,
          pendingInstitutes: 3,
          totalReviews: 89,
          totalEnquiries: 45
        },
        recentActivities: {
          pendingInstitutes: [
            {
              _id: '1',
              name: 'New Tech Institute',
              user: { name: 'Tech Admin', email: 'tech@example.com' },
              createdAt: new Date()
            }
          ],
          recentReviews: [
            {
              _id: '1',
              user: { name: 'Student One' },
              institute: { name: 'Delhi Public School' },
              createdAt: new Date()
            }
          ]
        }
      };
      setDashboardData(mockData);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading admin dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <AdminSidebar onLogout={handleLogout} />
      
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Admin Dashboard</h1>
          <div className="welcome-message">
            System Overview & Analytics
          </div>
        </div>

        {location.pathname === '/admin' && dashboardData && (
          <div className="dashboard-overview">
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon">üë•</div>
                <div className="stat-info">
                  <h3>{dashboardData.stats.totalUsers}</h3>
                  <p>Total Users</p>
                </div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">üè´</div>
                <div className="stat-info">
                  <h3>{dashboardData.stats.totalInstitutes}</h3>
                  <p>Verified Institutes</p>
                </div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">‚è≥</div>
                <div className="stat-info">
                  <h3>{dashboardData.stats.pendingInstitutes}</h3>
                  <p>Pending Institutes</p>
                </div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">‚≠ê</div>
                <div className="stat-info">
                  <h3>{dashboardData.stats.totalReviews}</h3>
                  <p>Total Reviews</p>
                </div>
              </div>
            </div>

            <div className="recent-activities">
              <div className="activity-section">
                <h3>Pending Institute Approvals</h3>
                {dashboardData.recentActivities.pendingInstitutes.map(institute => (
                  <div key={institute._id} className="activity-item">
                    <div className="activity-info">
                      <strong>{institute.name}</strong>
                      <span>{institute.user.name}</span>
                    </div>
                    <span className="activity-time">
                      {new Date(institute.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                ))}
              </div>

              <div className="activity-section">
                <h3>Recent Reviews</h3>
                {dashboardData.recentActivities.recentReviews.map(review => (
                  <div key={review._id} className="activity-item">
                    <div className="activity-info">
                      <strong>{review.user.name}</strong>
                      <span>reviewed {review.institute.name}</span>
                    </div>
                    <span className="activity-time">
                      {new Date(review.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        <Routes>
          <Route path="institutes" element={<ManageInstitutes />} />
          <Route path="users" element={<ManageUsers />} />
          <Route path="analytics" element={<AnalyticsDashboard />} />
        </Routes>
      </div>
    </div>
  );
};

export default AdminDashboard;
