import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { instituteAPI, courseAPI, facilityAPI, reviewAPI } from '../../utils/api';

const InstituteDetail = () => {
  const { id } = useParams();
  const { user } = useContext(AuthContext);
  const [institute, setInstitute] = useState(null);
  const [courses, setCourses] = useState([]);
  const [facilities, setFacilities] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchInstituteData = async () => {
    try {
      setLoading(true);
      setError('');

      console.log('Fetching institute data for ID:', id);

      // Get institute basic info
      const instituteResponse = await instituteAPI.getById(id);
      if (instituteResponse.data.success) {
        setInstitute(instituteResponse.data.data);
      } else {
        throw new Error(instituteResponse.data.message || 'Failed to fetch institute');
      }

      // Get courses
      try {
        const coursesResponse = await instituteAPI.getCourses(id);
        if (coursesResponse.data.success) {
          setCourses(coursesResponse.data.data || []);
        }
      } catch (courseError) {
        console.log('Courses not available, using mock data');
        setCourses([
          { _id: '1', name: 'Computer Science', duration: '4 years', fees: '50000' },
          { _id: '2', name: 'Business Administration', duration: '3 years', fees: '40000' }
        ]);
      }

      // Get facilities
      try {
        const facilitiesResponse = await instituteAPI.getFacilities(id);
        if (facilitiesResponse.data.success) {
          setFacilities(facilitiesResponse.data.data || []);
        }
      } catch (facilityError) {
        console.log('Facilities not available, using mock data');
        setFacilities([
          { _id: '1', name: 'Library', description: 'Well-stocked library' },
          { _id: '2', name: 'Computer Lab', description: 'Modern computer lab' }
        ]);
      }

      // Get reviews
      try {
        const reviewsResponse = await instituteAPI.getReviews(id);
        if (reviewsResponse.data.success) {
          setReviews(reviewsResponse.data.data || []);
        }
      } catch (reviewError) {
        console.log('Reviews not available, using mock data');
        setReviews([
          { _id: '1', rating: 5, comment: 'Excellent institute', studentName: 'John Doe' },
          { _id: '2', rating: 4, comment: 'Good facilities', studentName: 'Jane Smith' }
        ]);
      }

    } catch (error) {
      console.error('Error fetching institute data:', error);
      setError(error.message || 'Failed to load institute details');
      
      // Fallback to mock data
      setInstitute({
        _id: id,
        name: 'Sample Institute',
        email: 'sample@institute.com',
        description: 'This is a sample institute description.',
        address: 'Sample Address',
        phone: '1234567890'
      });
      setCourses([
        { _id: '1', name: 'Computer Science', duration: '4 years' },
        { _id: '2', name: 'Business Administration', duration: '3 years' }
      ]);
      setFacilities([
        { _id: '1', name: 'Library', description: 'Well-stocked library' },
        { _id: '2', name: 'Computer Lab', description: 'Modern computer lab' }
      ]);
      setReviews([
        { _id: '1', rating: 5, comment: 'Excellent institute', studentName: 'John Doe' },
        { _id: '2', rating: 4, comment: 'Good facilities', studentName: 'Jane Smith' }
      ]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (id) {
      fetchInstituteData();
    }
  }, [id]);

  if (loading) return <div className="loading">Loading institute details...</div>;
  if (error && !institute) return <div className="error">Error: {error}</div>;

  return (
    <div className="institute-detail">
      {institute ? (
        <>
          <div className="institute-header">
            <h1>{institute.name}</h1>
            <p className="institute-email">{institute.email}</p>
            <p className="institute-phone">{institute.phone}</p>
            <p className="institute-address">{institute.address}</p>
            <p className="institute-description">{institute.description}</p>
          </div>

          <div className="institute-sections">
            {/* Courses Section */}
            <section className="courses-section">
              <h2>Courses Offered</h2>
              {courses.length === 0 ? (
                <p>No courses available.</p>
              ) : (
                <div className="courses-grid">
                  {courses.map(course => (
                    <div key={course._id} className="course-card">
                      <h3>{course.name}</h3>
                      <p>Duration: {course.duration}</p>
                      {course.fees && <p>Fees: ₹{course.fees}</p>}
                    </div>
                  ))}
                </div>
              )}
            </section>

            {/* Facilities Section */}
            <section className="facilities-section">
              <h2>Facilities</h2>
              {facilities.length === 0 ? (
                <p>No facilities information available.</p>
              ) : (
                <div className="facilities-grid">
                  {facilities.map(facility => (
                    <div key={facility._id} className="facility-card">
                      <h3>{facility.name}</h3>
                      <p>{facility.description}</p>
                    </div>
                  ))}
                </div>
              )}
            </section>

            {/* Reviews Section */}
            <section className="reviews-section">
              <h2>Student Reviews</h2>
              {reviews.length === 0 ? (
                <p>No reviews yet.</p>
              ) : (
                <div className="reviews-list">
                  {reviews.map(review => (
                    <div key={review._id} className="review-card">
                      <div className="review-header">
                        <h3>{review.studentName || 'Anonymous'}</h3>
                        <div className="rating">
                          {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
                        </div>
                      </div>
                      <p className="review-comment">{review.comment}</p>
                    </div>
                  ))}
                </div>
              )}
            </section>
          </div>
        </>
      ) : (
        <div className="error">Institute not found.</div>
      )}
    </div>
  );
};

export default InstituteDetail;
