import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { instituteAPI, adminAPI } from '../../services/api';
import AdminSidebar from './AdminSidebar';
import './AdminPanel.css';

const ManageInstitutes = () => {
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [institutes, setInstitutes] = useState([]);
  const [filteredInstitutes, setFilteredInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [currentInstitute, setCurrentInstitute] = useState(null);
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    fetchInstitutes();
  }, [user]);

  useEffect(() => {
    filterInstitutes();
  }, [institutes, searchTerm, statusFilter]);

  const fetchInstitutes = async () => {
    try {
      // Get all institutes including pending ones
      const [allInstitutes, pendingInstitutes] = await Promise.all([
        instituteAPI.getAll(),
        adminAPI.getPendingInstitutes()
      ]);

      // Combine and remove duplicates
      const allInstitutesData = allInstitutes.data || [];
      const pendingInstitutesData = pendingInstitutes.data || [];
      
      const combinedInstitutes = [
        ...pendingInstitutesData,
        ...allInstitutesData.filter(inst => 
          !pendingInstitutesData.some(pending => pending._id === inst._id)
        )
      ];

      setInstitutes(combinedInstitutes);
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load institutes'
      });
    } finally {
      setLoading(false);
    }
  };

  const filterInstitutes = () => {
    let filtered = institutes;

    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(institute =>
        institute.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        institute.city.toLowerCase().includes(searchTerm.toLowerCase()) ||
        institute.affiliation.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(institute => institute.status === statusFilter);
    }

    setFilteredInstitutes(filtered);
  };

  const handleStatusUpdate = async (instituteId, newStatus) => {
    try {
      await instituteAPI.updateStatus(instituteId, newStatus);
      
      addAlert({
        type: 'success',
        message: `Institute ${newStatus} successfully`
      });

      // Update local state
      setInstitutes(prev => prev.map(inst =>
        inst._id === instituteId ? { ...inst, status: newStatus } : inst
      ));

      // Add activity alert
      const institute = institutes.find(inst => inst._id === instituteId);
      if (institute) {
        addAlert({
          type: 'info',
          message: `Institute "${institute.name}" has been ${newStatus}`
        });
      }

    } catch (error) {
      addAlert({
        type: 'error',
        message: `Failed to update institute status`
      });
    }
  };

  const handleViewDetails = (institute) => {
    setCurrentInstitute(institute);
    setShowDetails(true);
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'warning', label: 'Pending' },
      approved: { class: 'success', label: 'Approved' },
      rejected: { class: 'danger', label: 'Rejected' },
      active: { class: 'success', label: 'Active' },
      inactive: { class: 'secondary', label: 'Inactive' }
    };

    const config = statusConfig[status] || { class: 'secondary', label: status };
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <AdminSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading institutes...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <AdminSidebar />
      <div className="dashboard-content">
        <div className="page-header">
          <h1>Manage Institutes</h1>
          <div className="header-stats">
            <span className="stat-item">
              Total: <strong>{institutes.length}</strong>
            </span>
            <span className="stat-item">
              Pending: <strong>{institutes.filter(i => i.status === 'pending').length}</strong>
            </span>
            <span className="stat-item">
              Approved: <strong>{institutes.filter(i => i.status === 'approved').length}</strong>
            </span>
          </div>
        </div>

        {/* Filters */}
        <div className="filters-card">
          <div className="filter-group">
            <div className="search-box">
              <i className="fas fa-search"></i>
              <input
                type="text"
                placeholder="Search institutes by name, city, or affiliation..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>
          </div>
          
          <div className="filter-group">
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Status</option>
              <option value="pending">Pending</option>
              <option value="approved">Approved</option>
              <option value="rejected">Rejected</option>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
            </select>
          </div>
        </div>

        {/* Institutes Table */}
        <div className="table-card">
          <div className="table-responsive">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Institute</th>
                  <th>Affiliation</th>
                  <th>Location</th>
                  <th>Contact</th>
                  <th>Status</th>
                  <th>Registration Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredInstitutes.length > 0 ? (
                  filteredInstitutes.map(institute => (
                    <tr key={institute._id}>
                      <td>
                        <div className="institute-cell">
                          <div className="institute-avatar-sm">
                            {institute.name?.charAt(0)}
                          </div>
                          <div className="institute-info">
                            <div className="institute-name">{institute.name}</div>
                            <div className="institute-email">{institute.email}</div>
                          </div>
                        </div>
                      </td>
                      <td>{institute.affiliation}</td>
                      <td>
                        {institute.city}, {institute.state}
                      </td>
                      <td>{institute.contactNumber}</td>
                      <td>
                        {getStatusBadge(institute.status)}
                      </td>
                      <td>
                        {new Date(institute.createdAt).toLocaleDateString()}
                      </td>
                      <td>
                        <div className="action-buttons">
                          <button
                            onClick={() => handleViewDetails(institute)}
                            className="btn btn-sm btn-outline"
                            title="View Details"
                          >
                            <i className="fas fa-eye"></i>
                          </button>
                          
                          {institute.status === 'pending' && (
                            <>
                              <button
                                onClick={() => handleStatusUpdate(institute._id, 'approved')}
                                className="btn btn-sm btn-success"
                                title="Approve"
                              >
                                <i className="fas fa-check"></i>
                              </button>
                              <button
                                onClick={() => handleStatusUpdate(institute._id, 'rejected')}
                                className="btn btn-sm btn-danger"
                                title="Reject"
                              >
                                <i className="fas fa-times"></i>
                              </button>
                            </>
                          )}
                          
                          {institute.status === 'approved' && (
                            <button
                              onClick={() => handleStatusUpdate(institute._id, 'inactive')}
                              className="btn btn-sm btn-warning"
                              title="Deactivate"
                            >
                              <i className="fas fa-pause"></i>
                            </button>
                          )}
                          
                          {institute.status === 'inactive' && (
                            <button
                              onClick={() => handleStatusUpdate(institute._id, 'approved')}
                              className="btn btn-sm btn-success"
                              title="Activate"
                            >
                              <i className="fas fa-play"></i>
                            </button>
                          )}

                          <button
                            onClick={() => handleStatusUpdate(institute._id, 'rejected')}
                            className="btn btn-sm btn-danger"
                            title="Remove"
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="7" className="no-data">
                      <div className="no-data-content">
                        <i className="fas fa-school"></i>
                        <p>No institutes found matching your criteria</p>
                      </div>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Institute Details Modal */}
        {showDetails && currentInstitute && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3>Institute Details</h3>
                <button 
                  onClick={() => setShowDetails(false)}
                  className="btn btn-close"
                >
                  <i className="fas fa-times"></i>
                </button>
              </div>
              
              <div className="modal-body">
                <div className="institute-details">
                  <div className="detail-section">
                    <h4>Basic Information</h4>
                    <div className="detail-grid">
                      <div className="detail-item">
                        <strong>Name:</strong>
                        <span>{currentInstitute.name}</span>
                      </div>
                      <div className="detail-item">
                        <strong>Email:</strong>
                        <span>{currentInstitute.email}</span>
                      </div>
                      <div className="detail-item">
                        <strong>Affiliation:</strong>
                        <span>{currentInstitute.affiliation}</span>
                      </div>
                      <div className="detail-item">
                        <strong>Website:</strong>
                        <a href={currentInstitute.website} target="_blank" rel="noopener noreferrer">
                          {currentInstitute.website}
                        </a>
                      </div>
                    </div>
                  </div>

                  <div className="detail-section">
                    <h4>Contact Information</h4>
                    <div className="detail-grid">
                      <div className="detail-item">
                        <strong>Phone:</strong>
                        <span>{currentInstitute.contactNumber}</span>
                      </div>
                      <div className="detail-item">
                        <strong>Address:</strong>
                        <span>{currentInstitute.address}</span>
                      </div>
                      <div className="detail-item">
                        <strong>City:</strong>
                        <span>{currentInstitute.city}</span>
                      </div>
                      <div className="detail-item">
                        <strong>State:</strong>
                        <span>{currentInstitute.state}</span>
                      </div>
                    </div>
                  </div>

                  <div className="detail-section">
                    <h4>Additional Information</h4>
                    <div className="detail-item full-width">
                      <strong>Description:</strong>
                      <p>{currentInstitute.description || 'No description provided.'}</p>
                    </div>
                  </div>

                  <div className="detail-section">
                    <h4>Registration Details</h4>
                    <div className="detail-grid">
                      <div className="detail-item">
                        <strong>Status:</strong>
                        {getStatusBadge(currentInstitute.status)}
                      </div>
                      <div className="detail-item">
                        <strong>Registered:</strong>
                        <span>{new Date(currentInstitute.createdAt).toLocaleDateString()}</span>
                      </div>
                      <div className="detail-item">
                        <strong>Last Updated:</strong>
                        <span>{new Date(currentInstitute.updatedAt).toLocaleDateString()}</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="modal-footer">
                <div className="action-buttons">
                  {currentInstitute.status === 'pending' && (
                    <>
                      <button
                        onClick={() => {
                          handleStatusUpdate(currentInstitute._id, 'approved');
                          setShowDetails(false);
                        }}
                        className="btn btn-success"
                      >
                        Approve Institute
                      </button>
                      <button
                        onClick={() => {
                          handleStatusUpdate(currentInstitute._id, 'rejected');
                          setShowDetails(false);
                        }}
                        className="btn btn-danger"
                      >
                        Reject Institute
                      </button>
                    </>
                  )}
                  <button
                    onClick={() => setShowDetails(false)}
                    className="btn btn-outline"
                  >
                    Close
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ManageInstitutes;
