import React, { useState, useEffect } from 'react';
import { enquiryAPI } from '../../services/api';
import './InstituteDashboard.css';

const Enquiries = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedEnquiry, setSelectedEnquiry] = useState(null);

  useEffect(() => {
    fetchEnquiries();
  }, []);

  const fetchEnquiries = async () => {
    try {
      // In real implementation, you'd have an API endpoint for institute enquiries
      // For demo, we'll simulate some data
      const mockEnquiries = [
        {
          _id: '1',
          userName: 'Rahul Sharma',
          userEmail: 'rahul@example.com',
          userPhone: '9876543210',
          message: 'I would like to know more about the admission process for the Computer Science course.',
          status: 'pending',
          createdAt: new Date('2024-01-15').toISOString()
        },
        {
          _id: '2',
          userName: 'Priya Patel',
          userEmail: 'priya@example.com',
          userPhone: '9876543211',
          message: 'Could you please share the fee structure for the MBA program?',
          status: 'responded',
          createdAt: new Date('2024-01-14').toISOString()
        }
      ];
      setEnquiries(mockEnquiries);
    } catch (error) {
      console.error('Error fetching enquiries:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRespond = async (enquiryId) => {
    try {
      // Update enquiry status
      const updatedEnquiries = enquiries.map(enquiry =>
        enquiry._id === enquiryId ? { ...enquiry, status: 'responded' } : enquiry
      );
      setEnquiries(updatedEnquiries);
      
      // In real implementation, you'd call an API here
      console.log('Responded to enquiry:', enquiryId);
    } catch (error) {
      console.error('Error responding to enquiry:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'status-pending', text: 'Pending' },
      responded: { class: 'status-responded', text: 'Responded' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.text}</span>;
  };

  if (loading) {
    return <div className="loading">Loading enquiries...</div>;
  }

  return (
    <div className="enquiries-management">
      <div className="section-header">
        <h2>Student Enquiries</h2>
        <div className="enquiry-stats">
          <span className="stat">
            Total: <strong>{enquiries.length}</strong>
          </span>
          <span className="stat">
            Pending: <strong>{enquiries.filter(e => e.status === 'pending').length}</strong>
          </span>
        </div>
      </div>

      <div className="enquiries-container">
        <div className="enquiries-list">
          {enquiries.length === 0 ? (
            <div className="empty-state">
              <p>No enquiries received yet.</p>
            </div>
          ) : (
            enquiries.map(enquiry => (
              <div 
                key={enquiry._id} 
                className={`enquiry-item ${selectedEnquiry?._id === enquiry._id ? 'selected' : ''}`}
                onClick={() => setSelectedEnquiry(enquiry)}
              >
                <div className="enquiry-header">
                  <h4>{enquiry.userName}</h4>
                  {getStatusBadge(enquiry.status)}
                </div>
                <p className="enquiry-preview">
                  {enquiry.message.length > 100 
                    ? `${enquiry.message.substring(0, 100)}...` 
                    : enquiry.message
                  }
                </p>
                <div className="enquiry-meta">
                  <span className="email">{enquiry.userEmail}</span>
                  <span className="date">{formatDate(enquiry.createdAt)}</span>
                </div>
              </div>
            ))
          )}
        </div>

        {selectedEnquiry && (
          <div className="enquiry-detail">
            <div className="detail-header">
              <h3>Enquiry Details</h3>
              <button 
                className="close-detail"
                onClick={() => setSelectedEnquiry(null)}
              >
                ×
              </button>
            </div>
            
            <div className="enquiry-info">
              <div className="info-row">
                <label>Student Name:</label>
                <span>{selectedEnquiry.userName}</span>
              </div>
              <div className="info-row">
                <label>Email:</label>
                <span>{selectedEnquiry.userEmail}</span>
              </div>
              <div className="info-row">
                <label>Phone:</label>
                <span>{selectedEnquiry.userPhone}</span>
              </div>
              <div className="info-row">
                <label>Date:</label>
                <span>{formatDate(selectedEnquiry.createdAt)}</span>
              </div>
              <div className="info-row">
                <label>Status:</label>
                {getStatusBadge(selectedEnquiry.status)}
              </div>
            </div>

            <div className="enquiry-message">
              <label>Message:</label>
              <p>{selectedEnquiry.message}</p>
            </div>

            {selectedEnquiry.status === 'pending' && (
              <div className="enquiry-actions">
                <button 
                  className="btn-primary"
                  onClick={() => handleRespond(selectedEnquiry._id)}
                >
                  Mark as Responded
                </button>
                <button className="btn-secondary">
                  Send Email Response
                </button>
              </div>
            )}

            {selectedEnquiry.status === 'responded' && (
              <div className="response-info">
                <p className="success-message">✓ This enquiry has been responded to.</p>
              </div>
            )}
          </div>
        )}

        {!selectedEnquiry && enquiries.length > 0 && (
          <div className="enquiry-placeholder">
            <p>Select an enquiry to view details</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Enquiries;
