import React, { useState, useEffect } from 'react';
import { adminAPI } from '../../services/api';
import './AdminPanel.css';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [pendingInstitutes, setPendingInstitutes] = useState([]);
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchInstitutes();
  }, []);

  const fetchInstitutes = async () => {
    try {
      setLoading(true);
      console.log('üîÑ Fetching institutes for admin...');
      
      const [pendingResponse, analyticsResponse] = await Promise.all([
        adminAPI.getPendingInstitutes(),
        adminAPI.getAnalytics()
      ]);
      
      console.log('‚úÖ Pending institutes:', pendingResponse.data);
      console.log('‚úÖ Analytics:', analyticsResponse.data);
      
      setPendingInstitutes(pendingResponse.data);
      
      // For approved institutes, we'll fetch all and filter
      const allInstitutesResponse = await fetch('http://localhost:5000/api/institutes');
      const allInstitutes = await allInstitutesResponse.json();
      setInstitutes(allInstitutes);
      
    } catch (error) {
      console.error('‚ùå Error fetching institutes:', error);
      setMessage('Error loading institutes: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (instituteId) => {
    try {
      console.log(`‚úÖ Approving institute: ${instituteId}`);
      await adminAPI.approveInstitute(instituteId);
      
      setMessage('Institute approved successfully!');
      
      // Refresh the list
      fetchInstitutes();
      
    } catch (error) {
      console.error('‚ùå Error approving institute:', error);
      setMessage('Error approving institute: ' + error.message);
    }
  };

  const handleReject = async (instituteId) => {
    if (window.confirm('Are you sure you want to reject this institute? This action cannot be undone.')) {
      try {
        // Since we don't have reject endpoint, we'll just remove from pending
        setPendingInstitutes(prev => prev.filter(inst => inst._id !== instituteId));
        setMessage('Institute rejected successfully!');
      } catch (error) {
        console.error('Error rejecting institute:', error);
        setMessage('Error rejecting institute: ' + error.message);
      }
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN');
  };

  if (loading) {
    return (
      <div className="manage-institutes">
        <div className="loading">Loading institutes...</div>
      </div>
    );
  }

  return (
    <div className="manage-institutes">
      <div className="section-header">
        <h2>Manage Institutes</h2>
        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}
      </div>

      <div className="tab-navigation">
        <button 
          className={`tab-btn ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          ‚è≥ Pending Approval ({pendingInstitutes.length})
        </button>
        <button 
          className={`tab-btn ${activeTab === 'approved' ? 'active' : ''}`}
          onClick={() => setActiveTab('approved')}
        >
          ‚úÖ Approved Institutes ({institutes.length})
        </button>
      </div>

      {activeTab === 'pending' && (
        <div className="institutes-section">
          <h3>‚è≥ Institutes Waiting for Approval</h3>
          
          {pendingInstitutes.length === 0 ? (
            <div className="empty-state">
              <p>No pending institute approvals.</p>
              <p className="help-text">
                üí° Institutes will appear here when they register. 
                You can create test institutes using the backend script.
              </p>
            </div>
          ) : (
            <div className="institutes-list">
              {pendingInstitutes.map(institute => (
                <div key={institute._id} className="institute-card pending">
                  <div className="institute-info">
                    <h3>{institute.name}</h3>
                    <div className="institute-details">
                      <p><strong>Category:</strong> {institute.category}</p>
                      <p><strong>Affiliation:</strong> {institute.affiliation}</p>
                      <p><strong>Location:</strong> {institute.city}, {institute.state}</p>
                      <p><strong>Contact:</strong> {institute.contactEmail} | {institute.contactPhone}</p>
                      <p><strong>Registered:</strong> {formatDate(institute.createdAt)}</p>
                    </div>
                    {institute.description && (
                      <p className="institute-description">
                        <strong>Description:</strong> {institute.description}
                      </p>
                    )}
                    {institute.facilities && institute.facilities.length > 0 && (
                      <p className="institute-facilities">
                        <strong>Facilities:</strong> {institute.facilities.join(', ')}
                      </p>
                    )}
                  </div>
                  <div className="institute-actions">
                    <button 
                      onClick={() => handleApprove(institute._id)}
                      className="btn-approve"
                    >
                      ‚úÖ Approve
                    </button>
                    <button 
                      onClick={() => handleReject(institute._id)}
                      className="btn-reject"
                    >
                      ‚ùå Reject
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {activeTab === 'approved' && (
        <div className="institutes-section">
          <h3>‚úÖ Approved Institutes</h3>
          
          {institutes.length === 0 ? (
            <div className="empty-state">
              <p>No approved institutes yet.</p>
            </div>
          ) : (
            <div className="institutes-list">
              {institutes.map(institute => (
                <div key={institute._id} className="institute-card approved">
                  <div className="institute-info">
                    <h3>{institute.name}</h3>
                    <div className="institute-details">
                      <p><strong>Category:</strong> {institute.category}</p>
                      <p><strong>Affiliation:</strong> {institute.affiliation}</p>
                      <p><strong>Location:</strong> {institute.city}, {institute.state}</p>
                      <p><strong>Status:</strong> 
                        <span className="status approved">Approved</span>
                      </p>
                    </div>
                  </div>
                  <div className="institute-actions">
                    <button className="btn-view">
                      üëÅÔ∏è View Details
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ManageInstitutes;
