import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import toast from 'react-hot-toast';

const Reviews = ({ institute }) => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchReviews();
  }, [institute._id]);

  const fetchReviews = async () => {
    try {
      const response = await api.get(`/reviews/institute/${institute._id}`);
      setReviews(response.data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFlagReview = async (reviewId) => {
    if (!window.confirm('Are you sure you want to flag this review as inappropriate?')) return;

    try {
      // Note: This would typically call an API endpoint to flag the review
      toast.success('Review flagged for admin review');
      // In a real implementation, you would call:
      // await api.patch(`/reviews/${reviewId}/flag`);
    } catch (error) {
      toast.error('Failed to flag review');
      console.error('Error flagging review:', error);
    }
  };

  const renderStars = (rating) => {
    return '‚≠ê'.repeat(rating) + '‚òÜ'.repeat(5 - rating);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading reviews...</p>
      </div>
    );
  }

  return (
    <div className="reviews-management">
      <div className="section-header">
        <h2>Reviews Management</h2>
        <p>View and manage reviews for your institute</p>
        
        <div className="reviews-summary">
          <div className="summary-card">
            <div className="summary-number">{reviews.length}</div>
            <div className="summary-label">Total Reviews</div>
          </div>
          <div className="summary-card">
            <div className="summary-number">
              {institute.rating || 'N/A'}
            </div>
            <div className="summary-label">Average Rating</div>
          </div>
        </div>
      </div>

      <div className="reviews-list">
        {reviews.length === 0 ? (
          <div className="empty-state">
            <h3>No reviews yet</h3>
            <p>Reviews from students and parents will appear here.</p>
          </div>
        ) : (
          reviews.map((review) => (
            <div key={review._id} className="review-card">
              <div className="review-header">
                <div className="reviewer-info">
                  <strong>{review.user.name}</strong>
                  <div className="review-date">
                    {new Date(review.createdAt).toLocaleDateString()}
                  </div>
                </div>
                <div className="review-rating">
                  <div className="stars">
                    {renderStars(review.rating)}
                  </div>
                  <span className="rating-text">{review.rating}/5 stars</span>
                </div>
              </div>

              <div className="review-content">
                <p>{review.reviewText}</p>
              </div>

              <div className="review-actions">
                <button 
                  onClick={() => handleFlagReview(review._id)}
                  className="btn btn-sm btn-outline"
                >
                  üö© Flag as Inappropriate
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Reviews;
