import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import AdminSidebar from './AdminSidebar';
import ManageInstitutes from './ManageInstitutes';
import ManageUsers from './ManageUsers';
import ManageReviews from './ManageReviews';
import AnalyticsDashboard from './AnalyticsDashboard';
import { instituteAPI, userAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';
import './AdminPanel.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalInstitutes: 0,
    pendingInstitutes: 0,
    totalUsers: 0,
    totalReviews: 0
  });
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is admin
    const userRole = user?.user?.role || user?.role;
    if (userRole !== 'admin') {
      navigate('/');
      return;
    }
    fetchDashboardData();
  }, [user, navigate]);

  const fetchDashboardData = async () => {
    try {
      const [institutesRes, pendingRes, usersRes] = await Promise.all([
        instituteAPI.getAll({ page: 1, limit: 1 }),
        instituteAPI.getPending(),
        userAPI.getAll()
      ]);

      setStats({
        totalInstitutes: institutesRes.data?.total || 0,
        pendingInstitutes: pendingRes.data?.length || 0,
        totalUsers: usersRes.data?.length || 0,
        totalReviews: 0
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="admin-dashboard">
        <AdminSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading admin dashboard...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <AdminSidebar />
      
      <div className="dashboard-content">
        <div className="content-header">
          <h1>Admin Dashboard</h1>
          <p>Manage platform activities and monitor performance</p>
        </div>

        <Routes>
          <Route 
            path="/" 
            element={<DashboardHome stats={stats} />} 
          />
          <Route path="/institutes" element={<ManageInstitutes />} />
          <Route path="/users" element={<ManageUsers />} />
          <Route path="/reviews" element={<ManageReviews />} />
          <Route path="/analytics" element={<AnalyticsDashboard />} />
        </Routes>
      </div>
    </div>
  );
};

const DashboardHome = ({ stats }) => {
  return (
    <div className="dashboard-home">
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üè´</div>
          <div className="stat-info">
            <h3>{stats.totalInstitutes}</h3>
            <p>Total Institutes</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">‚è≥</div>
          <div className="stat-info">
            <h3>{stats.pendingInstitutes}</h3>
            <p>Pending Approvals</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-info">
            <h3>{stats.totalUsers}</h3>
            <p>Total Users</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <h3>{stats.totalReviews}</h3>
            <p>Total Reviews</p>
          </div>
        </div>
      </div>

      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <div className="action-card" onClick={() => window.location.href = '/admin/institutes'}>
            <div className="action-icon">üè´</div>
            <h3>Manage Institutes</h3>
            <p>Approve or reject institute registrations</p>
          </div>
          <div className="action-card" onClick={() => window.location.href = '/admin/users'}>
            <div className="action-icon">üë•</div>
            <h3>Manage Users</h3>
            <p>View and manage all users</p>
          </div>
          <div className="action-card" onClick={() => window.location.href = '/admin/reviews'}>
            <div className="action-icon">‚≠ê</div>
            <h3>Moderate Reviews</h3>
            <p>Approve or remove reviews</p>
          </div>
          <div className="action-card" onClick={() => window.location.href = '/admin/analytics'}>
            <div className="action-icon">üìä</div>
            <h3>View Analytics</h3>
            <p>Platform statistics and insights</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
