import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { adminAPI } from '../../services/api';
import AdminSidebar from './AdminSidebar';
import './AdminPanel.css';

const ManageUsers = () => {
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    fetchUsers();
  }, [user]);

  useEffect(() => {
    filterUsers();
  }, [users, searchTerm, roleFilter, statusFilter]);

  const fetchUsers = async () => {
    try {
      const response = await adminAPI.getAllUsers();
      
      // Handle different response structures
      let usersData = [];
      
      if (response.data && Array.isArray(response.data)) {
        usersData = response.data;
      } else if (response.data && response.data.data && Array.isArray(response.data.data)) {
        usersData = response.data.data;
      } else if (Array.isArray(response.data)) {
        usersData = response.data;
      } else {
        console.warn('Unexpected API response structure for users:', response);
        usersData = [];
      }
      
      setUsers(usersData);
    } catch (error) {
      console.error('Failed to load users:', error);
      addAlert({
        type: 'error',
        message: 'Failed to load users'
      });
      setUsers([]); // Ensure users is always an array
    } finally {
      setLoading(false);
    }
  };

  const filterUsers = () => {
    // Ensure users is always treated as an array
    const usersArray = Array.isArray(users) ? users : [];
    let filtered = usersArray;

    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(user =>
        user.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply role filter
    if (roleFilter !== 'all') {
      filtered = filtered.filter(user => user.role === roleFilter);
    }

    // Apply status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(user => user.status === statusFilter);
    }

    setFilteredUsers(filtered);
  };

  const handleStatusUpdate = async (userId, newStatus) => {
    try {
      // API call to update user status would go here
      // await userAPI.updateStatus(userId, newStatus);
      
      // Update local state
      setUsers(prev => prev.map(u =>
        u._id === userId ? { ...u, status: newStatus } : u
      ));

      addAlert({
        type: 'success',
        message: `User ${newStatus} successfully`
      });

      // Add activity alert
      const userObj = users.find(u => u._id === userId);
      if (userObj) {
        addAlert({
          type: 'info',
          message: `User "${userObj.name}" has been ${newStatus}`
        });
      }

    } catch (error) {
      addAlert({
        type: 'error',
        message: `Failed to update user status`
      });
    }
  };

  const getRoleBadge = (role) => {
    const roleConfig = {
      user: { class: 'primary', label: 'User' },
      institute: { class: 'success', label: 'Institute' },
      admin: { class: 'danger', label: 'Admin' }
    };

    const config = roleConfig[role] || { class: 'secondary', label: role };
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      active: { class: 'success', label: 'Active' },
      inactive: { class: 'secondary', label: 'Inactive' },
      suspended: { class: 'danger', label: 'Suspended' }
    };

    const config = statusConfig[status] || { class: 'secondary', label: status };
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <AdminSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading users...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <AdminSidebar />
      <div className="dashboard-content">
        <div className="page-header">
          <h1>Manage Users</h1>
          <div className="header-stats">
            <span className="stat-item">
              Total: <strong>{users.length}</strong>
            </span>
            <span className="stat-item">
              Students/Parents: <strong>{users.filter(u => u.role === 'user').length}</strong>
            </span>
            <span className="stat-item">
              Institutes: <strong>{users.filter(u => u.role === 'institute').length}</strong>
            </span>
            <span className="stat-item">
              Active: <strong>{users.filter(u => u.status === 'active').length}</strong>
            </span>
          </div>
        </div>

        {/* Filters */}
        <div className="filters-card">
          <div className="filter-group">
            <div className="search-box">
              <i className="fas fa-search"></i>
              <input
                type="text"
                placeholder="Search users by name or email..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>
          </div>
          
          <div className="filter-group">
            <select
              value={roleFilter}
              onChange={(e) => setRoleFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Roles</option>
              <option value="user">Students/Parents</option>
              <option value="institute">Institutes</option>
              <option value="admin">Admins</option>
            </select>
          </div>

          <div className="filter-group">
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Status</option>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
              <option value="suspended">Suspended</option>
            </select>
          </div>
        </div>

        {/* Users Table */}
        <div className="table-card">
          <div className="table-responsive">
            <table className="data-table">
              <thead>
                <tr>
                  <th>User</th>
                  <th>Role</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Status</th>
                  <th>Registration Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredUsers.length > 0 ? (
                  filteredUsers.map(user => (
                    <tr key={user._id}>
                      <td>
                        <div className="user-cell">
                          <div className="user-avatar-sm">
                            {user.name?.charAt(0) || 'U'}
                          </div>
                          <div className="user-info">
                            <div className="user-name">{user.name || 'Unknown User'}</div>
                            {user.role === 'institute' && (
                              <div className="user-institute">{user.instituteName || 'Institute'}</div>
                            )}
                          </div>
                        </div>
                      </td>
                      <td>
                        {getRoleBadge(user.role)}
                      </td>
                      <td>{user.email || 'No email'}</td>
                      <td>{user.phone || 'N/A'}</td>
                      <td>
                        {getStatusBadge(user.status)}
                      </td>
                      <td>
                        {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}
                      </td>
                      <td>
                        <div className="action-buttons">
                          {user.status === 'active' ? (
                            <button
                              onClick={() => handleStatusUpdate(user._id, 'inactive')}
                              className="btn btn-sm btn-warning"
                              title="Deactivate"
                            >
                              <i className="fas fa-pause"></i>
                            </button>
                          ) : (
                            <button
                              onClick={() => handleStatusUpdate(user._id, 'active')}
                              className="btn btn-sm btn-success"
                              title="Activate"
                            >
                              <i className="fas fa-play"></i>
                            </button>
                          )}
                          
                          <button
                            onClick={() => handleStatusUpdate(user._id, 'suspended')}
                            className="btn btn-sm btn-danger"
                            title="Suspend"
                          >
                            <i className="fas fa-ban"></i>
                          </button>

                          {user.role !== 'admin' && (
                            <button
                              className="btn btn-sm btn-outline"
                              title="View Details"
                            >
                              <i className="fas fa-eye"></i>
                            </button>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="7" className="no-data">
                      <div className="no-data-content">
                        <i className="fas fa-users"></i>
                        <p>No users found matching your criteria</p>
                      </div>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* User Statistics */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon primary">
              <i className="fas fa-user-graduate"></i>
            </div>
            <div className="stat-info">
              <h3>{users.filter(u => u.role === 'user').length}</h3>
              <p>Students/Parents</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon success">
              <i className="fas fa-school"></i>
            </div>
            <div className="stat-info">
              <h3>{users.filter(u => u.role === 'institute').length}</h3>
              <p>Institutes</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon warning">
              <i className="fas fa-user-shield"></i>
            </div>
            <div className="stat-info">
              <h3>{users.filter(u => u.role === 'admin').length}</h3>
              <p>Administrators</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon danger">
              <i className="fas fa-user-slash"></i>
            </div>
            <div className="stat-info">
              <h3>{users.filter(u => u.status !== 'active').length}</h3>
              <p>Inactive Users</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManageUsers;
