import React, { useState } from 'react';
import './InstituteDashboard.css';

const CourseManagement = ({ institute, onUpdate }) => {
  const [courses, setCourses] = useState(institute.courses || []);
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    fees: '',
    images: []
  });
  const [imageFiles, setImageFiles] = useState([]);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setImageFiles(files);
    
    // Create preview URLs
    const imageUrls = files.map(file => URL.createObjectURL(file));
    setFormData({
      ...formData,
      images: [...formData.images, ...imageUrls]
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const courseData = {
      ...formData,
      fees: parseFloat(formData.fees),
      images: formData.images
    };

    if (editingCourse) {
      // Update existing course
      const updatedCourses = courses.map(course =>
        course === editingCourse ? courseData : course
      );
      setCourses(updatedCourses);
    } else {
      // Add new course
      setCourses([...courses, { ...courseData, id: Date.now() }]);
    }

    resetForm();
    onUpdate();
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setFormData({
      title: course.title,
      description: course.description,
      duration: course.duration,
      fees: course.fees.toString(),
      images: course.images
    });
    setShowForm(true);
  };

  const handleDelete = (courseToDelete) => {
    setCourses(courses.filter(course => course !== courseToDelete));
    onUpdate();
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      duration: '',
      fees: '',
      images: []
    });
    setImageFiles([]);
    setEditingCourse(null);
    setShowForm(false);
  };

  const removeImage = (index) => {
    const newImages = formData.images.filter((_, i) => i !== index);
    setFormData({ ...formData, images: newImages });
  };

  return (
    <div className="course-management">
      <div className="section-header">
        <h2>Course Management</h2>
        <button 
          className="btn-primary"
          onClick={() => setShowForm(true)}
        >
          Add New Course
        </button>
      </div>

      {showForm && (
        <div className="form-modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingCourse ? 'Edit Course' : 'Add New Course'}</h3>
              <button className="close-btn" onClick={resetForm}>√ó</button>
            </div>
            
            <form onSubmit={handleSubmit} className="course-form">
              <div className="form-group">
                <label>Course Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  rows="4"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Duration *</label>
                  <input
                    type="text"
                    name="duration"
                    value={formData.duration}
                    onChange={handleInputChange}
                    placeholder="e.g., 4 years, 6 months"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Fees (‚Çπ) *</label>
                  <input
                    type="number"
                    name="fees"
                    value={formData.fees}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Course Images</label>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleImageChange}
                />
                <div className="image-previews">
                  {formData.images.map((image, index) => (
                    <div key={index} className="image-preview">
                      <img src={image} alt={`Preview ${index}`} />
                      <button
                        type="button"
                        onClick={() => removeImage(index)}
                        className="remove-image"
                      >
                        √ó
                      </button>
                    </div>
                  ))}
                </div>
              </div>

              <div className="form-actions">
                <button type="button" onClick={resetForm} className="btn-secondary">
                  Cancel
                </button>
                <button type="submit" className="btn-primary">
                  {editingCourse ? 'Update Course' : 'Add Course'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="courses-grid">
        {courses.length === 0 ? (
          <div className="empty-state">
            <p>No courses added yet.</p>
            <button 
              className="btn-primary"
              onClick={() => setShowForm(true)}
            >
              Add Your First Course
            </button>
          </div>
        ) : (
          courses.map((course, index) => (
            <div key={index} className="course-card">
              <div className="course-images">
                {course.images && course.images.length > 0 ? (
                  <img src={course.images[0]} alt={course.title} />
                ) : (
                  <div className="no-image">No Image</div>
                )}
              </div>
              
              <div className="course-info">
                <h3>{course.title}</h3>
                <p className="course-description">{course.description}</p>
                <div className="course-details">
                  <span className="duration">‚è±Ô∏è {course.duration}</span>
                  <span className="fees">üí∞ ‚Çπ{course.fees?.toLocaleString()}</span>
                </div>
              </div>
              
              <div className="course-actions">
                <button 
                  onClick={() => handleEdit(course)}
                  className="btn-edit"
                >
                  Edit
                </button>
                <button 
                  onClick={() => handleDelete(course)}
                  className="btn-delete"
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CourseManagement;
