import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteAPI } from '../../services/api';
import './HomePage.css';

const HomePage = () => {
  const [featuredInstitutes, setFeaturedInstitutes] = useState([]);
  const [stats, setStats] = useState({
    institutes: 0,
    reviews: 0,
    students: 0
  });

  useEffect(() => {
    fetchFeaturedInstitutes();
    fetchStats();
  }, []);

  const fetchFeaturedInstitutes = async () => {
    try {
      const response = await instituteAPI.getAll({ limit: 6 });
      setFeaturedInstitutes(response.data);
    } catch (error) {
      console.error('Error fetching featured institutes:', error);
    }
  };

  const fetchStats = async () => {
    // Mock stats - in real app, get from API
    setStats({
      institutes: 127,
      reviews: 458,
      students: 12500
    });
  };

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <div className="hero-content">
            <div className="hero-text">
              <h1>Find Your Perfect Educational Institution</h1>
              <p>
                Discover schools, colleges, and coaching centers with detailed reviews, 
                course information, and facilities. Make informed decisions for your educational journey.
              </p>
              <div className="hero-buttons">
                <Link to="/institutes" className="btn btn-primary btn-large">
                  Explore Institutes
                </Link>
                <Link to="/register" className="btn btn-outline btn-large">
                  List Your Institute
                </Link>
              </div>
            </div>
            <div className="hero-image">
              <div className="floating-card card-1">
                <span>üè´</span>
                <p>Top Schools</p>
              </div>
              <div className="floating-card card-2">
                <span>üéì</span>
                <p>Best Colleges</p>
              </div>
              <div className="floating-card card-3">
                <span>‚≠ê</span>
                <p>Rated & Reviewed</p>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section">
        <div className="container">
          <div className="stats-grid">
            <div className="stat-item">
              <h3>{stats.institutes}+</h3>
              <p>Verified Institutes</p>
            </div>
            <div className="stat-item">
              <h3>{stats.reviews}+</h3>
              <p>Student Reviews</p>
            </div>
            <div className="stat-item">
              <h3>{stats.students}+</h3>
              <p>Happy Students</p>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Institutes */}
      <section className="featured-section">
        <div className="container">
          <h2>Featured Institutes</h2>
          <p className="section-subtitle">
            Discover some of our top-rated educational institutions
          </p>
          
          <div className="institutes-grid">
            {featuredInstitutes.map(institute => (
              <div key={institute._id} className="institute-card">
                <div className="card-image">
                  {institute.images?.banner ? (
                    <img 
                      src={`http://localhost:5000/uploads/${institute.images.banner}`} 
                      alt={institute.name}
                    />
                  ) : (
                    <div className="image-placeholder">
                      <span>üè´</span>
                    </div>
                  )}
                  <div className="card-rating">
                    ‚≠ê {institute.rating?.average?.toFixed(1) || '4.5'}
                  </div>
                </div>
                
                <div className="card-content">
                  <h3>{institute.name}</h3>
                  <p className="card-category">{institute.category}</p>
                  <p className="card-location">
                    üìç {institute.address?.city}, {institute.address?.state}
                  </p>
                  <div className="card-facilities">
                    {institute.facilities?.slice(0, 3).map((facility, index) => (
                      <span key={index} className="facility-tag">
                        {facility.name}
                      </span>
                    ))}
                  </div>
                </div>
                
                <div className="card-actions">
                  <Link 
                    to={`/institute/${institute._id}`}
                    className="btn btn-primary"
                  >
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>

          <div className="section-actions">
            <Link to="/institutes" className="btn btn-outline">
              View All Institutes
            </Link>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <div className="container">
          <h2>Why Choose EduList?</h2>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">üîç</div>
              <h3>Easy Search</h3>
              <p>Find institutes by location, category, fees, and facilities with our advanced filters.</p>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon">‚≠ê</div>
              <h3>Verified Reviews</h3>
              <p>Read genuine reviews from students and parents to make informed decisions.</p>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon">üìä</div>
              <h3>Detailed Information</h3>
              <p>Get complete details about courses, facilities, fees, and admission procedures.</p>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon">üí¨</div>
              <h3>Direct Enquiry</h3>
              <p>Contact institutes directly through our platform for quick responses.</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
