import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    role: 'user'
  });
  const [instituteData, setInstituteData] = useState({
    name: '',
    category: 'school',
    affiliation: '',
    address: {
      city: '',
      state: ''
    },
    contact: {
      phone: '',
      website: ''
    },
    description: '',
    establishedYear: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { register } = useAuth();
  const navigate = useNavigate();

  const handleUserChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleInstituteChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setInstituteData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setInstituteData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Validation
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      setLoading(false);
      return;
    }

    // Prepare registration data
    const registrationData = {
      name: formData.name,
      email: formData.email,
      phone: formData.phone,
      password: formData.password,
      role: formData.role
    };

    // Add institute data if registering as institute
    if (formData.role === 'institute') {
      if (!instituteData.name || !instituteData.affiliation) {
        setError('Institute name and affiliation are required');
        setLoading(false);
        return;
      }
      registrationData.instituteData = instituteData;
    }

    console.log('Registration data:', registrationData);

    const result = await register(registrationData);
    
    if (result.success) {
      if (formData.role === 'institute') {
        navigate('/login'); // Redirect to login for institutes (needs approval)
        alert('Institute registered successfully! Please wait for admin approval before logging in.');
      } else {
        navigate(formData.role === 'admin' ? '/admin' : '/user');
      }
    } else {
      setError(result.message || 'Registration failed');
    }
    
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Create Your Account</h2>
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Full Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleUserChange}
              required
              placeholder="Enter your full name"
            />
          </div>

          <div className="form-group">
            <label>Email Address *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleUserChange}
              required
              placeholder="Enter your email"
            />
          </div>

          <div className="form-group">
            <label>Phone Number *</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleUserChange}
              required
              placeholder="Enter your phone number"
            />
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleUserChange}
              required
              placeholder="Enter your password (min 6 characters)"
              minLength="6"
            />
          </div>

          <div className="form-group">
            <label>Confirm Password *</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleUserChange}
              required
              placeholder="Confirm your password"
            />
          </div>

          <div className="form-group">
            <label>Register As *</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleUserChange}
              required
            >
              <option value="user">Student/Parent</option>
              <option value="institute">Institute</option>
            </select>
          </div>

          {formData.role === 'institute' && (
            <div className="institute-fields">
              <h3>Institute Information</h3>
              
              <div className="form-group">
                <label>Institute Name *</label>
                <input
                  type="text"
                  name="name"
                  value={instituteData.name}
                  onChange={handleInstituteChange}
                  required
                  placeholder="Enter institute name"
                />
              </div>

              <div className="form-group">
                <label>Category *</label>
                <select
                  name="category"
                  value={instituteData.category}
                  onChange={handleInstituteChange}
                  required
                >
                  <option value="school">School</option>
                  <option value="college">College</option>
                  <option value="coaching">Coaching Center</option>
                  <option value="preschool">Preschool</option>
                  <option value="university">University</option>
                </select>
              </div>

              <div className="form-group">
                <label>Affiliation *</label>
                <input
                  type="text"
                  name="affiliation"
                  value={instituteData.affiliation}
                  onChange={handleInstituteChange}
                  required
                  placeholder="e.g., CBSE, State Board, UGC"
                />
              </div>

              <div className="form-group">
                <label>City</label>
                <input
                  type="text"
                  name="address.city"
                  value={instituteData.address.city}
                  onChange={handleInstituteChange}
                  placeholder="Enter city"
                />
              </div>

              <div className="form-group">
                <label>State</label>
                <input
                  type="text"
                  name="address.state"
                  value={instituteData.address.state}
                  onChange={handleInstituteChange}
                  placeholder="Enter state"
                />
              </div>

              <div className="form-group">
                <label>Website</label>
                <input
                  type="url"
                  name="contact.website"
                  value={instituteData.contact.website}
                  onChange={handleInstituteChange}
                  placeholder="Enter website URL"
                />
              </div>

              <div className="form-group">
                <label>Established Year</label>
                <input
                  type="number"
                  name="establishedYear"
                  value={instituteData.establishedYear}
                  onChange={handleInstituteChange}
                  placeholder="Established year"
                  min="1900"
                  max="2024"
                />
              </div>

              <div className="form-group">
                <label>Description</label>
                <textarea
                  name="description"
                  value={instituteData.description}
                  onChange={handleInstituteChange}
                  placeholder="Describe your institute"
                  rows="4"
                />
              </div>
            </div>
          )}

          <button 
            type="submit" 
            className="auth-button" 
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Register'}
          </button>
        </form>

        <p className="auth-link">
          Already have an account? <Link to="/login">Login here</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
