import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Common.css';

const Header = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/');
    setIsMenuOpen(false);
  };

  const getDashboardLink = () => {
    if (!user) return '/login';
    
    switch(user.role) {
      case 'admin':
        return '/admin/dashboard';
      case 'institute':
        return '/institute/dashboard';
      case 'user':
        return '/user/dashboard';
      default:
        return '/';
    }
  };

  const getDashboardName = () => {
    if (!user) return 'Dashboard';
    
    switch(user.role) {
      case 'admin':
        return 'Admin Panel';
      case 'institute':
        return 'Institute Panel';
      case 'user':
        return 'My Dashboard';
      default:
        return 'Dashboard';
    }
  };

  return (
    <header className="header">
      <div className="header-container">
        <Link to="/" className="logo" onClick={() => setIsMenuOpen(false)}>
          <span className="logo-icon">ðŸŽ“</span>
          EduList
        </Link>

        <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
          <Link to="/" onClick={() => setIsMenuOpen(false)}>Home</Link>
          <Link to="/institutes" onClick={() => setIsMenuOpen(false)}>Institutes</Link>
          
          {user ? (
            <>
              <Link to={getDashboardLink()} onClick={() => setIsMenuOpen(false)} className="dashboard-link">
                {getDashboardName()}
              </Link>
              <div className="user-menu">
                <span className="user-greeting">Hello, {user.name}</span>
                <button onClick={handleLogout} className="logout-btn">
                  Logout
                </button>
              </div>
            </>
          ) : (
            <>
              <Link to="/login" onClick={() => setIsMenuOpen(false)}>Login</Link>
              <Link to="/register" onClick={() => setIsMenuOpen(false)} className="register-btn">
                Register
              </Link>
            </>
          )}
        </nav>

        <button 
          className="menu-toggle"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          â˜°
        </button>
      </div>
    </header>
  );
};

export default Header;
