import React, { useState, useEffect } from 'react';
import { enquiryService, instituteService } from '../../services/api';
import './InstituteDashboard.css';

const Enquiries = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [respondingTo, setRespondingTo] = useState(null);
  const [responseText, setResponseText] = useState('');
  const [instituteId, setInstituteId] = useState(null);

  useEffect(() => {
    fetchInstituteAndEnquiries();
  }, []);

  const fetchInstituteAndEnquiries = async () => {
    try {
      setLoading(true);
      
      // First get the institute to get the ID
      const instituteRes = await instituteService.getCurrentInstitute();
      const instituteData = instituteRes.data;
      
      if (instituteData && instituteData._id) {
        setInstituteId(instituteData._id);
        const enquiriesRes = await enquiryService.getByInstitute(instituteData._id);
        setEnquiries(enquiriesRes.data || []);
      } else {
        throw new Error('Institute not found');
      }
    } catch (error) {
      console.error('Error fetching enquiries:', error);
      setEnquiries([]);
    } finally {
      setLoading(false);
    }
  };

  const handleRespond = async (enquiryId) => {
    if (!responseText.trim()) return;

    try {
      await enquiryService.updateStatus(enquiryId, {
        status: 'responded',
        response: responseText
      });
      
      setRespondingTo(null);
      setResponseText('');
      fetchInstituteAndEnquiries(); // Refresh the list
    } catch (error) {
      console.error('Error responding to enquiry:', error);
    }
  };

  const handleCloseEnquiry = async (enquiryId) => {
    try {
      await enquiryService.updateStatus(enquiryId, {
        status: 'closed'
      });
      fetchInstituteAndEnquiries();
    } catch (error) {
      console.error('Error closing enquiry:', error);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'pending', label: 'Pending' },
      responded: { class: 'responded', label: 'Responded' },
      closed: { class: 'closed', label: 'Closed' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return <div className="loading">Loading enquiries...</div>;
  }

  return (
    <div className="enquiries-management">
      <div className="dashboard-section">
        <div className="section-header">
          <div>
            <h2>Student Enquiries</h2>
            <p>Manage and respond to student enquiries</p>
          </div>
          <div className="enquiries-stats">
            <span className="stat">
              Total: <strong>{enquiries.length}</strong>
            </span>
            <span className="stat">
              Pending: <strong>{enquiries.filter(e => e.status === 'pending').length}</strong>
            </span>
          </div>
        </div>

        {enquiries.length === 0 ? (
          <div className="no-data">
            <h3>No Enquiries Yet</h3>
            <p>You haven't received any enquiries from students yet.</p>
          </div>
        ) : (
          <div className="enquiries-list">
            {enquiries.map(enquiry => (
              <div key={enquiry._id} className="enquiry-card">
                <div className="enquiry-header">
                  <div className="enquiry-user">
                    <div className="user-avatar">
                      {enquiry.user?.name?.charAt(0).toUpperCase()}
                    </div>
                    <div className="user-info">
                      <h4>{enquiry.user?.name}</h4>
                      <p>{enquiry.user?.email} â€¢ {enquiry.user?.phone}</p>
                    </div>
                  </div>
                  <div className="enquiry-meta">
                    {getStatusBadge(enquiry.status)}
                    <span className="enquiry-date">
                      {new Date(enquiry.createdAt).toLocaleDateString()} at{' '}
                      {new Date(enquiry.createdAt).toLocaleTimeString()}
                    </span>
                  </div>
                </div>

                <div className="enquiry-content">
                  <p>{enquiry.message}</p>
                </div>

                {enquiry.response && (
                  <div className="enquiry-response">
                    <strong>Your Response:</strong>
                    <p>{enquiry.response.message}</p>
                    <small>
                      Responded on {new Date(enquiry.response.respondedAt).toLocaleDateString()}
                    </small>
                  </div>
                )}

                <div className="enquiry-actions">
                  {enquiry.status === 'pending' && !respondingTo && (
                    <button
                      onClick={() => setRespondingTo(enquiry._id)}
                      className="btn-primary"
                    >
                      Respond
                    </button>
                  )}
                  
                  {enquiry.status === 'responded' && (
                    <button
                      onClick={() => handleCloseEnquiry(enquiry._id)}
                      className="btn-secondary"
                    >
                      Mark as Closed
                    </button>
                  )}

                  {enquiry.status === 'closed' && (
                    <button
                      onClick={() => setRespondingTo(enquiry._id)}
                      className="btn-primary"
                    >
                      Reopen & Respond
                    </button>
                  )}
                </div>

                {respondingTo === enquiry._id && (
                  <div className="response-form">
                    <textarea
                      value={responseText}
                      onChange={(e) => setResponseText(e.target.value)}
                      placeholder="Type your response here..."
                      rows="4"
                    />
                    <div className="response-actions">
                      <button
                        onClick={() => handleRespond(enquiry._id)}
                        disabled={!responseText.trim()}
                        className="btn-primary"
                      >
                        Send Response
                      </button>
                      <button
                        onClick={() => {
                          setRespondingTo(null);
                          setResponseText('');
                        }}
                        className="btn-secondary"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Enquiries;
