import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const authAPI = {
  login: (credentials) => api.post('/login', credentials),
  register: (userData) => api.post('/register', userData),
};

export const instituteAPI = {
  getAll: (filters) => api.get('/institutes', { params: filters }),
  getById: (id) => api.get(`/institutes/${id}`),
  update: (id, data) => api.put(`/institutes/${id}`, data),
};

export const reviewAPI = {
  create: (data) => api.post('/reviews', data),
};

export const enquiryAPI = {
  create: (data) => api.post('/enquiries', data),
};

export const adminAPI = {
  getPendingInstitutes: () => api.get('/admin/pending-institutes'),
  approveInstitute: (id) => api.put(`/admin/approve-institute/${id}`),
  getAnalytics: () => api.get('/admin/analytics'),
};

export default api;
