import React, { useState, useEffect } from 'react';
import { instituteAPI } from '../../services/api';
import ImageUpload from '../Common/ImageUpload';
import './InstituteDashboard.css';

const ProfileManagement = ({ institute }) => {
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    affiliation: '',
    address: '',
    city: '',
    state: '',
    phone: '',
    email: '',
    website: '',
    description: '',
    facilities: [],
    logo: '',
    banner: '',
    images: []
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    if (institute) {
      setFormData({
        name: institute.name || '',
        category: institute.category || '',
        affiliation: institute.affiliation || '',
        address: institute.address || '',
        city: institute.city || '',
        state: institute.state || '',
        phone: institute.phone || '',
        email: institute.email || '',
        website: institute.website || '',
        description: institute.description || '',
        facilities: institute.facilities || [],
        logo: institute.logo || '',
        banner: institute.banner || '',
        images: institute.images || []
      });
    }
  }, [institute]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFacilityChange = (e) => {
    const facilities = e.target.value.split(',').map(f => f.trim()).filter(f => f);
    setFormData(prev => ({
      ...prev,
      facilities
    }));
  };

  const handleImagesChange = (images) => {
    setFormData(prev => ({
      ...prev,
      ...images
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await instituteAPI.updateProfile(institute._id, formData);
      setMessage('Profile updated successfully!');
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage('Error updating profile. Please try again.');
      console.error('Error updating profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (files) => {
    setUploading(true);
    try {
      const formData = new FormData();
      
      if (files.logoFile) {
        formData.append('logo', files.logoFile);
      }
      if (files.bannerFile) {
        formData.append('banner', files.bannerFile);
      }
      if (files.galleryFiles) {
        files.galleryFiles.forEach(file => {
          formData.append('gallery', file);
        });
      }

      const response = await fetch(`http://localhost:5000/api/upload/institute/${institute._id}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: formData
      });

      const result = await response.json();
      
      if (response.ok) {
        setFormData(prev => ({
          ...prev,
          logo: result.institute.logo || prev.logo,
          banner: result.institute.banner || prev.banner,
          images: result.institute.images || prev.images
        }));
        setMessage('Images uploaded successfully!');
      } else {
        setMessage('Error uploading images');
      }
    } catch (error) {
      console.error('Upload error:', error);
      setMessage('Error uploading images');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="profile-management">
      <div className="page-header">
        <h1>Institute Profile Management</h1>
        <p>Update your institute information and upload images</p>
      </div>

      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      {/* Image Upload Section */}
      <ImageUpload
        onImagesChange={handleImagesChange}
        existingLogo={formData.logo}
        existingBanner={formData.banner}
        existingGallery={formData.images}
      />

      <form onSubmit={handleSubmit} className="profile-form">
        <div className="form-section">
          <h3>Basic Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Institute Name *</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Category *</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select Category</option>
                <option value="School">School</option>
                <option value="College">College</option>
                <option value="University">University</option>
                <option value="Coaching Center">Coaching Center</option>
                <option value="Preschool">Preschool</option>
              </select>
            </div>

            <div className="form-group">
              <label>Affiliation *</label>
              <input
                type="text"
                name="affiliation"
                value={formData.affiliation}
                onChange={handleChange}
                required
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Contact Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Address *</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>City *</label>
              <input
                type="text"
                name="city"
                value={formData.city}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>State *</label>
              <input
                type="text"
                name="state"
                value={formData.state}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Phone *</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Email *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Website</label>
              <input
                type="url"
                name="website"
                value={formData.website}
                onChange={handleChange}
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Additional Information</h3>
          <div className="form-group">
            <label>Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="6"
              required
            />
          </div>

          <div className="form-group">
            <label>Facilities (comma separated)</label>
            <input
              type="text"
              value={formData.facilities.join(', ')}
              onChange={handleFacilityChange}
              placeholder="Library, Sports, Lab, Hostel, etc."
            />
            <small>Separate multiple facilities with commas</small>
          </div>
        </div>

        <button type="submit" disabled={loading} className="btn-primary">
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
      </form>
    </div>
  );
};

export default ProfileManagement;
