import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { instituteAPI, enquiryAPI, reviewAPI } from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const { user } = useAuth();
  const { addAlert, recentActivities } = useApp();
  const [stats, setStats] = useState({
    totalEnquiries: 0,
    pendingEnquiries: 0,
    totalReviews: 0,
    averageRating: 0
  });
  const [recentEnquiries, setRecentEnquiries] = useState([]);
  const [recentReviews, setRecentReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      const [instituteRes, enquiriesRes, reviewsRes] = await Promise.all([
        instituteAPI.getById(user.instituteId),
        enquiryAPI.getByInstitute(user.instituteId),
        reviewAPI.getByInstitute(user.instituteId)
      ]);

      const institute = instituteRes.data;
      const enquiries = enquiriesRes.data;
      const reviews = reviewsRes.data;

      // Calculate stats
      const totalEnquiries = enquiries.length;
      const pendingEnquiries = enquiries.filter(e => e.status === 'pending').length;
      const totalReviews = reviews.length;
      const averageRating = reviews.length > 0 
        ? (reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length).toFixed(1)
        : 0;

      setStats({
        totalEnquiries,
        pendingEnquiries,
        totalReviews,
        averageRating
      });

      // Get recent data
      setRecentEnquiries(enquiries.slice(0, 5));
      setRecentReviews(reviews.slice(0, 5));

    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load dashboard data'
      });
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <InstituteSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading dashboard...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <InstituteSidebar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Institute Dashboard</h1>
          <p>Welcome back! Here's what's happening with your institute.</p>
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon primary">
              <i className="fas fa-envelope"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.totalEnquiries}</h3>
              <p>Total Enquiries</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon warning">
              <i className="fas fa-clock"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.pendingEnquiries}</h3>
              <p>Pending Enquiries</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-card">
              <div className="stat-icon success">
                <i className="fas fa-star"></i>
              </div>
              <div className="stat-info">
                <h3>{stats.averageRating}</h3>
                <p>Average Rating</p>
              </div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-card">
              <div className="stat-info">
                <h3>{stats.totalReviews}</h3>
                <p>Total Reviews</p>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Activities */}
        <div className="dashboard-grid">
          {/* Recent Enquiries */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>Recent Enquiries</h3>
              <Link to="/institute/enquiries" className="btn btn-sm btn-primary">
                View All
              </Link>
            </div>
            <div className="card-content">
              {recentEnquiries.length > 0 ? (
                <div className="activity-list">
                  {recentEnquiries.map(enquiry => (
                    <div key={enquiry._id} className="activity-item">
                      <div className="activity-info">
                        <h4>{enquiry.name}</h4>
                        <p>{enquiry.message}</p>
                        <span className="activity-time">
                          {new Date(enquiry.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                      <div className={`status-badge ${enquiry.status}`}>
                        {enquiry.status}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-data">No enquiries yet</p>
              )}
            </div>
          </div>

          {/* Recent Reviews */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>Recent Reviews</h3>
              <Link to="/institute/reviews" className="btn btn-sm btn-primary">
                View All
              </Link>
            </div>
            <div className="card-content">
              {recentReviews.length > 0 ? (
                <div className="activity-list">
                  {recentReviews.map(review => (
                    <div key={review._id} className="activity-item">
                      <div className="activity-info">
                        <div className="review-header">
                          <h4>{review.user?.name || 'Anonymous'}</h4>
                          <div className="rating-stars">
                            {'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}
                          </div>
                        </div>
                        <p>{review.reviewText}</p>
                        <span className="activity-time">
                          {new Date(review.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-data">No reviews yet</p>
              )}
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="dashboard-card">
          <div className="card-header">
            <h3>Quick Actions</h3>
          </div>
          <div className="card-content">
            <div className="quick-actions">
              <Link to="/institute/profile" className="action-btn">
                <i className="fas fa-edit"></i>
                <span>Update Profile</span>
              </Link>
              <Link to="/institute/courses" className="action-btn">
                <i className="fas fa-book"></i>
                <span>Manage Courses</span>
              </Link>
              <Link to="/institute/facilities" className="action-btn">
                <i className="fas fa-dumbbell"></i>
                <span>Manage Facilities</span>
              </Link>
              <Link to="/institute/enquiries" className="action-btn">
                <i className="fas fa-envelope"></i>
                <span>View Enquiries</span>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InstituteDashboard;
