import React, { useState } from 'react';
import { instituteAPI } from '../../services/api';

const ProfileManagement = ({ institute, onUpdate }) => {
  const [formData, setFormData] = useState({
    name: institute?.name || '',
    category: institute?.category || 'school',
    affiliation: institute?.affiliation || '',
    address: {
      street: institute?.address?.street || '',
      city: institute?.address?.city || '',
      state: institute?.address?.state || '',
      pincode: institute?.address?.pincode || ''
    },
    contact: {
      phone: institute?.contact?.phone || '',
      email: institute?.contact?.email || '',
      website: institute?.contact?.website || ''
    },
    description: institute?.description || '',
    establishedYear: institute?.establishedYear || '',
    feeRange: {
      min: institute?.feeRange?.min || '',
      max: institute?.feeRange?.max || ''
    }
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await instituteAPI.update(institute._id, formData);
      setMessage('Profile updated successfully!');
      onUpdate();
    } catch (error) {
      setMessage('Error updating profile: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-management">
      <div className="section-header">
        <h2>Institute Profile Management</h2>
        <p>Update your institute information and settings</p>
      </div>

      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="profile-form">
        <div className="form-section">
          <h3>Basic Information</h3>
          <div className="form-row">
            <div className="form-group">
              <label>Institute Name *</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Category *</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="school">School</option>
                <option value="college">College</option>
                <option value="coaching">Coaching Center</option>
                <option value="preschool">Preschool</option>
                <option value="university">University</option>
              </select>
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Affiliation *</label>
              <input
                type="text"
                name="affiliation"
                value={formData.affiliation}
                onChange={handleChange}
                required
                placeholder="e.g., CBSE, ICSE, State Board"
              />
            </div>
            <div className="form-group">
              <label>Established Year</label>
              <input
                type="number"
                name="establishedYear"
                value={formData.establishedYear}
                onChange={handleChange}
                placeholder="e.g., 1990"
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Contact Information</h3>
          <div className="form-row">
            <div className="form-group">
              <label>Phone</label>
              <input
                type="tel"
                name="contact.phone"
                value={formData.contact.phone}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="contact.email"
                value={formData.contact.email}
                onChange={handleChange}
              />
            </div>
          </div>
          <div className="form-group">
            <label>Website</label>
            <input
              type="url"
              name="contact.website"
              value={formData.contact.website}
              onChange={handleChange}
              placeholder="https://example.com"
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Address</h3>
          <div className="form-group">
            <label>Street Address</label>
            <input
              type="text"
              name="address.street"
              value={formData.address.street}
              onChange={handleChange}
            />
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>City *</label>
              <input
                type="text"
                name="address.city"
                value={formData.address.city}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>State *</label>
              <input
                type="text"
                name="address.state"
                value={formData.address.state}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Pincode</label>
              <input
                type="text"
                name="address.pincode"
                value={formData.address.pincode}
                onChange={handleChange}
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Fee Information</h3>
          <div className="form-row">
            <div className="form-group">
              <label>Minimum Fee (₹)</label>
              <input
                type="number"
                name="feeRange.min"
                value={formData.feeRange.min}
                onChange={handleChange}
                placeholder="0"
              />
            </div>
            <div className="form-group">
              <label>Maximum Fee (₹)</label>
              <input
                type="number"
                name="feeRange.max"
                value={formData.feeRange.max}
                onChange={handleChange}
                placeholder="0"
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Description</h3>
          <div className="form-group">
            <label>Institute Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              rows="6"
              placeholder="Describe your institute, facilities, achievements, etc."
            />
          </div>
        </div>

        <button type="submit" className="submit-button" disabled={loading}>
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
      </form>
    </div>
  );
};

export default ProfileManagement;
