import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Handle responses and errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const instituteAPI = {
  getAll: (params) => api.get('/institutes', { params }),
  getById: (id) => api.get(`/institutes/${id}`),
  updateProfile: (data) => api.put('/institutes/profile', data),
  addCourse: (data) => api.post('/institutes/courses', data),
  updateCourse: (courseId, data) => api.put(`/institutes/courses/${courseId}`, data),
  deleteCourse: (courseId) => api.delete(`/institutes/courses/${courseId}`),
  addFacility: (data) => api.post('/institutes/facilities', data),
  deleteFacility: (facilityId) => api.delete(`/institutes/facilities/${facilityId}`),
  getDashboardData: () => api.get('/institutes/dashboard/data'),
};

export const reviewAPI = {
  getByInstitute: (instituteId) => api.get(`/reviews/institute/${instituteId}`),
  create: (data) => api.post('/reviews', data),
  getMyInstituteReviews: () => api.get('/reviews/my-institute'),
};

export const enquiryAPI = {
  create: (data) => api.post('/enquiries', data),
  getMyInstituteEnquiries: () => api.get('/enquiries/my-institute'),
  respond: (id, data) => api.put(`/enquiries/${id}/respond`, data),
};

export const adminAPI = {
  getDashboard: () => api.get('/admin/dashboard'),
  getInstitutes: (params) => api.get('/admin/institutes', { params }),
  verifyInstitute: (id, verified) => api.put(`/admin/institutes/${id}/verify`, { verified }),
  updateInstituteStatus: (id, isActive) => api.put(`/admin/institutes/${id}/status`, { isActive }),
  getUsers: (params) => api.get('/admin/users', { params }),
  updateUserStatus: (id, isActive) => api.put(`/admin/users/${id}/status`, { isActive }),
  getReviews: (params) => api.get('/admin/reviews', { params }),
  approveReview: (id, isApproved) => api.put(`/admin/reviews/${id}/approve`, { isApproved }),
};

export default api;
