import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import { facilityAPI } from '../../services/api'; // Change from facilitiesAPI to facilityAPI

const FacilitiesManagement = () => {
  const [facilities, setFacilities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useContext(AuthContext);

  const fetchFacilities = async () => {
    try {
      setLoading(true);
      setError('');
      
      if (!user || !user._id) {
        setError('Please login to view facilities');
        setLoading(false);
        return;
      }

      console.log('Fetching facilities for user:', user._id);
      
      // Use facilityAPI instead of facilitiesAPI
      const response = await facilityAPI.getByInstitute(user._id);
      if (response.data.success) {
        setFacilities(response.data.data || []);
      } else {
        throw new Error(response.data.message || 'Failed to fetch facilities');
      }
      
    } catch (error) {
      console.error('Error fetching facilities:', error);
      setError(error.message || 'Failed to load facilities');
    } finally {
      setLoading(false);
    }
  };

  const handleAddFacility = async (facilityData) => {
    try {
      setError('');
      
      if (!user || !user._id) {
        setError('Please login to add facilities');
        return;
      }

      // Use facilityAPI instead of facilitiesAPI
      const response = await facilityAPI.create(user._id, facilityData);
      if (response.data.success) {
        await fetchFacilities(); // Refresh the list
        return { success: true, message: 'Facility added successfully' };
      } else {
        throw new Error(response.data.message || 'Failed to add facility');
      }
    } catch (error) {
      console.error('Error adding facility:', error);
      setError(error.message || 'Failed to add facility');
      return { success: false, message: error.message };
    }
  };

  const handleUpdateFacility = async (facilityId, facilityData) => {
    try {
      setError('');
      
      // Use facilityAPI instead of facilitiesAPI
      const response = await facilityAPI.update(facilityId, facilityData);
      if (response.data.success) {
        await fetchFacilities(); // Refresh the list
        return { success: true, message: 'Facility updated successfully' };
      } else {
        throw new Error(response.data.message || 'Failed to update facility');
      }
    } catch (error) {
      console.error('Error updating facility:', error);
      setError(error.message || 'Failed to update facility');
      return { success: false, message: error.message };
    }
  };

  const handleDeleteFacility = async (facilityId) => {
    try {
      setError('');
      
      // Use facilityAPI instead of facilitiesAPI
      const response = await facilityAPI.delete(facilityId);
      if (response.data.success) {
        await fetchFacilities(); // Refresh the list
        return { success: true, message: 'Facility deleted successfully' };
      } else {
        throw new Error(response.data.message || 'Failed to delete facility');
      }
    } catch (error) {
      console.error('Error deleting facility:', error);
      setError(error.message || 'Failed to delete facility');
      return { success: false, message: error.message };
    }
  };

  useEffect(() => {
    if (user && user._id) {
      fetchFacilities();
    } else {
      setLoading(false);
    }
  }, [user]);

  if (loading) return <div className="loading">Loading facilities...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="facilities-management">
      <h2>Facilities Management</h2>
      
      {/* Add your facilities management UI here */}
      {facilities.length === 0 ? (
        <p>No facilities found. Add some facilities to get started.</p>
      ) : (
        <div className="facilities-list">
          {facilities.map(facility => (
            <div key={facility._id} className="facility-card">
              <h3>{facility.name}</h3>
              <p>{facility.description}</p>
              {/* Add edit/delete buttons as needed */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FacilitiesManagement;
