import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const InstituteSidebar = ({ institute }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const { logout } = useAuth();

  const menuItems = [
    { path: '/institute-dashboard', icon: 'üìä', label: 'Dashboard' },
    { path: '/institute-dashboard/profile', icon: 'üè´', label: 'Profile' },
    { path: '/institute-dashboard/courses', icon: 'üìö', label: 'Courses' },
    { path: '/institute-dashboard/facilities', icon: '‚öôÔ∏è', label: 'Facilities' },
    { path: '/institute-dashboard/enquiries', icon: 'üìß', label: 'Enquiries' },
    { path: '/institute-dashboard/reviews', icon: '‚≠ê', label: 'Reviews' },
  ];

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <div className="institute-logo">
          {institute.logo ? (
            <img src={institute.logo} alt={institute.name} />
          ) : (
            <div className="logo-placeholder">üè´</div>
          )}
        </div>
        <h3>{institute.name}</h3>
        <p className="institute-category">{institute.category}</p>
        <p className="institute-status verified">‚úì Verified</p>
      </div>

      <nav className="sidebar-nav">
        <ul className="sidebar-menu">
          {menuItems.map((item) => (
            <li key={item.path} className="sidebar-item">
              <Link
                to={item.path}
                className={`sidebar-link ${
                  location.pathname === item.path ? 'active' : ''
                }`}
              >
                <span className="sidebar-icon">{item.icon}</span>
                <span className="sidebar-label">{item.label}</span>
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      <div className="sidebar-footer">
        <button onClick={handleLogout} className="btn btn-danger btn-block">
          üö™ Logout
        </button>
      </div>
    </div>
  );
};

export default InstituteSidebar;
