import React, { useState } from 'react';
import { instituteAPI } from '../../services/api';

const FacilitiesManagement = ({ institute, onUpdate }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingFacility, setEditingFacility] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const commonFacilities = [
    'Library', 'Sports Complex', 'Computer Lab', 'Science Lab', 
    'Auditorium', 'Cafeteria', 'Hostel', 'Transport', 'WiFi', 
    'Medical Room', 'Playground', 'Swimming Pool'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      if (editingFacility) {
        // Update facility logic
        console.log('Update facility:', editingFacility);
      } else {
        await instituteAPI.addFacility(formData);
      }

      onUpdate();
      resetForm();
    } catch (error) {
      console.error('Error saving facility:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: ''
    });
    setEditingFacility(null);
    setShowForm(false);
  };

  const handleEdit = (facility) => {
    setEditingFacility(facility);
    setFormData({
      name: facility.name,
      description: facility.description
    });
    setShowForm(true);
  };

  const handleDelete = async (facilityIndex) => {
    if (window.confirm('Are you sure you want to delete this facility?')) {
      try {
        // Implement delete facility logic
        console.log('Delete facility at index:', facilityIndex);
        onUpdate();
      } catch (error) {
        console.error('Error deleting facility:', error);
      }
    }
  };

  const addCommonFacility = (facilityName) => {
    setFormData({
      name: facilityName,
      description: `${facilityName} facility available`
    });
    setShowForm(true);
  };

  return (
    <div className="facilities-management">
      <div className="section-header">
        <h2>Facilities Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(true)}
        >
          + Add New Facility
        </button>
      </div>

      {showForm && (
        <div className="form-modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingFacility ? 'Edit Facility' : 'Add New Facility'}</h3>
              <button onClick={resetForm} className="close-btn">√ó</button>
            </div>
            
            <form onSubmit={handleSubmit} className="modal-form">
              <div className="form-group">
                <label>Facility Name *</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  rows="3"
                  required
                />
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={resetForm}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Saving...' : editingFacility ? 'Update Facility' : 'Add Facility'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="quick-add-section">
        <h4>Quick Add Common Facilities</h4>
        <div className="common-facilities">
          {commonFacilities.map(facility => (
            <button
              key={facility}
              className="facility-chip"
              onClick={() => addCommonFacility(facility)}
            >
              {facility}
            </button>
          ))}
        </div>
      </div>

      <div className="facilities-list">
        <h3>Your Facilities ({institute?.facilities?.length || 0})</h3>
        
        {institute?.facilities?.length > 0 ? (
          <div className="facilities-grid">
            {institute.facilities.map((facility, index) => (
              <div key={index} className="facility-card">
                <div className="facility-icon">
                  {facility.name.includes('Library') && 'üìö'}
                  {facility.name.includes('Sports') && '‚öΩ'}
                  {facility.name.includes('Lab') && 'üî¨'}
                  {facility.name.includes('Hostel') && 'üè†'}
                  {facility.name.includes('Transport') && 'üöå'}
                  {!facility.name.includes('Library') && 
                   !facility.name.includes('Sports') &&
                   !facility.name.includes('Lab') &&
                   !facility.name.includes('Hostel') &&
                   !facility.name.includes('Transport') && '‚ö°'}
                </div>
                
                <div className="facility-content">
                  <h4>{facility.name}</h4>
                  <p>{facility.description}</p>
                </div>
                
                <div className="facility-actions">
                  <button 
                    className="btn btn-outline btn-sm"
                    onClick={() => handleEdit(facility)}
                  >
                    Edit
                  </button>
                  <button 
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(index)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">‚ö°</div>
            <h4>No Facilities Added</h4>
            <p>Add facilities to showcase what your institute offers.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default FacilitiesManagement;
