import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI, reviewAPI } from '../../services/api';
import './UserPanel.css';

const UserDashboard = () => {
  const { user } = useAuth();
  const [recentInstitutes, setRecentInstitutes] = useState([]);
  const [myReviews, setMyReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [institutesResponse] = await Promise.all([
        instituteAPI.getAll()
      ]);
      
      // Get first 4 institutes for recent view
      setRecentInstitutes(institutesResponse.data.slice(0, 4));
      
      // Mock user reviews (in real app, fetch user-specific reviews)
      const mockReviews = [
        {
          _id: '1',
          instituteName: 'Delhi Public School',
          rating: 5,
          reviewText: 'Excellent school with great facilities and faculty.',
          createdAt: new Date('2024-01-10').toISOString()
        },
        {
          _id: '2',
          instituteName: 'St. Xavier College',
          rating: 4,
          reviewText: 'Good college with quality education.',
          createdAt: new Date('2024-01-08').toISOString()
        }
      ];
      setMyReviews(mockReviews);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric'
    });
  };

  const renderStars = (rating) => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map(star => (
          <span
            key={star}
            className={star <= rating ? 'star filled' : 'star'}
          >
            ‚òÖ
          </span>
        ))}
      </div>
    );
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="user-dashboard">
      <div className="dashboard-header">
        <h1>Welcome back, {user.name}!</h1>
        <p>Find the best educational institutes and share your experiences</p>
      </div>

      <div className="dashboard-stats">
        <div className="stat-card">
          <div className="stat-icon">üè´</div>
          <div className="stat-info">
            <h3>{recentInstitutes.length}+</h3>
            <p>Institutes Available</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <h3>{myReviews.length}</h3>
            <p>Your Reviews</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üìù</div>
          <div className="stat-info">
            <h3>0</h3>
            <p>Enquiries Sent</p>
          </div>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="content-section">
          <div className="section-header">
            <h2>Recently Added Institutes</h2>
            <a href="/institutes" className="view-all">View All</a>
          </div>
          <div className="institutes-grid">
            {recentInstitutes.map(institute => (
              <div key={institute._id} className="institute-card">
                <div className="institute-image">
                  {institute.images && institute.images.length > 0 ? (
                    <img src={institute.images[0]} alt={institute.name} />
                  ) : (
                    <div className="no-image">üè´</div>
                  )}
                </div>
                <div className="institute-info">
                  <h3>{institute.name}</h3>
                  <p className="institute-location">
                    üìç {institute.city}, {institute.state}
                  </p>
                  <p className="institute-category">
                    üè∑Ô∏è {institute.category.charAt(0).toUpperCase() + institute.category.slice(1)}
                  </p>
                  <div className="institute-meta">
                    <span className="affiliation">{institute.affiliation}</span>
                  </div>
                </div>
                <div className="institute-actions">
                  <a href={`/institute/${institute._id}`} className="btn-primary">
                    View Details
                  </a>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="content-section">
          <div className="section-header">
            <h2>Your Recent Reviews</h2>
          </div>
          {myReviews.length === 0 ? (
            <div className="empty-state">
              <p>You haven't reviewed any institutes yet.</p>
              <a href="/institutes" className="btn-primary">
                Browse Institutes
              </a>
            </div>
          ) : (
            <div className="reviews-list">
              {myReviews.map(review => (
                <div key={review._id} className="review-item">
                  <div className="review-header">
                    <h4>{review.instituteName}</h4>
                    <div className="review-meta">
                      {renderStars(review.rating)}
                      <span className="review-date">{formatDate(review.createdAt)}</span>
                    </div>
                  </div>
                  <p className="review-text">{review.reviewText}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
