import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { courseAPI } from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import './InstituteDashboard.css';

const CourseManagement = () => {
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    fees: '',
    level: '',
    eligibility: '',
    seats: ''
  });
  const [image, setImage] = useState(null);

  useEffect(() => {
    fetchCourses();
  }, [user]);

  const fetchCourses = async () => {
    try {
      const response = await courseAPI.getByInstitute(user.instituteId);
      setCourses(response.data);
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load courses'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const courseData = {
        ...formData,
        institute: user.instituteId
      };

      let response;
      if (editingCourse) {
        response = await courseAPI.update(editingCourse._id, courseData);
        addAlert({
          type: 'success',
          message: 'Course updated successfully'
        });
      } else {
        response = await courseAPI.create(courseData);
        addAlert({
          type: 'success',
          message: 'Course created successfully'
        });
      }

      // Upload image if provided
      if (image) {
        const formData = new FormData();
        formData.append('image', image);
        await courseAPI.uploadImage(response.data._id, formData);
      }

      resetForm();
      fetchCourses();
    } catch (error) {
      addAlert({
        type: 'error',
        message: `Failed to ${editingCourse ? 'update' : 'create'} course`
      });
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      duration: '',
      fees: '',
      level: '',
      eligibility: '',
      seats: ''
    });
    setImage(null);
    setEditingCourse(null);
    setShowForm(false);
  };

  const handleEdit = (course) => {
    setFormData({
      title: course.title,
      description: course.description,
      duration: course.duration,
      fees: course.fees,
      level: course.level,
      eligibility: course.eligibility,
      seats: course.seats
    });
    setEditingCourse(course);
    setShowForm(true);
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        await courseAPI.delete(courseId);
        addAlert({
          type: 'success',
          message: 'Course deleted successfully'
        });
        fetchCourses();
      } catch (error) {
        addAlert({
          type: 'error',
          message: 'Failed to delete course'
        });
      }
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <InstituteSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading courses...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <InstituteSidebar />
      <div className="dashboard-content">
        <div className="page-header">
          <h1>Course Management</h1>
          <button 
            onClick={() => setShowForm(true)} 
            className="btn btn-primary"
          >
            Add New Course
          </button>
        </div>

        {showForm && (
          <div className="form-card">
            <div className="card-header">
              <h3>{editingCourse ? 'Edit Course' : 'Add New Course'}</h3>
              <button onClick={resetForm} className="btn btn-outline">Cancel</button>
            </div>
            <form onSubmit={handleSubmit} className="course-form">
              <div className="form-grid">
                <div className="form-group">
                  <label className="form-label">Course Title</label>
                  <input
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Duration</label>
                  <input
                    type="text"
                    name="duration"
                    value={formData.duration}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="e.g., 4 years, 6 months"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Fees</label>
                  <input
                    type="number"
                    name="fees"
                    value={formData.fees}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Annual fees"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Level</label>
                  <select
                    name="level"
                    value={formData.level}
                    onChange={handleInputChange}
                    className="form-control"
                    required
                  >
                    <option value="">Select Level</option>
                    <option value="Certificate">Certificate</option>
                    <option value="Diploma">Diploma</option>
                    <option value="Undergraduate">Undergraduate</option>
                    <option value="Postgraduate">Postgraduate</option>
                    <option value="Doctorate">Doctorate</option>
                  </select>
                </div>

                <div className="form-group">
                  <label className="form-label">Available Seats</label>
                  <input
                    type="number"
                    name="seats"
                    value={formData.seats}
                    onChange={handleInputChange}
                    className="form-control"
                  />
                </div>

                <div className="form-group full-width">
                  <label className="form-label">Eligibility Criteria</label>
                  <input
                    type="text"
                    name="eligibility"
                    value={formData.eligibility}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="e.g., 12th pass with 60% marks"
                  />
                </div>

                <div className="form-group full-width">
                  <label className="form-label">Description</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    className="form-control"
                    rows="4"
                    placeholder="Detailed course description..."
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Course Image</label>
                  <div className="image-upload-box">
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleImageUpload}
                      className="image-input"
                    />
                    <div className="upload-placeholder">
                      <i className="fas fa-upload"></i>
                      <span>Upload Image</span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="form-actions">
                <button type="submit" className="btn btn-primary">
                  {editingCourse ? 'Update Course' : 'Create Course'}
                </button>
              </div>
            </form>
          </div>
        )}

        <div className="courses-grid">
          {courses.length > 0 ? (
            courses.map(course => (
              <div key={course._id} className="course-card">
                <div className="course-image">
                  {course.image ? (
                    <img src={course.image} alt={course.title} />
                  ) : (
                    <div className="image-placeholder">
                      <i className="fas fa-book"></i>
                    </div>
                  )}
                </div>
                <div className="course-content">
                  <h3>{course.title}</h3>
                  <p className="course-level">{course.level}</p>
                  <p className="course-duration">{course.duration}</p>
                  <p className="course-fees">â‚¹{course.fees}/year</p>
                  <p className="course-description">{course.description}</p>
                  
                  {course.eligibility && (
                    <div className="course-eligibility">
                      <strong>Eligibility:</strong> {course.eligibility}
                    </div>
                  )}

                  {course.seats && (
                    <div className="course-seats">
                      <strong>Seats:</strong> {course.seats}
                    </div>
                  )}
                </div>
                <div className="course-actions">
                  <button 
                    onClick={() => handleEdit(course)}
                    className="btn btn-sm btn-outline"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleDelete(course._id)}
                    className="btn btn-sm btn-danger"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))
          ) : (
            <div className="no-data-card">
              <i className="fas fa-book"></i>
              <h3>No Courses Added</h3>
              <p>Start by adding your first course to showcase your programs.</p>
              <button 
                onClick={() => setShowForm(true)} 
                className="btn btn-primary"
              >
                Add Your First Course
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CourseManagement;
