import api from './api';

export const courseService = {
  createCourse: async (courseData) => {
    try {
      // For demo, simulate API call
      console.log('Creating course:', courseData);
      return {
        message: 'Course created successfully',
        course: {
          ...courseData,
          _id: Date.now().toString(),
          createdAt: new Date().toISOString()
        }
      };
    } catch (error) {
      console.error('Error creating course:', error);
      throw new Error('Failed to create course');
    }
  },

  getCoursesByInstitute: async () => {
    try {
      const response = await api.get('/courses/institute');
      return response.data;
    } catch (error) {
      console.log('Using mock courses data');
      // Return mock courses
      return [
        {
          _id: '1',
          title: 'Science Stream - Class 11-12',
          description: 'Comprehensive science program with Physics, Chemistry, Mathematics, and Biology.',
          duration: '2 years',
          fees: 120000,
          category: 'science',
          facilities: ['Lab Access', 'Study Material', 'Test Series'],
          eligibility: 'Class 10 with 75% marks',
          image: null
        },
        {
          _id: '2',
          title: 'IIT-JEE Foundation Course',
          description: 'Specialized coaching for IIT-JEE entrance examination with expert faculty.',
          duration: '1 year',
          fees: 150000,
          category: 'engineering',
          facilities: ['Doubt Classes', 'Mock Tests', 'Online Portal'],
          eligibility: 'Class 11-12 Science students',
          image: null
        }
      ];
    }
  },

  updateCourse: async (courseId, courseData) => {
    try {
      // For demo, simulate API call
      console.log('Updating course:', courseId, courseData);
      return {
        message: 'Course updated successfully',
        course: {
          ...courseData,
          _id: courseId
        }
      };
    } catch (error) {
      console.error('Error updating course:', error);
      throw new Error('Failed to update course');
    }
  },

  deleteCourse: async (courseId) => {
    try {
      // For demo, simulate API call
      console.log('Deleting course:', courseId);
      return { message: 'Course deleted successfully' };
    } catch (error) {
      console.error('Error deleting course:', error);
      throw new Error('Failed to delete course');
    }
  },

  getInstituteCourses: async (instituteId) => {
    try {
      const response = await api.get(`/courses/institute/${instituteId}`);
      return response.data;
    } catch (error) {
      console.log('Using mock institute courses');
      return [];
    }
  }
};
