import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Common.css';

const Header = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <Link to="/" className="logo">
            EduList
          </Link>
          
          <nav className="nav-links">
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/institutes" className="nav-link">Institutes</Link>
            
            {user ? (
              <>
                {user.role === 'institute' && (
                  <Link to="/institute-dashboard" className="nav-link">Dashboard</Link>
                )}
                {user.role === 'admin' && (
                  <Link to="/admin" className="nav-link">Admin</Link>
                )}
                <span className="nav-link">Welcome, {user.name}</span>
                <button onClick={handleLogout} className="btn btn-outline" style={{color: 'white', borderColor: 'white'}}>
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="nav-link">Login</Link>
                <Link to="/register" className="btn btn-outline" style={{color: 'white', borderColor: 'white'}}>
                  Register
                </Link>
              </>
            )}
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
