import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { instituteAPI } from '../../services/api';
import './HomePage.css';

const HomePage = () => {
  const { user, userType } = useAuth();
  const { addAlert } = useApp();
  const navigate = useNavigate();
  const [featuredInstitutes, setFeaturedInstitutes] = useState([]);
  const [topRatedInstitutes, setTopRatedInstitutes] = useState([]);
  const [stats, setStats] = useState({
    totalInstitutes: 0,
    totalReviews: 0,
    totalUsers: 0
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [searchLocation, setSearchLocation] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchHomeData();
  }, []);

  const fetchHomeData = async () => {
    try {
      const response = await instituteAPI.getAll();
      const institutes = response.data || [];

      // Filter approved and active institutes only
      const approvedInstitutes = institutes.filter(inst => 
        inst.status === 'approved' && inst.isActive !== false
      );

      // Get featured institutes (most reviewed)
      const featured = [...approvedInstitutes]
        .sort((a, b) => (b.reviews?.length || 0) - (a.reviews?.length || 0))
        .slice(0, 6);

      // Get top rated institutes
      const topRated = [...approvedInstitutes]
        .filter(inst => inst.reviews && inst.reviews.length > 0)
        .sort((a, b) => {
          const ratingA = calculateAverageRating(a.reviews);
          const ratingB = calculateAverageRating(b.reviews);
          return ratingB - ratingA;
        })
        .slice(0, 6);

      setFeaturedInstitutes(featured);
      setTopRatedInstitutes(topRated);

      // Calculate stats from real data
      setStats({
        totalInstitutes: approvedInstitutes.length,
        totalReviews: approvedInstitutes.reduce((sum, inst) => sum + (inst.reviews?.length || 0), 0),
        totalUsers: 0 // This would come from a separate API call in real implementation
      });

    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load home page data'
      });
    } finally {
      setLoading(false);
    }
  };

  const calculateAverageRating = (reviews) => {
    if (!reviews || reviews.length === 0) return 0;
    const sum = reviews.reduce((total, review) => total + review.rating, 0);
    return (sum / reviews.length).toFixed(1);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim() || searchLocation.trim()) {
      const params = new URLSearchParams();
      if (searchTerm.trim()) params.append('search', searchTerm.trim());
      if (searchLocation.trim()) params.append('city', searchLocation.trim());
      navigate(`/institutes?${params.toString()}`);
    }
  };

  const getDashboardLink = () => {
    if (!userType) return '/login';
    return `/${userType}/dashboard`;
  };

  if (loading) {
    return (
      <div className="homepage">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  return (
    <div className="homepage">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-background">
          <div className="hero-overlay"></div>
        </div>
        <div className="hero-content">
          <div className="container">
            <div className="hero-text">
              <h1>Find Your Perfect Educational Institution</h1>
              <p className="hero-subtitle">
                Discover, compare, and choose from thousands of schools, colleges, 
                and coaching centers with authentic reviews and ratings.
              </p>
              
              {/* Search Bar */}
              <form onSubmit={handleSearch} className="search-bar">
                <div className="search-input-group">
                  <div className="input-with-icon">
                    <i className="fas fa-search"></i>
                    <input
                      type="text"
                      placeholder="Search for schools, colleges, courses..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="search-input"
                    />
                  </div>
                  <div className="input-with-icon">
                    <i className="fas fa-map-marker-alt"></i>
                    <input
                      type="text"
                      placeholder="City or location"
                      value={searchLocation}
                      onChange={(e) => setSearchLocation(e.target.value)}
                      className="search-input"
                    />
                  </div>
                  <button type="submit" className="btn btn-primary search-btn">
                    Search
                  </button>
                </div>
              </form>

              <div className="hero-stats">
                <div className="stat-item">
                  <span className="stat-number">{stats.totalInstitutes}+</span>
                  <span className="stat-label">Institutes</span>
                </div>
                <div className="stat-item">
                  <span className="stat-number">{stats.totalReviews}+</span>
                  <span className="stat-label">Reviews</span>
                </div>
                <div className="stat-item">
                  <span className="stat-number">{stats.totalUsers}+</span>
                  <span className="stat-label">Students & Parents</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <div className="container">
          <div className="section-header">
            <h2>Why Choose EduList?</h2>
            <p>We make finding the right educational institution simple and reliable</p>
          </div>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">
                <i className="fas fa-star"></i>
              </div>
              <h3>Authentic Reviews</h3>
              <p>Read genuine reviews from real students and parents to make informed decisions.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <i className="fas fa-search"></i>
              </div>
              <h3>Smart Search</h3>
              <p>Find institutes based on location, fees, facilities, and ratings that match your needs.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <i className="fas fa-chart-line"></i>
              </div>
              <h3>Compare & Analyze</h3>
              <p>Compare multiple institutes side by side with detailed information and statistics.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <i className="fas fa-comments"></i>
              </div>
              <h3>Direct Enquiry</h3>
              <p>Contact institutes directly through our platform for admissions and information.</p>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Institutes */}
      {featuredInstitutes.length > 0 && (
        <section className="institutes-section">
          <div className="container">
            <div className="section-header">
              <h2>Featured Institutes</h2>
              <p>Most popular educational institutions on our platform</p>
              <Link to="/institutes" className="view-all-link">
                View All Institutes <i className="fas fa-arrow-right"></i>
              </Link>
            </div>
            <div className="institutes-grid">
              {featuredInstitutes.map(institute => (
                <InstituteCard 
                  key={institute._id} 
                  institute={institute} 
                />
              ))}
            </div>
          </div>
        </section>
      )}

      {/* Top Rated Institutes */}
      {topRatedInstitutes.length > 0 && (
        <section className="institutes-section bg-light">
          <div className="container">
            <div className="section-header">
              <h2>Top Rated Institutes</h2>
              <p>Highest rated educational institutions based on student reviews</p>
              <Link to="/institutes" className="view-all-link">
                View All Institutes <i className="fas fa-arrow-right"></i>
              </Link>
            </div>
            <div className="institutes-grid">
              {topRatedInstitutes.map(institute => (
                <InstituteCard 
                  key={institute._id} 
                  institute={institute} 
                />
              ))}
            </div>
          </div>
        </section>
      )}

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <div className="cta-content">
            <h2>Ready to Find Your Perfect Institute?</h2>
            <p>Join thousands of students and parents who have found their ideal educational institution through EduList.</p>
            <div className="cta-buttons">
              {user ? (
                <Link to={getDashboardLink()} className="btn btn-primary btn-large">
                  Go to Dashboard
                </Link>
              ) : (
                <>
                  <Link to="/register" className="btn btn-primary btn-large">
                    Sign Up Free
                  </Link>
                  <Link to="/institutes" className="btn btn-outline-white btn-large">
                    Browse Institutes
                  </Link>
                </>
              )}
            </div>
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="container">
          <div className="section-header">
            <h2>Browse by Category</h2>
            <p>Find institutes based on your educational needs</p>
          </div>
          <div className="categories-grid">
            <CategoryCard 
              icon="fas fa-graduation-cap"
              title="Schools"
              description="Primary and secondary education institutions"
              count={featuredInstitutes.filter(inst => inst.affiliation?.includes('School')).length}
            />
            <CategoryCard 
              icon="fas fa-university"
              title="Colleges"
              description="Undergraduate and postgraduate institutions"
              count={featuredInstitutes.filter(inst => inst.affiliation?.includes('College')).length}
            />
            <CategoryCard 
              icon="fas fa-flask"
              title="Coaching Centers"
              description="Specialized training and coaching institutes"
              count={featuredInstitutes.filter(inst => inst.affiliation?.includes('Coaching')).length}
            />
            <CategoryCard 
              icon="fas fa-baby"
              title="Preschools"
              description="Early childhood education centers"
              count={featuredInstitutes.filter(inst => inst.affiliation?.includes('Preschool')).length}
            />
          </div>
        </div>
      </section>
    </div>
  );
};

// Institute Card Component for Homepage
const InstituteCard = ({ institute }) => {
  const calculateAverageRating = (reviews) => {
    if (!reviews || reviews.length === 0) return 0;
    const sum = reviews.reduce((total, review) => total + review.rating, 0);
    return (sum / reviews.length).toFixed(1);
  };

  const avgRating = calculateAverageRating(institute.reviews);
  const totalReviews = institute.reviews?.length || 0;

  return (
    <div className="institute-card-home">
      <Link to={`/institute/${institute._id}`} className="card-link">
        <div className="card-image">
          {institute.bannerImage ? (
            <img src={institute.bannerImage} alt={institute.name} />
          ) : (
            <div className="image-placeholder">
              <i className="fas fa-school"></i>
            </div>
          )}
          <div className="card-badge">
            {institute.affiliation}
          </div>
          <div className="rating-overlay">
            <span className="rating-value">{avgRating}</span>
            <div className="rating-stars">
              {'â˜…'.repeat(Math.floor(avgRating))}
            </div>
            <span className="review-count">({totalReviews})</span>
          </div>
        </div>

        <div className="card-content">
          <div className="institute-logo">
            {institute.logo ? (
              <img src={institute.logo} alt={`${institute.name} logo`} />
            ) : (
              <div className="logo-placeholder">
                {institute.name?.charAt(0)}
              </div>
            )}
          </div>
          <h3>{institute.name}</h3>
          <p className="location">
            <i className="fas fa-map-marker-alt"></i>
            {institute.city}, {institute.state}
          </p>
          <p className="description">
            {institute.description?.substring(0, 100)}...
          </p>
          
          <div className="institute-features">
            {institute.facilities?.slice(0, 2).map(facility => (
              <span key={facility._id} className="feature-tag">
                {facility.type}
              </span>
            ))}
            {institute.facilities && institute.facilities.length > 2 && (
              <span className="feature-tag">
                +{institute.facilities.length - 2} more
              </span>
            )}
          </div>
        </div>
      </Link>
    </div>
  );
};

// Category Card Component
const CategoryCard = ({ icon, title, description, count }) => (
  <div className="category-card">
    <div className="category-icon">
      <i className={icon}></i>
    </div>
    <h3>{title}</h3>
    <p>{description}</p>
    <div className="category-count">
      <span>{count}+ Institutes</span>
    </div>
    <Link to={`/institutes?category=${encodeURIComponent(title)}`} className="category-link">
      Explore <i className="fas fa-arrow-right"></i>
    </Link>
  </div>
);

export default HomePage;
