import React, { useState, useEffect } from 'react';
import { instituteAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const ProfileManagement = ({ institute }) => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    affiliation: '',
    address: '',
    city: '',
    state: '',
    phone: '',
    email: '',
    website: '',
    description: '',
    facilities: [],
    logo: null,
    banner: null,
    images: []
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [logoPreview, setLogoPreview] = useState('');
  const [bannerPreview, setBannerPreview] = useState('');
  const [imagePreviews, setImagePreviews] = useState([]);

  useEffect(() => {
    if (institute) {
      setFormData({
        name: institute.name || '',
        category: institute.category || '',
        affiliation: institute.affiliation || '',
        address: institute.address || '',
        city: institute.city || '',
        state: institute.state || '',
        phone: institute.phone || '',
        email: institute.email || '',
        website: institute.website || '',
        description: institute.description || '',
        facilities: institute.facilities || [],
        logo: null,
        banner: null,
        images: []
      });
      
      if (institute.logo) setLogoPreview(institute.logo);
      if (institute.banner) setBannerPreview(institute.banner);
      if (institute.images) setImagePreviews(institute.images);
    }
  }, [institute]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFacilityChange = (e) => {
    const facilities = e.target.value.split(',').map(f => f.trim()).filter(f => f);
    setFormData(prev => ({
      ...prev,
      facilities
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    
    if (files && files[0]) {
      const file = files[0];
      
      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        if (name === 'logo') {
          setLogoPreview(e.target.result);
        } else if (name === 'banner') {
          setBannerPreview(e.target.result);
        }
      };
      reader.readAsDataURL(file);
      
      setFormData(prev => ({
        ...prev,
        [name]: file
      }));
    }
  };

  const handleImagesChange = (e) => {
    const files = Array.from(e.target.files);
    
    if (files.length > 0) {
      const newPreviews = [];
      files.forEach(file => {
        const reader = new FileReader();
        reader.onload = (e) => {
          newPreviews.push(e.target.result);
          if (newPreviews.length === files.length) {
            setImagePreviews(prev => [...prev, ...newPreviews]);
          }
        };
        reader.readAsDataURL(file);
      });
      
      setFormData(prev => ({
        ...prev,
        images: [...prev.images, ...files]
      }));
    }
  };

  const removeImage = (index) => {
    setImagePreviews(prev => prev.filter((_, i) => i !== index));
    setFormData(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const submitData = new FormData();
      
      // Append text fields
      Object.keys(formData).forEach(key => {
        if (key !== 'logo' && key !== 'banner' && key !== 'images') {
          if (key === 'facilities') {
            submitData.append(key, JSON.stringify(formData[key]));
          } else {
            submitData.append(key, formData[key]);
          }
        }
      });

      // Append files
      if (formData.logo) {
        submitData.append('logo', formData.logo);
      }
      if (formData.banner) {
        submitData.append('banner', formData.banner);
      }
      formData.images.forEach(image => {
        submitData.append('images', image);
      });

      await instituteAPI.updateProfile(institute._id, submitData);
      setMessage('Profile updated successfully!');
      
      // Clear file inputs after successful upload
      setFormData(prev => ({
        ...prev,
        logo: null,
        banner: null,
        images: []
      }));
      
    } catch (error) {
      console.error('Error updating profile:', error);
      setMessage('Error updating profile. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-management">
      <div className="page-header">
        <h1>Institute Profile Management</h1>
        <p>Update your institute information, logo, and images</p>
      </div>

      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="profile-form">
        {/* Image Upload Section */}
        <div className="form-section">
          <h3>Institute Images</h3>
          
          <div className="image-upload-grid">
            <div className="image-upload-group">
              <label>Institute Logo</label>
              <div className="image-upload-container">
                {logoPreview ? (
                  <div className="image-preview">
                    <img src={logoPreview} alt="Logo preview" />
                    <button 
                      type="button" 
                      className="remove-image"
                      onClick={() => {
                        setLogoPreview('');
                        setFormData(prev => ({ ...prev, logo: null }));
                      }}
                    >
                      √ó
                    </button>
                  </div>
                ) : (
                  <div className="upload-placeholder">
                    <span>üì∑</span>
                    <p>Upload Logo</p>
                  </div>
                )}
                <input
                  type="file"
                  name="logo"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="file-input"
                />
              </div>
              <small>Recommended: 200x200px, PNG or JPG</small>
            </div>

            <div className="image-upload-group">
              <label>Banner Image</label>
              <div className="image-upload-container">
                {bannerPreview ? (
                  <div className="image-preview banner-preview">
                    <img src={bannerPreview} alt="Banner preview" />
                    <button 
                      type="button" 
                      className="remove-image"
                      onClick={() => {
                        setBannerPreview('');
                        setFormData(prev => ({ ...prev, banner: null }));
                      }}
                    >
                      √ó
                    </button>
                  </div>
                ) : (
                  <div className="upload-placeholder banner-placeholder">
                    <span>üèûÔ∏è</span>
                    <p>Upload Banner</p>
                  </div>
                )}
                <input
                  type="file"
                  name="banner"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="file-input"
                />
              </div>
              <small>Recommended: 1200x400px, PNG or JPG</small>
            </div>
          </div>

          <div className="image-upload-group">
            <label>Gallery Images</label>
            <div className="gallery-upload">
              <div className="gallery-previews">
                {imagePreviews.map((preview, index) => (
                  <div key={index} className="gallery-preview">
                    <img src={preview} alt={`Gallery ${index + 1}`} />
                    <button 
                      type="button" 
                      className="remove-image"
                      onClick={() => removeImage(index)}
                    >
                      √ó
                    </button>
                  </div>
                ))}
                <div className="upload-gallery-item">
                  <span>‚ûï</span>
                  <p>Add Images</p>
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={handleImagesChange}
                    className="file-input"
                  />
                </div>
              </div>
            </div>
            <small>Upload up to 6 images of your campus and facilities</small>
          </div>
        </div>

        <div className="form-section">
          <h3>Basic Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Institute Name *</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Category *</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select Category</option>
                <option value="School">School</option>
                <option value="College">College</option>
                <option value="University">University</option>
                <option value="Coaching Center">Coaching Center</option>
                <option value="Preschool">Preschool</option>
              </select>
            </div>

            <div className="form-group">
              <label>Affiliation *</label>
              <input
                type="text"
                name="affiliation"
                value={formData.affiliation}
                onChange={handleChange}
                required
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Contact Information</h3>
          <div className="form-grid">
            <div className="form-group">
              <label>Address *</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>City *</label>
              <input
                type="text"
                name="city"
                value={formData.city}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>State *</label>
              <input
                type="text"
                name="state"
                value={formData.state}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Phone *</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Email *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Website</label>
              <input
                type="url"
                name="website"
                value={formData.website}
                onChange={handleChange}
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>Additional Information</h3>
          <div className="form-group">
            <label>Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="6"
              required
            />
          </div>

          <div className="form-group">
            <label>Facilities (comma separated)</label>
            <input
              type="text"
              value={formData.facilities.join(', ')}
              onChange={handleFacilityChange}
              placeholder="Library, Sports, Lab, Hostel, etc."
            />
            <small>Separate multiple facilities with commas</small>
          </div>
        </div>

        <button type="submit" disabled={loading} className="btn-primary">
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
      </form>
    </div>
  );
};

export default ProfileManagement;
