import React, { useState } from 'react';
import { reviewAPI } from '../../services/api';

const ReviewForm = ({ institute, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    rating: 0,
    reviewText: ''
  });
  const [loading, setLoading] = useState(false);
  const [hoverRating, setHoverRating] = useState(0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (formData.rating === 0) {
      alert('Please select a rating');
      return;
    }

    if (!formData.reviewText.trim()) {
      alert('Please write a review');
      return;
    }

    setLoading(true);

    try {
      await reviewAPI.create({
        institute: institute._id,
        rating: formData.rating,
        reviewText: formData.reviewText
      });
      
      onSuccess();
    } catch (error) {
      alert('Error submitting review: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRatingClick = (rating) => {
    setFormData(prev => ({ ...prev, rating }));
  };

  const handleRatingHover = (rating) => {
    setHoverRating(rating);
  };

  const handleRatingLeave = () => {
    setHoverRating(0);
  };

  const renderStars = () => {
    return Array.from({ length: 5 }, (_, index) => {
      const ratingValue = index + 1;
      return (
        <span
          key={index}
          className={`star ${ratingValue <= (hoverRating || formData.rating) ? 'filled' : ''}`}
          onClick={() => handleRatingClick(ratingValue)}
          onMouseEnter={() => handleRatingHover(ratingValue)}
          onMouseLeave={handleRatingLeave}
          style={{ cursor: 'pointer', fontSize: '2rem' }}
        >
          {ratingValue <= (hoverRating || formData.rating) ? '⭐' : '☆'}
        </span>
      );
    });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Write a Review for {institute.name}</h3>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>

        <form onSubmit={handleSubmit} className="review-form">
          <div className="form-group">
            <label>Your Rating *</label>
            <div className="rating-input">
              {renderStars()}
              <div className="rating-text">
                {formData.rating > 0 ? `${formData.rating} stars` : 'Select rating'}
              </div>
            </div>
          </div>

          <div className="form-group">
            <label>Your Review *</label>
            <textarea
              value={formData.reviewText}
              onChange={(e) => setFormData(prev => ({ ...prev, reviewText: e.target.value }))}
              placeholder="Share your experience with this institute..."
              rows="6"
              required
            />
          </div>

          <div className="form-actions">
            <button type="button" className="btn btn-outline" onClick={onClose}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? 'Submitting...' : 'Submit Review'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ReviewForm;
