import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import toast from 'react-hot-toast';

const Enquiries = ({ institute }) => {
  const [enquiries, setEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [respondingTo, setRespondingTo] = useState(null);
  const [responseText, setResponseText] = useState('');

  useEffect(() => {
    fetchEnquiries();
  }, [institute._id]);

  const fetchEnquiries = async () => {
    try {
      const response = await api.get(`/enquiries/institute/${institute._id}`);
      setEnquiries(response.data);
    } catch (error) {
      console.error('Error fetching enquiries:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRespond = async (enquiryId) => {
    if (!responseText.trim()) {
      toast.error('Please enter a response');
      return;
    }

    try {
      await api.patch(`/enquiries/${enquiryId}/respond`, { 
        response: responseText 
      });
      
      toast.success('Response sent successfully!');
      setRespondingTo(null);
      setResponseText('');
      fetchEnquiries(); // Refresh enquiries
    } catch (error) {
      toast.error('Failed to send response');
      console.error('Error responding to enquiry:', error);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'pending', label: 'Pending' },
      responded: { class: 'responded', label: 'Responded' },
      closed: { class: 'closed', label: 'Closed' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading enquiries...</p>
      </div>
    );
  }

  return (
    <div className="enquiries-management">
      <div className="section-header">
        <h2>Enquiry Management</h2>
        <p>Manage and respond to student/parent enquiries</p>
      </div>

      <div className="enquiries-list">
        {enquiries.length === 0 ? (
          <div className="empty-state">
            <h3>No enquiries yet</h3>
            <p>Enquiries from users will appear here.</p>
          </div>
        ) : (
          enquiries.map((enquiry) => (
            <div key={enquiry._id} className="enquiry-card">
              <div className="enquiry-header">
                <div className="enquiry-user">
                  <strong>{enquiry.user.name}</strong>
                  <span className="user-email">{enquiry.user.email}</span>
                  {enquiry.user.phone && (
                    <span className="user-phone">{enquiry.user.phone}</span>
                  )}
                </div>
                <div className="enquiry-meta">
                  {getStatusBadge(enquiry.status)}
                  <span className="enquiry-date">
                    {new Date(enquiry.createdAt).toLocaleDateString()} at{' '}
                    {new Date(enquiry.createdAt).toLocaleTimeString()}
                  </span>
                </div>
              </div>

              <div className="enquiry-message">
                <strong>Message:</strong>
                <p>{enquiry.message}</p>
              </div>

              {enquiry.response && (
                <div className="enquiry-response">
                  <strong>Your Response:</strong>
                  <p>{enquiry.response.text}</p>
                  <small>
                    Responded on {new Date(enquiry.response.respondedAt).toLocaleDateString()}
                  </small>
                </div>
              )}

              {enquiry.status === 'pending' && (
                <div className="enquiry-actions">
                  {respondingTo === enquiry._id ? (
                    <div className="response-form">
                      <textarea
                        value={responseText}
                        onChange={(e) => setResponseText(e.target.value)}
                        placeholder="Type your response here..."
                        className="form-control"
                        rows="4"
                      />
                      <div className="response-actions">
                        <button 
                          onClick={() => handleRespond(enquiry._id)}
                          className="btn btn-success"
                        >
                          Send Response
                        </button>
                        <button 
                          onClick={() => {
                            setRespondingTo(null);
                            setResponseText('');
                          }}
                          className="btn btn-outline"
                        >
                          Cancel
                        </button>
                      </div>
                    </div>
                  ) : (
                    <button 
                      onClick={() => setRespondingTo(enquiry._id)}
                      className="btn btn-primary"
                    >
                      Respond to Enquiry
                    </button>
                  )}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Enquiries;
