import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteAPI } from '../../services/api';
import './UserPanel.css';

const InstituteList = () => {
  const [institutes, setInstitutes] = useState([]);
  const [filteredInstitutes, setFilteredInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    category: '',
    city: '',
    minFees: '',
    maxFees: '',
    facilities: [],
    minRating: ''
  });

  const facilitiesOptions = ['Library', 'Sports', 'Lab', 'Hostel', 'Cafeteria', 'Transport', 'WiFi'];

  useEffect(() => {
    fetchInstitutes();
  }, []);

  useEffect(() => {
    filterInstitutes();
  }, [institutes, filters]);

  const fetchInstitutes = async () => {
    try {
      const response = await instituteAPI.getAll();
      setInstitutes(response.data);
    } catch (error) {
      console.error('Error fetching institutes:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterInstitutes = () => {
    let filtered = institutes;

    if (filters.search) {
      filtered = filtered.filter(institute =>
        institute.name.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    if (filters.category) {
      filtered = filtered.filter(institute =>
        institute.category === filters.category
      );
    }

    if (filters.city) {
      filtered = filtered.filter(institute =>
        institute.address?.city?.toLowerCase().includes(filters.city.toLowerCase())
      );
    }

    if (filters.minFees) {
      filtered = filtered.filter(institute =>
        institute.courses?.some(course => course.fees >= parseInt(filters.minFees))
      );
    }

    if (filters.maxFees) {
      filtered = filtered.filter(institute =>
        institute.courses?.some(course => course.fees <= parseInt(filters.maxFees))
      );
    }

    if (filters.facilities.length > 0) {
      filtered = filtered.filter(institute =>
        filters.facilities.every(facility =>
          institute.facilities?.some(f => f.name === facility)
        )
      );
    }

    if (filters.minRating) {
      filtered = filtered.filter(institute =>
        institute.rating?.average >= parseFloat(filters.minRating)
      );
    }

    setFilteredInstitutes(filtered);
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleFacilityToggle = (facility) => {
    setFilters(prev => ({
      ...prev,
      facilities: prev.facilities.includes(facility)
        ? prev.facilities.filter(f => f !== facility)
        : [...prev.facilities, facility]
    }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      category: '',
      city: '',
      minFees: '',
      maxFees: '',
      facilities: [],
      minRating: ''
    });
  };

  if (loading) {
    return <div className="loading">Loading institutes...</div>;
  }

  return (
    <div className="institute-list-page">
      <div className="container">
        <div className="page-header">
          <h1>Find Educational Institutes</h1>
          <p>Discover the perfect institution for your educational journey</p>
        </div>

        <div className="content-with-filters">
          {/* Filters Sidebar */}
          <div className="filters-sidebar">
            <div className="filters-header">
              <h3>Filters</h3>
              <button onClick={clearFilters} className="btn btn-link">
                Clear All
              </button>
            </div>

            <div className="filter-group">
              <label>Search</label>
              <input
                type="text"
                placeholder="Search by name..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
              />
            </div>

            <div className="filter-group">
              <label>Category</label>
              <select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
              >
                <option value="">All Categories</option>
                <option value="school">School</option>
                <option value="college">College</option>
                <option value="coaching">Coaching Center</option>
                <option value="preschool">Preschool</option>
                <option value="university">University</option>
              </select>
            </div>

            <div className="filter-group">
              <label>City</label>
              <input
                type="text"
                placeholder="Enter city..."
                value={filters.city}
                onChange={(e) => handleFilterChange('city', e.target.value)}
              />
            </div>

            <div className="filter-group">
              <label>Fees Range</label>
              <div className="fees-range">
                <input
                  type="number"
                  placeholder="Min"
                  value={filters.minFees}
                  onChange={(e) => handleFilterChange('minFees', e.target.value)}
                />
                <span>to</span>
                <input
                  type="number"
                  placeholder="Max"
                  value={filters.maxFees}
                  onChange={(e) => handleFilterChange('maxFees', e.target.value)}
                />
              </div>
            </div>

            <div className="filter-group">
              <label>Minimum Rating</label>
              <select
                value={filters.minRating}
                onChange={(e) => handleFilterChange('minRating', e.target.value)}
              >
                <option value="">Any Rating</option>
                <option value="4.5">4.5+ Stars</option>
                <option value="4.0">4.0+ Stars</option>
                <option value="3.5">3.5+ Stars</option>
                <option value="3.0">3.0+ Stars</option>
              </select>
            </div>

            <div className="filter-group">
              <label>Facilities</label>
              <div className="facilities-list">
                {facilitiesOptions.map(facility => (
                  <label key={facility} className="facility-checkbox">
                    <input
                      type="checkbox"
                      checked={filters.facilities.includes(facility)}
                      onChange={() => handleFacilityToggle(facility)}
                    />
                    <span>{facility}</span>
                  </label>
                ))}
              </div>
            </div>
          </div>

          {/* Institutes Grid */}
          <div className="institutes-content">
            <div className="results-header">
              <h2>
                {filteredInstitutes.length} Institute{filteredInstitutes.length !== 1 ? 's' : ''} Found
              </h2>
              <div className="sort-options">
                <select>
                  <option>Sort by: Rating</option>
                  <option>Sort by: Name</option>
                  <option>Sort by: Fees</option>
                </select>
              </div>
            </div>

            <div className="institutes-grid">
              {filteredInstitutes.map(institute => (
                <div key={institute._id} className="institute-card">
                  <div className="card-image">
                    {institute.images?.banner ? (
                      <img 
                        src={`http://localhost:5000/uploads/${institute.images.banner}`} 
                        alt={institute.name}
                      />
                    ) : (
                      <div className="image-placeholder">
                        <span>üè´</span>
                      </div>
                    )}
                    <div className="card-rating">
                      ‚≠ê {institute.rating?.average?.toFixed(1) || 'N/A'}
                    </div>
                  </div>
                  
                  <div className="card-content">
                    <h3>{institute.name}</h3>
                    <p className="card-category">{institute.category}</p>
                    <p className="card-location">
                      üìç {institute.address?.city}, {institute.address?.state}
                    </p>
                    <p className="card-description">
                      {institute.description?.substring(0, 100)}...
                    </p>
                    
                    <div className="card-facilities">
                      {institute.facilities?.slice(0, 4).map((facility, index) => (
                        <span key={index} className="facility-tag">
                          {facility.name}
                        </span>
                      ))}
                      {institute.facilities?.length > 4 && (
                        <span className="facility-tag">
                          +{institute.facilities.length - 4} more
                        </span>
                      )}
                    </div>

                    <div className="card-courses">
                      <strong>Courses:</strong> {institute.courses?.length || 0} available
                    </div>
                  </div>
                  
                  <div className="card-actions">
                    <Link 
                      to={`/institute/${institute._id}`}
                      className="btn btn-primary"
                    >
                      View Details
                    </Link>
                  </div>
                </div>
              ))}
            </div>

            {filteredInstitutes.length === 0 && (
              <div className="empty-state">
                <div className="empty-icon">üîç</div>
                <h3>No institutes found</h3>
                <p>Try adjusting your filters to see more results.</p>
                <button onClick={clearFilters} className="btn btn-primary">
                  Clear Filters
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default InstituteList;
