import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const AdminSidebar = ({ onLogout }) => {
  const location = useLocation();

  const menuItems = [
    { path: '/admin', icon: 'üìä', label: 'Dashboard' },
    { path: '/admin/institutes', icon: 'üè´', label: 'Manage Institutes' },
    { path: '/admin/users', icon: 'üë•', label: 'Manage Users' },
    { path: '/admin/analytics', icon: 'üìà', label: 'Analytics' },
  ];

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Admin Panel</h3>
      </div>
      
      <nav className="sidebar-nav">
        {menuItems.map(item => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </Link>
        ))}
      </nav>

      <button onClick={onLogout} className="logout-btn btn btn-danger">
        üö™ Logout
      </button>
    </div>
  );
};

export default AdminSidebar;
