import React, { useState, useEffect } from 'react';
import { courseService } from '../../services/course';

const CourseManagement = () => {
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    fees: '',
    category: '',
    facilities: [],
    eligibility: '',
    syllabus: [],
    image: null
  });
  const [imagePreview, setImagePreview] = useState('');

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const data = await courseService.getCoursesByInstitute();
      setCourses(data);
    } catch (error) {
      console.error('Error fetching courses:', error);
      // Use mock data for demo
      setCourses([
        {
          _id: '1',
          title: 'Science Stream - Class 11-12',
          description: 'Comprehensive science program with Physics, Chemistry, Mathematics, and Biology.',
          duration: '2 years',
          fees: 120000,
          category: 'science',
          facilities: ['Lab Access', 'Study Material', 'Test Series'],
          eligibility: 'Class 10 with 75% marks',
          image: '/images/science-course.jpg'
        },
        {
          _id: '2',
          title: 'IIT-JEE Foundation Course',
          description: 'Specialized coaching for IIT-JEE entrance examination with expert faculty.',
          duration: '1 year',
          fees: 150000,
          category: 'engineering',
          facilities: ['Doubt Classes', 'Mock Tests', 'Online Portal'],
          eligibility: 'Class 11-12 Science students',
          image: '/images/engineering-course.jpg'
        }
      ]);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, image: file }));
      
      // Create preview URL
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const courseData = { ...formData };
      
      // For demo purposes, we'll simulate image upload
      if (formData.image) {
        // In real app, you would upload to server
        courseData.image = URL.createObjectURL(formData.image); // Local preview
      }

      if (editingCourse) {
        await courseService.updateCourse(editingCourse._id, courseData);
      } else {
        await courseService.createCourse(courseData);
      }
      
      setShowForm(false);
      setEditingCourse(null);
      setFormData({
        title: '',
        description: '',
        duration: '',
        fees: '',
        category: '',
        facilities: [],
        eligibility: '',
        syllabus: [],
        image: null
      });
      setImagePreview('');
      fetchCourses();
    } catch (error) {
      console.error('Error saving course:', error);
      alert('Error saving course. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setFormData({
      title: course.title,
      description: course.description,
      duration: course.duration,
      fees: course.fees,
      category: course.category,
      facilities: course.facilities || [],
      eligibility: course.eligibility || '',
      syllabus: course.syllabus || [],
      image: null
    });
    setImagePreview(course.image || '');
    setShowForm(true);
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        await courseService.deleteCourse(courseId);
        fetchCourses();
      } catch (error) {
        console.error('Error deleting course:', error);
        alert('Error deleting course. Please try again.');
      }
    }
  };

  const addFacility = () => {
    setFormData(prev => ({
      ...prev,
      facilities: [...prev.facilities, '']
    }));
  };

  const updateFacility = (index, value) => {
    setFormData(prev => ({
      ...prev,
      facilities: prev.facilities.map((fac, i) => i === index ? value : fac)
    }));
  };

  const removeFacility = (index) => {
    setFormData(prev => ({
      ...prev,
      facilities: prev.facilities.filter((_, i) => i !== index)
    }));
  };

  const addSyllabus = () => {
    setFormData(prev => ({
      ...prev,
      syllabus: [...prev.syllabus, '']
    }));
  };

  const updateSyllabus = (index, value) => {
    setFormData(prev => ({
      ...prev,
      syllabus: prev.syllabus.map((item, i) => i === index ? value : item)
    }));
  };

  const removeSyllabus = (index) => {
    setFormData(prev => ({
      ...prev,
      syllabus: prev.syllabus.filter((_, i) => i !== index)
    }));
  };

  return (
    <div className="course-management">
      <div className="page-header">
        <h2>Course Management</h2>
        <button 
          onClick={() => setShowForm(true)}
          className="btn btn-primary"
        >
          + Add New Course
        </button>
      </div>

      {/* Course Form Modal */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content large-modal">
            <div className="modal-header">
              <h3>{editingCourse ? 'Edit Course' : 'Add New Course'}</h3>
              <button 
                onClick={() => {
                  setShowForm(false);
                  setEditingCourse(null);
                  setFormData({
                    title: '',
                    description: '',
                    duration: '',
                    fees: '',
                    category: '',
                    facilities: [],
                    eligibility: '',
                    syllabus: [],
                    image: null
                  });
                  setImagePreview('');
                }}
                className="close-button"
              >
                Ã—
              </button>
            </div>

            <form onSubmit={handleSubmit} className="course-form">
              <div className="form-section">
                <h4>Course Image</h4>
                <div className="image-upload-section">
                  <div className="image-preview">
                    {imagePreview ? (
                      <img src={imagePreview} alt="Course preview" className="preview-image" />
                    ) : (
                      <div className="image-placeholder">
                        ðŸ“· Course Image
                      </div>
                    )}
                  </div>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageChange}
                    className="image-upload-input"
                  />
                  <small>Upload a course image (JPEG, PNG, max 5MB)</small>
                </div>
              </div>

              <div className="form-grid">
                <div className="form-group">
                  <label>Course Title *</label>
                  <input
                    type="text"
                    value={formData.title}
                    onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Category *</label>
                  <select
                    value={formData.category}
                    onChange={(e) => setFormData(prev => ({ ...prev, category: e.target.value }))}
                    required
                  >
                    <option value="">Select Category</option>
                    <option value="engineering">Engineering</option>
                    <option value="medical">Medical</option>
                    <option value="arts">Arts</option>
                    <option value="science">Science</option>
                    <option value="commerce">Commerce</option>
                    <option value="coaching">Coaching</option>
                    <option value="other">Other</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Duration *</label>
                  <input
                    type="text"
                    value={formData.duration}
                    onChange={(e) => setFormData(prev => ({ ...prev, duration: e.target.value }))}
                    placeholder="e.g., 4 years, 6 months"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Fees (â‚¹) *</label>
                  <input
                    type="number"
                    value={formData.fees}
                    onChange={(e) => setFormData(prev => ({ ...prev, fees: e.target.value }))}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  value={formData.description}
                  onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                  rows="3"
                  required
                />
              </div>

              <div className="form-group">
                <label>Eligibility Criteria</label>
                <input
                  type="text"
                  value={formData.eligibility}
                  onChange={(e) => setFormData(prev => ({ ...prev, eligibility: e.target.value }))}
                  placeholder="e.g., 12th grade with 60% marks"
                />
              </div>

              <div className="form-group">
                <label>Facilities</label>
                {formData.facilities.map((facility, index) => (
                  <div key={index} className="array-input">
                    <input
                      type="text"
                      value={facility}
                      onChange={(e) => updateFacility(index, e.target.value)}
                      placeholder="Facility name"
                    />
                    <button 
                      type="button"
                      onClick={() => removeFacility(index)}
                      className="btn btn-danger btn-sm"
                    >
                      Remove
                    </button>
                  </div>
                ))}
                <button 
                  type="button"
                  onClick={addFacility}
                  className="btn btn-outline btn-sm"
                >
                  + Add Facility
                </button>
              </div>

              <div className="form-group">
                <label>Syllabus/Topics</label>
                {formData.syllabus.map((topic, index) => (
                  <div key={index} className="array-input">
                    <input
                      type="text"
                      value={topic}
                      onChange={(e) => updateSyllabus(index, e.target.value)}
                      placeholder="Syllabus topic"
                    />
                    <button 
                      type="button"
                      onClick={() => removeSyllabus(index)}
                      className="btn btn-danger btn-sm"
                    >
                      Remove
                    </button>
                  </div>
                ))}
                <button 
                  type="button"
                  onClick={addSyllabus}
                  className="btn btn-outline btn-sm"
                >
                  + Add Topic
                </button>
              </div>

              <div className="form-actions">
                <button 
                  type="button"
                  onClick={() => {
                    setShowForm(false);
                    setEditingCourse(null);
                    setImagePreview('');
                  }}
                  className="btn btn-outline"
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Saving...' : (editingCourse ? 'Update Course' : 'Create Course')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Courses List */}
      <div className="courses-list">
        {courses.length === 0 ? (
          <div className="empty-state">
            <p>No courses added yet</p>
            <button 
              onClick={() => setShowForm(true)}
              className="btn btn-primary"
            >
              Add Your First Course
            </button>
          </div>
        ) : (
          <div className="courses-grid">
            {courses.map(course => (
              <div key={course._id} className="course-card">
                <div className="course-image">
                  {course.image ? (
                    <img src={course.image} alt={course.title} />
                  ) : (
                    <div className="course-image-placeholder">
                      ðŸ“š
                    </div>
                  )}
                </div>
                
                <div className="course-content">
                  <div className="course-header">
                    <h3>{course.title}</h3>
                    <div className="course-actions">
                      <button 
                        onClick={() => handleEdit(course)}
                        className="btn btn-sm btn-outline"
                      >
                        Edit
                      </button>
                      <button 
                        onClick={() => handleDelete(course._id)}
                        className="btn btn-sm btn-danger"
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                  
                  <p className="course-category">{course.category}</p>
                  <p className="course-description">{course.description}</p>
                  
                  <div className="course-details">
                    <div className="detail-item">
                      <strong>Duration:</strong> {course.duration}
                    </div>
                    <div className="detail-item">
                      <strong>Fees:</strong> â‚¹{course.fees?.toLocaleString()}
                    </div>
                    {course.eligibility && (
                      <div className="detail-item">
                        <strong>Eligibility:</strong> {course.eligibility}
                      </div>
                    )}
                  </div>

                  {course.facilities && course.facilities.length > 0 && (
                    <div className="course-facilities">
                      <strong>Facilities:</strong>
                      <div className="facilities-tags">
                        {course.facilities.map((facility, index) => (
                          <span key={index} className="facility-tag">
                            {facility}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {course.syllabus && course.syllabus.length > 0 && (
                    <div className="course-syllabus">
                      <strong>Syllabus:</strong>
                      <ul>
                        {course.syllabus.slice(0, 3).map((topic, index) => (
                          <li key={index}>{topic}</li>
                        ))}
                        {course.syllabus.length > 3 && (
                          <li>+{course.syllabus.length - 3} more topics</li>
                        )}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CourseManagement;
