import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const InstituteSidebar = ({ institute, stats, onLogout }) => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const location = useLocation();

  const menuItems = [
    { path: '/institute-dashboard', icon: 'üìä', label: 'Dashboard' },
    { path: '/institute-dashboard/profile', icon: 'üè´', label: 'Profile' },
    { path: '/institute-dashboard/courses', icon: 'üìö', label: 'Courses' },
    { path: '/institute-dashboard/facilities', icon: '‚ö°', label: 'Facilities' },
    { path: '/institute-dashboard/enquiries', icon: 'üí¨', label: `Enquiries (${stats.enquiries || 0})` },
    { path: '/institute-dashboard/reviews', icon: '‚≠ê', label: `Reviews (${stats.reviews || 0})` },
  ];

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <div className={`institute-sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        {!isCollapsed && (
          <>
            <div className="institute-logo">
              {institute?.logo ? (
                <img src={institute.logo} alt={institute.name} />
              ) : (
                <div className="logo-placeholder">üè´</div>
              )}
            </div>
            <h3>{institute?.name}</h3>
            <p className="institute-status verified">‚úì Verified</p>
          </>
        )}
        <button 
          className="collapse-btn"
          onClick={() => setIsCollapsed(!isCollapsed)}
        >
          {isCollapsed ? '‚Üí' : '‚Üê'}
        </button>
      </div>

      <nav className="sidebar-nav">
        {menuItems.map(item => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-item ${isActive(item.path) ? 'active' : ''}`}
          >
            <span className="nav-icon">{item.icon}</span>
            {!isCollapsed && <span className="nav-label">{item.label}</span>}
          </Link>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button onClick={onLogout} className="logout-btn">
          <span className="nav-icon">üö™</span>
          {!isCollapsed && <span>Logout</span>}
        </button>
      </div>
    </div>
  );
};

export default InstituteSidebar;
