import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import { toast } from '../../utils/toast';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [pendingInstitutes, setPendingInstitutes] = useState([]);
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchInstitutes();
  }, []);

  const fetchInstitutes = async () => {
    try {
      console.log('üìã Fetching institutes data...');
      
      const [allResponse, pendingResponse] = await Promise.all([
        api.get('/admin/institutes'),
        api.get('/admin/institutes/pending')
      ]);
      
      setInstitutes(allResponse.data);
      setPendingInstitutes(pendingResponse.data);
      console.log('‚úÖ Institutes data loaded successfully');
    } catch (error) {
      console.error('‚ùå Error fetching institutes:', error);
      toast.error('Failed to load institutes data');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveReject = async (instituteId, action) => {
    try {
      await api.patch(`/admin/institutes/${instituteId}/approve`, { action });
      
      if (action === 'approve') {
        toast.success('Institute approved successfully!');
      } else {
        toast.success('Institute rejected successfully!');
      }
      
      fetchInstitutes(); // Refresh data
    } catch (error) {
      toast.error(`Failed to ${action} institute`);
      console.error(`Error ${action}ing institute:`, error);
    }
  };

  const handleStatusChange = async (instituteId, isActive) => {
    try {
      await api.patch(`/admin/institutes/${instituteId}/status`, { isActive });
      
      toast.success(`Institute ${isActive ? 'activated' : 'deactivated'} successfully!`);
      fetchInstitutes(); // Refresh data
    } catch (error) {
      toast.error('Failed to update institute status');
      console.error('Error updating institute status:', error);
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading institutes...</p>
      </div>
    );
  }

  return (
    <div className="manage-institutes">
      <div className="section-header">
        <h2>Manage Institutes</h2>
        <p>Approve new institutes and manage existing ones</p>
      </div>

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Approval ({pendingInstitutes.length})
        </button>
        <button 
          className={`tab ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          All Institutes ({institutes.length})
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'pending' ? (
          <PendingInstitutes 
            institutes={pendingInstitutes}
            onApproveReject={handleApproveReject}
          />
        ) : (
          <AllInstitutes 
            institutes={institutes}
            onStatusChange={handleStatusChange}
          />
        )}
      </div>
    </div>
  );
};

const PendingInstitutes = ({ institutes, onApproveReject }) => {
  if (institutes.length === 0) {
    return (
      <div className="empty-state">
        <h3>No pending approvals</h3>
        <p>All institute registrations have been processed.</p>
      </div>
    );
  }

  return (
    <div className="institutes-list">
      {institutes.map((institute) => (
        <div key={institute._id} className="institute-card pending">
          <div className="institute-info">
            <h3>{institute.name}</h3>
            <div className="institute-details">
              <span className="category">{institute.category}</span>
              <span className="affiliation">{institute.affiliation}</span>
              <span className="location">
                üìç {institute.address.city}, {institute.address.state}
              </span>
            </div>
            <p className="institute-description">{institute.description}</p>
            <div className="contact-info">
              <strong>Contact:</strong> {institute.contact?.email} | {institute.contact?.phone}
            </div>
            <div className="application-date">
              Applied on: {new Date(institute.createdAt).toLocaleDateString()}
            </div>
          </div>
          
          <div className="institute-actions">
            <button 
              onClick={() => onApproveReject(institute._id, 'approve')}
              className="btn btn-success"
            >
              ‚úì Approve
            </button>
            <button 
              onClick={() => onApproveReject(institute._id, 'reject')}
              className="btn btn-danger"
            >
              ‚úó Reject
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

const AllInstitutes = ({ institutes, onStatusChange }) => {
  return (
    <div className="table-container">
      <table className="table">
        <thead>
          <tr>
            <th>Institute Name</th>
            <th>Category</th>
            <th>Location</th>
            <th>Status</th>
            <th>Rating</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {institutes.map((institute) => (
            <tr key={institute._id}>
              <td>
                <div className="institute-name">
                  <strong>{institute.name}</strong>
                  <div className="affiliation">{institute.affiliation}</div>
                </div>
              </td>
              <td>
                <span className="category-tag">{institute.category}</span>
              </td>
              <td>
                {institute.address.city}, {institute.address.state}
              </td>
              <td>
                <span className={`status-badge ${institute.isVerified ? 'verified' : 'pending'} ${institute.isActive ? 'active' : 'inactive'}`}>
                  {institute.isVerified ? (institute.isActive ? 'Active' : 'Inactive') : 'Pending'}
                </span>
              </td>
              <td>
                <div className="rating">
                  ‚≠ê {institute.rating || 'N/A'}
                  <div className="review-count">({institute.reviewCount} reviews)</div>
                </div>
              </td>
              <td>
                <div className="action-buttons">
                  {institute.isActive ? (
                    <button 
                      onClick={() => onStatusChange(institute._id, false)}
                      className="btn btn-sm btn-warning"
                    >
                      Deactivate
                    </button>
                  ) : (
                    <button 
                      onClick={() => onStatusChange(institute._id, true)}
                      className="btn btn-sm btn-success"
                    >
                      Activate
                    </button>
                  )}
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageInstitutes;
