import React, { useState, useEffect, useCallback } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI } from '../../services/api';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const { user } = useAuth();
  const [institute, setInstitute] = useState(null);
  const [stats] = useState({
    enquiries: 0,
    reviews: 0,
    courses: 0
  });

  // Fixed: Get institute by user ID
  const fetchInstituteData = useCallback(async () => {
    try {
      const response = await instituteAPI.getAll();
      const userInstitute = response.data.find(inst => inst.user?._id === user.id);
      
      if (userInstitute) {
        // Get detailed institute data
        const detailedInstitute = await instituteAPI.getById(userInstitute._id);
        setInstitute(detailedInstitute.data);
      }
    } catch (error) {
      console.error('Error fetching institute data:', error);
    }
  }, [user.id]);

  useEffect(() => {
    fetchInstituteData();
  }, [fetchInstituteData]);

  if (!institute) {
    return <div className="loading">Loading institute data...</div>;
  }

  return (
    <div className="institute-dashboard">
      <InstituteSidebar institute={institute} />
      <div className="dashboard-content">
        <Routes>
          <Route path="/" element={<DashboardHome stats={stats} institute={institute} />} />
          <Route path="/profile" element={<ProfileManagement institute={institute} />} />
          <Route path="/courses" element={<CourseManagement institute={institute} />} />
          <Route path="/facilities" element={<FacilitiesManagement institute={institute} />} />
          <Route path="/enquiries" element={<Enquiries institute={institute} />} />
          <Route path="/reviews" element={<Reviews institute={institute} />} />
          <Route path="*" element={<Navigate to="/institute" replace />} />
        </Routes>
      </div>
    </div>
  );
};

const DashboardHome = ({ stats, institute }) => {
  return (
    <div className="dashboard-home">
      <div className="dashboard-header">
        <h1>Welcome, {institute.name}</h1>
        <p>Manage your institute profile and activities</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìß</div>
          <div className="stat-info">
            <h3>{stats.enquiries}</h3>
            <p>New Enquiries</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <h3>{stats.reviews}</h3>
            <p>Total Reviews</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìö</div>
          <div className="stat-info">
            <h3>{stats.courses}</h3>
            <p>Courses</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-info">
            <h3>Active</h3>
            <p>Status</p>
          </div>
        </div>
      </div>

      <div className="recent-activities">
        <h2>Recent Activities</h2>
        <div className="activities-list">
          <div className="activity-item">
            <div className="activity-icon">üìß</div>
            <div className="activity-content">
              <p>New enquiry from John Doe</p>
              <span>2 hours ago</span>
            </div>
          </div>
          <div className="activity-item">
            <div className="activity-icon">‚≠ê</div>
            <div className="activity-content">
              <p>New review received (5 stars)</p>
              <span>5 hours ago</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InstituteDashboard;
