import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext'; // Fixed import path
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    role: 'student'
  });
  const [loading, setLoading] = useState(false);
  const { login, error, clearError } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) clearError();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      console.log('üîÑ Login form submitted:', formData);
      
      const result = await login(formData.email, formData.password, formData.role);
      
      if (result.success) {
        console.log('‚úÖ Login successful, navigating...');
        // Navigate based on role - with proper null checks
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        const userRole = user?.role || formData.role;
        
        switch (userRole) {
          case 'admin':
            navigate('/admin/dashboard');
            break;
          case 'institute':
            navigate('/institute/dashboard');
            break;
          case 'student':
          default:
            navigate('/');
            break;
        }
      } else {
        console.error('‚ùå Login failed:', result.message);
      }
    } catch (error) {
      console.error('‚ùå Login error in component:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} className="login-form">
        <h2>Login to EduList</h2>
        
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            placeholder="Enter your email"
          />
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            placeholder="Enter your password"
          />
        </div>

        <div className="form-group">
          <label>Role</label>
          <select
            name="role"
            value={formData.role}
            onChange={handleChange}
            required
          >
            <option value="student">Student</option>
            <option value="institute">Institute</option>
            <option value="admin">Admin</option>
          </select>
        </div>

        <button 
          type="submit" 
          disabled={loading}
          className="login-button"
        >
          {loading ? 'Logging in...' : 'Login'}
        </button>

        <p className="register-link">
          Don't have an account? <Link to="/register">Register here</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
