import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Common.css';

const Header = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
    setIsMenuOpen(false);
  };

  const getDashboardLink = () => {
    if (!user) return '/login';
    
    // Check if user object has the expected structure
    if (user.user) {
      switch (user.user.role) {
        case 'admin': return '/admin';
        case 'institute': return '/institute-dashboard';
        default: return '/user';
      }
    }
    
    // Fallback for different user object structure
    if (user.role) {
      switch (user.role) {
        case 'admin': return '/admin';
        case 'institute': return '/institute-dashboard';
        default: return '/user';
      }
    }
    
    return '/user';
  };

  const getUserName = () => {
    if (!user) return '';
    
    if (user.user && user.user.name) return user.user.name;
    if (user.name) return user.name;
    if (user.user && user.user.email) return user.user.email;
    if (user.email) return user.email;
    
    return 'User';
  };

  const getUserRole = () => {
    if (!user) return '';
    
    if (user.user && user.user.role) return user.user.role;
    if (user.role) return user.role;
    
    return 'user';
  };

  return (
    <header className="header">
      <div className="container">
        <Link to="/" className="logo">
          <h2>EduList</h2>
        </Link>

        <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
          <Link to="/institutes" className="nav-link">Browse Institutes</Link>
          
          {isAuthenticated ? (
            <div className="user-menu">
              <button 
                className="user-btn"
                onClick={() => setIsMenuOpen(!isMenuOpen)}
              >
                <span>Welcome, {getUserName()}</span>
                <span className="dropdown-arrow">â–¼</span>
              </button>
              
              {isMenuOpen && (
                <div className="dropdown-menu">
                  <div className="user-info">
                    <strong>{getUserName()}</strong>
                    <small>{getUserRole().toUpperCase()}</small>
                  </div>
                  <Link 
                    to={getDashboardLink()} 
                    className="dropdown-item"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    ðŸ“Š Dashboard
                  </Link>
                  <button 
                    className="dropdown-item logout"
                    onClick={handleLogout}
                  >
                    ðŸšª Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="btn btn-outline">Login</Link>
              <Link to="/register" className="btn btn-primary">Register</Link>
            </div>
          )}
        </nav>

        <button 
          className="mobile-menu-btn"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          â˜°
        </button>
      </div>
    </header>
  );
};

export default Header;
