import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

console.log('ðŸ”§ API Base URL:', API_BASE_URL);

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
      console.log('ðŸ”‘ Adding token to request');
    }
    return config;
  },
  (error) => {
    console.error('ðŸš« Request error:', error);
    return Promise.reject(error);
  }
);

// Handle responses and errors
api.interceptors.response.use(
  (response) => {
    console.log('âœ… API Response:', response.config.url, response.status);
    return response;
  },
  (error) => {
    console.error('ðŸš« API Error:', {
      url: error.config?.url,
      status: error.response?.status,
      message: error.response?.data?.message || error.message
    });
    
    if (error.response?.status === 401) {
      console.log('ðŸ”’ Token expired, logging out');
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    
    return Promise.reject(error);
  }
);

// Test API connection
export const testConnection = async () => {
  try {
    const response = await api.get('/test');
    console.log('âœ… Backend connection test:', response.data);
    return true;
  } catch (error) {
    console.error('âŒ Backend connection failed:', error.message);
    return false;
  }
};

export const instituteAPI = {
  getAll: (filters = {}) => api.get('/institutes', { params: filters }),
  getById: (id) => api.get(`/institutes/${id}`),
  updateProfile: (data) => api.put('/institutes/profile', data),
  addCourse: (data) => api.post('/institutes/courses', data),
  addFacility: (data) => api.post('/institutes/facilities', data),
};

export const reviewAPI = {
  create: (data) => api.post('/reviews', data),
  getByInstitute: (instituteId) => api.get(`/reviews/institute/${instituteId}`),
  getMyReviews: () => api.get('/reviews/my-reviews'),
};

export const enquiryAPI = {
  create: (data) => api.post('/enquiries', data),
  getByInstitute: () => api.get('/enquiries/institute'),
  getMyEnquiries: () => api.get('/enquiries/my-enquiries'),
  respond: (id, data) => api.patch(`/enquiries/${id}/respond`, data),
};

export const adminAPI = {
  getDashboard: () => api.get('/admin/dashboard'),
  getPendingInstitutes: () => api.get('/admin/institutes/pending'),
  verifyInstitute: (id, verified) => api.patch(`/admin/institutes/${id}/verify`, { verified }),
  getUsers: () => api.get('/admin/users'),
  updateUserStatus: (id, isActive) => api.patch(`/admin/users/${id}/status`, { isActive }),
  getReviews: () => api.get('/admin/reviews'),
  moderateReview: (id, isApproved) => api.patch(`/admin/reviews/${id}/moderate`, { isApproved }),
};

export const authService = {
  login: (email, password) => api.post('/auth/login', { email, password }),
  register: (userData) => api.post('/auth/register', userData),
  getCurrentUser: () => api.get('/auth/me'),
};

export default api;
