import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    role: 'user'
  });
  const [instituteData, setInstituteData] = useState({
    name: '',
    category: 'school',
    affiliation: '',
    address: {
      city: '',
      state: '',
      street: '',
      pincode: ''
    },
    contact: {
      phone: '',
      website: ''
    },
    description: '',
    establishedYear: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { register } = useAuth();
  const navigate = useNavigate();

  const handleUserChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    if (error) setError('');
  };

  const handleInstituteChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setInstituteData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setInstituteData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Validation
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      setLoading(false);
      return;
    }

    // Institute-specific validation
    if (formData.role === 'institute') {
      if (!instituteData.name || !instituteData.affiliation) {
        setError('Institute name and affiliation are required');
        setLoading(false);
        return;
      }
      if (!instituteData.address.city || !instituteData.address.state) {
        setError('Institute city and state are required');
        setLoading(false);
        return;
      }
    }

    // Prepare registration data
    const registrationData = {
      name: formData.name,
      email: formData.email,
      phone: formData.phone,
      password: formData.password,
      role: formData.role
    };

    // Add institute data if registering as institute
    if (formData.role === 'institute') {
      registrationData.instituteData = instituteData;
    }

    console.log('ðŸ“¤ Sending registration data:', registrationData);

    try {
      const result = await register(registrationData);
      
      if (result.success) {
        if (formData.role === 'institute') {
          // For institutes, show success message and redirect to login
          alert('âœ… Institute registered successfully! Please wait for admin approval. You will be able to login once approved.');
          navigate('/login');
        } else {
          // For regular users, redirect to their dashboard
          navigate('/user');
        }
      } else {
        setError(result.message || 'Registration failed. Please try again.');
      }
    } catch (err) {
      setError('Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Create Your Account</h2>
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Full Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleUserChange}
              required
              placeholder="Enter your full name"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Email Address *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleUserChange}
              required
              placeholder="Enter your email"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Phone Number *</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleUserChange}
              required
              placeholder="Enter your phone number"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleUserChange}
              required
              placeholder="Enter your password (min 6 characters)"
              minLength="6"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Confirm Password *</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleUserChange}
              required
              placeholder="Confirm your password"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>Register As *</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleUserChange}
              required
              disabled={loading}
            >
              <option value="user">Student/Parent</option>
              <option value="institute">Institute</option>
            </select>
          </div>

          {formData.role === 'institute' && (
            <div className="institute-fields">
              <h3>Institute Information</h3>
              <div className="info-note">
                â“˜ Your institute will be reviewed by admin before you can login.
              </div>
              
              <div className="form-group">
                <label>Institute Name *</label>
                <input
                  type="text"
                  name="name"
                  value={instituteData.name}
                  onChange={handleInstituteChange}
                  required
                  placeholder="Enter institute name"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Category *</label>
                <select
                  name="category"
                  value={instituteData.category}
                  onChange={handleInstituteChange}
                  required
                  disabled={loading}
                >
                  <option value="school">School</option>
                  <option value="college">College</option>
                  <option value="coaching">Coaching Center</option>
                  <option value="preschool">Preschool</option>
                  <option value="university">University</option>
                </select>
              </div>

              <div className="form-group">
                <label>Affiliation *</label>
                <input
                  type="text"
                  name="affiliation"
                  value={instituteData.affiliation}
                  onChange={handleInstituteChange}
                  required
                  placeholder="e.g., CBSE, State Board, UGC"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>City *</label>
                <input
                  type="text"
                  name="address.city"
                  value={instituteData.address.city}
                  onChange={handleInstituteChange}
                  required
                  placeholder="Enter city"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>State *</label>
                <input
                  type="text"
                  name="address.state"
                  value={instituteData.address.state}
                  onChange={handleInstituteChange}
                  required
                  placeholder="Enter state"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Street Address</label>
                <input
                  type="text"
                  name="address.street"
                  value={instituteData.address.street}
                  onChange={handleInstituteChange}
                  placeholder="Enter street address"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Pincode</label>
                <input
                  type="text"
                  name="address.pincode"
                  value={instituteData.address.pincode}
                  onChange={handleInstituteChange}
                  placeholder="Enter pincode"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Website</label>
                <input
                  type="url"
                  name="contact.website"
                  value={instituteData.contact.website}
                  onChange={handleInstituteChange}
                  placeholder="Enter website URL"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Established Year</label>
                <input
                  type="number"
                  name="establishedYear"
                  value={instituteData.establishedYear}
                  onChange={handleInstituteChange}
                  placeholder="Established year"
                  min="1900"
                  max="2024"
                  disabled={loading}
                />
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="description"
                  value={instituteData.description}
                  onChange={handleInstituteChange}
                  placeholder="Describe your institute"
                  rows="4"
                  required
                  disabled={loading}
                />
              </div>
            </div>
          )}

          <button 
            type="submit" 
            className="auth-button" 
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Register'}
          </button>
        </form>

        <p className="auth-link">
          Already have an account? <Link to="/login">Login here</Link>
        </p>

        <div className="demo-credentials">
          <h4>Test Credentials:</h4>
          <p><strong>Admin:</strong> admin@edulist.com / admin123</p>
          <p><strong>Verified Institute:</strong> info@dpsdelhi.com / institute123</p>
          <p><strong>User:</strong> rahul@example.com / user123</p>
        </div>
      </div>
    </div>
  );
};

export default Register;
